insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (20, 'MULTIPLE_CHOICE', '[{"data": "분산투자 효과에 대한 설명으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["분산투자를 하면 전쟁이나 천재지변에 따른 위험도 줄일 수 있다", "보유 종목 수를 계속 늘린다고 투자 위험이 계속 줄어들지는 않는다", "분산투자를 해도 노사문제와 같은 기업 고유의 위험은 줄어들지 않는다", "보유 종목 수를 늘려 나갈수록 위험 감소 폭은 커진다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "포트폴리오 내에 편입된 종목 수를 늘리면 계속해서 위험이 줄어들지는 않는다. 보유 종목 수를 추가할수록 위험이 줄어들기는 하지만, 감소의 폭은 줄어든다. 통상 포트폴리오에 30개 종목을 편입하면 비체계적 위험은 대부분 사라지며, 더 분산투자를 해도 위험은 거의 줄어들지 않는다. 따라서 포트폴리오를 구성할 때 기대 수익률과 표준편차, 자산 간의 상관관계를 고려하여 적절한 분산으로 가장 최적의 포트폴리오를 구성할 수 있도록 조율해야 한다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '분산투자효과 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (21, 'MULTIPLE_CHOICE', '[{"data": "비체계적인 위험에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개별 기업의 독특한 요소 때문에 발생하는 위험이다", "성격이 다른 자산이나 기업에 투자하면 피할 수 있다", "비체계적 위험을 떠안으면 추가적인 보상을 받을 수 있다", "개별 기업의 주가를 시장과 다른 방향으로 움직이는 위험이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "비체계적인 위험은 적절한 분산투자를 통해 쉽게 제거할 수 있기 때문에, 비체계적인 리스크를 떠안아도 추가적인 보상을 받을 수는 없다. 투자자들이 보상받을 수 있는 리스크는 체계적인 위험 뿐이다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '비체계적 위험 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (22, 'MULTIPLE_CHOICE', '[{"data": "장기투자 효과에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자 자산이 가진 변동성은 보유기간에 따라 달라진다", "단기적으로 채권 가격의 변동성이 주식보다 훨씬 적다", "투자 기간을 늘리더라도 주식의 변동성은 줄어들지 않는다", "투자 기간이 길면 포트폴리오 내 주식 비중을 크게 가져가도 된다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "주식과 채권의 위험은 보유 기간에 영향을 받는다. 단기 투자를 할 때 주식은 채권보다 훨씬 더 위험한 투자 자산이다. 그러나 보유 기간이 15년 이상으로 길어지면 연평균 수익률의 표준편차가 장단기 채권보다 작아진다. 30년 이상이면 주식의 위험은 장단기 채권의 절반 수준이 된다. 투자할 때 장기적인 관점이 중요한 것은 바로 이런 사실 때문이다. 투자 기간이 늘어날수록 포트폴리오 내에서 주식이 차지하는 비중도 커져야 한다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '장기투자 효과 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (23, 'MULTIPLE_CHOICE', '[{"data": "투자 전략과 전술을 수립할 때 고려하여야 할 사항으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["일정한 위험 수준에서 투자수익을 최대화한다", "일정한 투자 수익이라면 위험을 최소화한다", "투자 관리는 계획, 실행, 사후 통제 순으로 진행한다", "투자자산의 위험은 투자 기간과 무관하게 일정하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "투자 자산의 수익과 위험은 절대적이지 않으며 투자 기간에 따라 달라진다. 단기적으로 보면 채권 가격의 변동성은 주식보다 낮지만 기대 수익률도 낮다. 주식의 경우 단기적인 변동성은 채권보다 높지만 장기적으로는 채권보다 낮은 변동성을 보인다. 그런데도 수익은 채권보다 훨씬 높다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자전략전술 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (24, 'MULTIPLE_CHOICE', '[{"data": "글로벌 분산투자를 통해서만 줄일 수 있는 위험으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["부존자원이 많지 않은 국가의 원자재 가격 상승 위험", "노사분쟁이 자주 발생하는 기업이 가진 위험", "회계 투명성이 떨어지는 기업의 분식회계 위험", "산업재해 자주 발생하는 기업이 가진 위험"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "특정 국가가 가진 고유의 위험은 글로벌분산투자를 통해 줄일 수 있다. 노사분쟁, 분식회계, 산업재해와 같은 비체계적 위험은 국내 분산투자를 통해서도 줄일 수 있다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '글로벌 분산투자 적용', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (25, 'MULTIPLE_CHOICE', '[{"data": "다음의 투자위험을 해소하기 위한 대안으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["전 세계 증시에서 우리나라 증시가 차지하는 비중은 채2%가 안 된다", "수출 주도로 성장하는 국내 경제는 환율 변동에 취약하다", "우리나라는 부존 자원이 부족해 원자재 가격의 상승에 취약하다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["수출 주도로 성장하는 국내 기업에 집중투자한다", "내수 중심으로 성장하는 국내 기업에 집중투자한다", "환율 변동의 영향을 덜 받는 기업을 골라 투자한다", "글로벌 기업의 주식과 채권에 분산투자 해 변동성을 낮춘다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "투자자가 저지르는 흔한 실수가 있다면, 글로벌 투자의 기회를 충분히 활용하지 않는 것이다. 전 세계 증시에서 국내 증시가 차지하는 비중은 2%가 채 안 된다. 그러니 국내 자산에만 투자하는 것은 조그마한 바구니에 재산을 전부 쓸어 담는 셈이다. 국내 경제는 내수보다는 수출 중심으로 성장하기 때문에 환율 변동에 크게 영향을 받는 데다, 부존 자원이 많지 않아 원자재 가격의 상승에 취약할 수밖에 없다. 이러한 국가가 가진 고유의 위험을 줄이려면 투자 자금을 적극적으로 해외로 분산하는 것이 좋다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '글로벌 분산투자 적용', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (26, 'MULTIPLE_CHOICE', '[{"data": "펀드를 활용해 간접투자 할 때 나타나는 장단점에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["전문가에게 투자를 맡기면 자기 일에 집중할 수 있다", "적은 금액으로도 분산투자 할 수 있다", "추가적인 비용이 발생한다", "자기가 원하는 종목을 적시에 매수할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "간접 투자의 가장 큰 장점은 전문가에게 투자를 맡기고 자신의 일에 집중할 수 있다는 점이다. 물론 전문가를 활용하려면 비용을 지불해야 한다. 또한 소액으로 분산투자가 가능한 것도 장점이다. 간접 투자는 자산을 위탁하는 것이기 때문에 투자자의 의도와 다르게 운용될 수도 있다. 시장이 급등하더라도 내가 자금을 맡긴 펀드를 운용하는 매니저가 주도 주식을 갖고 있지 않으면 성과가 시장 수익률에도 못 미칠 수 있다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '간접투자 장단점 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (27, 'MULTIPLE_CHOICE', '[{"data": "직접 투자의 장단점에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자신이 원할 때 투자종목을 사고 팔 수 있다", "자신에게 적합한 포트폴리오를 스스로 구성한다", "전문가의 도움 없이 투자할 수 있다", "간접투자보다 항상 우수한 성과를 보인다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "직접 투자의 가장 큰 장점은 자신에게 적합한 포트폴리오를 스스로 꾸릴 수 있다는 점이다. 자신의 취향에 맞는 종목을 골라, 사고 싶을 때 사고 팔고 싶을 때 판다. 내 돈을 스스로 관리한다는 점에서 자부심도 느낄 수 있다. \n하지만 일반인들 중에 충분한 투자 경험과 역량을 갖춘 이는 많지 않은 데다, 단기간에 전문성을 갖추기도 어렵다. 투자 역량과 경험을 충분히 갖춘 투자자라고 해도 의사결정을 내리려면 필요한 정보를 취득하고 분석하는 데 많은 시간이 든다. 전문성을 갖추지 않은 채 투자 대안을 충분히 검토하지 않고 감정에 치우쳐 매매하면 수익은 내지 못하고 수수료만 지불할 가능성이 높다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '직접투자 장단점 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (28, 'MULTIPLE_CHOICE', '[{"data": "다음은 어떤 투자방법에 대한 설명인가?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["증권시장의 흐름을 파악해 포트폴리오 내 주식과 채권 비중을 조절한다", "시장 흐름을 주도하는 섹터와 테마에 속하는 주식의 비중을 늘린다", "시장 평균보다 높은 수익이 기대되는 종목에 집중투자한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["액티브투자", "패시브투자", "적립식투자", "포트폴리오투자"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "액티브 운용 전략은 시장 평균 수익률을 넘는 수익을 얻기 위해 투자방법이다. 증권시장의 흐름을 판단해 포트폴리오의 주식과 채권의 비중을 조절하거나, 시장 상황에 따라 주식 내에서 섹터와 테마의 비중을 조정하기도 한다. 시장보다 상대적으로 더 높은 수익이 기대되는 종목을 선정해서 집중 투자하는 방법도 있다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '액티브운용 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (29, 'MULTIPLE_CHOICE', '[{"data": "패시브 운용전략에 대한 설명으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["마켓 타이밍에 따라 증권을 매매한다", "비교적 비용이 저렴하다", "펀드간 수익률 차이가 크다", "비교 대상이 되는 지수보다 높은 수익을 추구한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "액티브 운용 전략에서는 시장 평균 수익률보다 높은 수익을 얻기 위해 적극적으로 노력하므로, 매매를 자주 하고 수수료와 운용 비용이 많이 들어간다. 하지만 패시브 운용 전략은 시장을 이기려고 노력하지 않으며 시장이 움직이는 대로 따라가기 때문에 비용이 적게 들어간다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '패스브운용 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (30, 'MULTIPLE_CHOICE', '[{"data": "패시브 운용전략을 잘 대표하는 상품은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["헤지펀드", "인덱스펀드", "배당주펀드", "원자재펀드"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "패시브 운용 전략을 대표하는 금융상품으로는 인덱스펀드가 있다. 인덱스펀드와 ETF는 시장을 대표하는 지수를 선정하고, 지수의 수익률과 펀드의 수익률 간에 편차가 발생하지 않도록 조정하는 것 말고는 특별한 노력을 기울이지 않는다. 따라서 액티브 운용 전략에 비해 수수료와 운용 비용이 덜 들어간다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '패시브 운용전략 이해', null, null);
