insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (33, 'MULTIPLE_CHOICE', '[{"data": "4달 동안 매달 10만원을 투자해서 다음과 같은 방식으로 A회사 주식을 매입했을 때, 주당 평균매입단가로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["858원", "954원", "1,011원", "1,054원"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "4달 동안 40만 원을 투자해서 466주를 매입한 셈이다. 이렇게 되면 1주당 평균 매입 단가는 858원이므로 A씨보다 싸게 주식을 매입한 셈이다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '코스트애버리징 적용', null, null);
