insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (41, 'MULTIPLE_CHOICE', '[{"data": "주당장부가치비율에 대한 설명으로 올바른 것을 모두 고르시오.", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "(가)", "dataType": "text", "data": ["주가를 주당 순자산으로 나눈 값이다", "이 지표가 1보다 낮다면 청산가치가 현재 주가보다 많다는 의미이다", "성장성이 높은 기업은 일반적으로 이 지표가 높게 산출된다", "보유하고 있는 공장이나 부동산 등 자산이 많아질수록 이 지표는 낮게 산출된다", "이 지표의 절대적인 적정 수준이 존재하지 않는다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["가, 다", "나, 다, 라", "가, 나, 다", "가, 나, 다, 라, 마 모두"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "PBR은 주가를 주당순자산으로 나눈 값으로, 기업이 청산된다고 가정할 때 주주가 건질 수 있는 청산가치의 규모를 주가와 비교하는 지표이다. PBR이 1보다 낮다는 것은 청산가치가 주가보다 크다는 것을 의미하며, 대부분 자산이 많은 기업들의 PBR이 상대적으로 낮은 편이다. 보유 자산은 적지만 성장성에 대한 기대가 높아 주가가 비싸게 형성되는 기업의 PBR은 상대적으로 높은 편이다. PER과 마찬가지로 PBR 역시 절대적인 적정 수준은 존재하지 않으면 상대적 비교에 유용하다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/06/15 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식가치지표의 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (42, 'MULTIPLE_CHOICE', '[{"data": "□□기업의 부채비율이 500%라고 할 때, 부채비율을 줄일 수 있는 방법으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["차입금을 갚는다", "사채를 발행한다", "유상증자를 실시한다", "기존 회사채를 상환한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "부채비율을 줄이기 위해서는 부채를 낮추거나 자본을 늘려야 한다. \n지문 1에서 부채 항목인 차입금을 갚으면 부채가 줄어들어 부채비율을 줄인다.\n지문 2에서 타인자본인 사채를 발행하면, 부채항목이 늘어나기 때문에 부채비율을 늘리는 조치이다.\n지문 3의 유상증자를 실시하는 것은 자기자본인 자본을 증액하는 것이므로, 부채비율을 줄인다.\n지문 4에서 기존 발행된 회사채를 상환하여 부채항목을 줄일 수 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/15 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '부채와 자본 구별', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (43, 'MULTIPLE_CHOICE', '[{"data": "주식회사가 신규 투자를 받기 위해 자금을 조달하는 방식으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["최근 금리가 많이 하락하여 저리로 회사채를 발행하여 운영자금을 마련한다", "최근 부동산 가격이 상승하여 보유하고 있는 유휴부지를 매각하여 설비투자 자금을 마련한다", "최근 주가가 많이 올라 1:5으로 액면분할을 실시한다", "최근 유력한 투자처를 소개받아 이사회 특별결의를 통해 제3자배정 유상증자를 실시한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "신규 투자를 위한 자금조달은 자기자본을 늘리거나 타인자본을 차입하는 방식으로 진행할 수 있다. 금융기관에서 대출을 받는 방식도 가능하다\n지문 1에서 회사채 발행은 타인자본을 늘려 운영자금을 마련할 수 있다.\n지문 2에서 유휴부지를 매각하여 현금을 확보하면 설비투자를 진행할 수 있다.\n지문 3에서 액면분할은 자기자본의 지분권을 쪼개는 것일뿐 자본의 증가를 유발하지 않는다\n지문 4에서 제3자배정을 통한 유상증자 역시 설비투자를 마련할 수 있는 방안이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/15 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자금조달 수단', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (44, 'MULTIPLE_CHOICE', '[{"data": "A기업(자기자본 1,000억원)이 독자적으로 개발한 신기술이 반도체 생산의 핵심기술로 채택되면서 매출과 영업이익이 큰 폭으로 늘어났다. 매출액은 전년도 1,000억원의 2배가 되었으며, 영업이익도 전년도 180억원에서 올해 400억원으로 급신장하였다. 당기순이익은 올해 200억원을 기록하였으며, 배당금은 50억원을 지급하였다. 이때, A기업의 재무비율을 설명하는 내용으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["A기업의 올해 매출액 대비 영업이익률은 20%이다", "A기업의 전년대비 매출액증가율은 200%이다", "A기업의 배당성향은 40%이다", "A기업의 자기자본 이익률은 20%이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "재무비율은 기업의 재무제표에서 확인할 수 있는 주요 지표의 비교를 통해 해당 기업의 수익성/성장성/안정성 등을 파악할 수 있다.\n지문 1의 매출액 대비 영업이익률은 올해 매출액 2000억원과 영업이익 400억원의 비율이다\n지문 2의 매출액증가율은 (당기매출액-전기매출액)/전기매출액X100으로 100% 신장이 맞다\n지문3의 배당성향은 배당금/순이익으로 나눈 값으로, 배당금 50억원과 당기순이익 200억원의 비율이다.\n지문4의 자기자본이익률은 순이익/자기자본으로 산출하며, 당기순이익 200억원과 자기자본 1000억원의 비율이다\n"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/06/16 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '재무비율의 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (45, 'MULTIPLE_CHOICE', '[{"data": "□□기업은 스타트업에서 성공하여 거래소에 상장한 기업이다. 매출이 꾸준하게 발생하고 있지만, 부채가 많아 실제 당기순이익은 많지 않은 편이다. □□기업을 평가할 때 주목해야 할 재무비율로 가장 거리가 먼 것은?\n", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["부족한 자본에도 불구하고 투자와 연구개발을 위해 타인자본을 활용해야 하는 기업 입장에서는 부채비율이 무조건 높다고 부정적으로만 평가할 필요는 없다", "매출이 발생하더라도 부채로 인해 발생하는 이자비용이 영업이익을 초과할 정도라면 이자보상배율이 1을 상회하기 때문에 재무건전성이 우려된다", "매출액 증가율이 매년 우상향하고 있다면 기업의 규모가 성장하고 있다고 평가할 수 있다", "유동비율은 1년만기 이라 단기부채의 상환능력을 파악하는 지표이며, ㅇㅇ기업의 유동비율은 유동자산에 비해 유동부채가 많기 때문에 낮게 산출될 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "성장기업임에도 불구하고 부채가 많을 경우 기업의 안정성 측면에서 유의할 필요가 있다. 성장기업의 경우 부채비율이 무조건 높다고 부정적으로만 평가할 필요는 없다. 매출규모와 성장성을 같이 판단해야 해당 기업을 제대로 평가할 수 있다.\n지문 2의 이자보상배율의 산식은 (영업이익/이자비용)*100이기 때문에, 이자비용이 영업이익을 초과한다면 이자보상배율은 1을 하회한다.\n지문 3에서 매출액이 매년 증가하는 기업이라면 성장성 측면에서 긍정적인 기업이다. \n지문 4의 유동비율의 산식은 (유동자산/유동부채)*100이므로, XYZ의 유동비율은 1 이하로 산출될 수 있다.\n"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/06/17 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '재무비율의 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (46, 'MULTIPLE_CHOICE', '[{"data": "배당금에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["배당은 기업의 잉여자본금을 주주에게 환원하는 대표적 행위이다", "배당수익률이 높다는 것은 주가 대비 지급되는 주당 배당금이 높거나 반대로 주당 배당금 대비 주가가 낮은 경우이다", "성장단계의 기업은 배당보다 투자에 집중하기 때문에 배당지급이 없거나 배당 지급 규모가 적을 가능성이 높다", "배당수익률이 10%라면, 현재 주가가 향후 10% 상승할 가능성이 높다고 평가할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "배당은 기업의 잉여자본을 주주에게 환원하는 경제행위이며, 주주는 기업에게 이익배당을 청구할 수 있는 권리가 있다. 성장단계의 기업은 배당보다 투자에 집중하는 경향이 높다. \n지문 2와 4의 배당수익률 산식은 (주당배당금/주가)*100이므로, 배당수익률이 높다면 배당금이 많거나 주가가 낮은 경우이다. 배당수익률은 배당 규모를 판단하는 지표이며, 주가의 예상 등락을 평가할 수 있는 가격지표로 활용할 수 없다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/18 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '배당 관련용어 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (47, 'MULTIPLE_CHOICE', '[{"data": "한국거래소의 공시제도에 대해 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기업의 재무구조나 회사의 중대한 경영 관련 사항에 대해서는 기업의 대내외 관계자는 물론 일반 투자자에게 충분하게 알려야 한다", "회사의 핵심 기술의 특허 만료가 이루어졌다면 시간이 걸리더라도 관련 영향을 충분히 검토한 후에 공시를 해야 한다", "최근 1개월 전의 공시 내역과 다르게 다른 회사와 합병을 추진한다고 밝힌다면 투자자에게 혼선을 주는 행위이기 때문에 거래소에서 불법공시로 제재를 할 수 있다", "회사 내부에서만 알 수 있는 계약사항이라도 회사 경영과 재무상황에 상당한 영향을 미치는 상황이라면, 계약 체결시 즉각적으로 공시를 해야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "공시제도는 상장법인의 경영관련 내용을 투자자 등 이해관계자들에게 공개하여 증권시장 내 투자자간 정보의 불균형을 해소하고 거래의 원활함과 공정성을 확보하기 위한 정보전달제도이다. \n공시제도는 정보의 정확성과 완전성, 신속성과 적시성을 확보해야 하며, 공시내용의 공평한 전달이 이루어져야 한다. 지문 ②의 경우 핵심기술의 특허만료는 회사 경영에 상당한 영향을 미칠 수 있기 때문에 신속하게 투자자에게 알려함에도 불구하고, 공시제도의 원칙에 위배되는 행위이다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/06/19 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '공시제도 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (48, 'MULTIPLE_CHOICE', '[{"data": "4개 기업의 주가와 각 사의 재무제표에서 산출한 기업가치(EV)와 EBITDA를 비교한 것이다. 각 기업에 대한 평가로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_IMAGE", "preface": "null", "dataType": "image", "data": ["img_1.png"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["A기업의 기업가치는 영업활동으로 벌어들인 이익의 3.6배에 달한다", "B기업을 인수한다면 약 4년이면 투자원금을 회수할 수 있을 것으로 전망된다", "C기업의 EV/EBITDA가 가장 낮기 때문에 4개 회사 중에서 가장 비싼 기업이라고 평가할 수 있다", "D기업은 주가는 낮지만 4개 기업 중에서 영업이익에 비해 상대적으로 기업가치를 비싸게 평가받는 기업이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "기업가치(EV)와 세전/이익지급전/감가상감차가전 영업이이(EBITDA)를 비교하는 것은 기업의 가치가 영업이익에 비해 얼마나 고평가/저평가 받는 지를 확인할 수 있는 주가배수이다. 특히 해당 기업을 인수하려는 투자자 입장에서는 기업의 영업활동으로 벌어들이는 수익으로 언제쯤 투자원금을 회수할 수 있는 지를 파악할 수 있는 지표이다. 지문 ①은 EV/EBITDA의 개념을 설명한다. 지문 ②는 EV/EBITDA의 활용방안인 인수 후 투자원금 회수기간을 의미한다. 지문 ③에서 C기업은 4개 기업 중 EV/EBITDA가 가장 낮기 때문에, 4개 기업 중에서는 상대적으로 저평가된 기업이라고 볼 수 있다. 지문 ④에서 D기업은 주가는 가장 낮지만 EV/EBITDA가 4개사 중 가장 높기 때문에, 사장 고평가받는 기업이다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/06/20 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '이익을 활용한 기업가치 평가', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (49, 'MULTIPLE_CHOICE', '[{"data": "상장기업이 주주와 이해관계자, 그리고 일반인을 대상으로 주가에 영향을 미칠만한 사실을 공개적으로 밝히는 행위는?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["공시", "공표", "공개", "매매"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "기업이 이해관계자와 일반인에게 주가에 영향을 미칠 만한 각종 사실을 공개적으로 밝히는 행위다. 공시는 사실성과 즉시성을 갖춰야만 투자자에게 올바른 정보를 신속하게 전달할 수 있다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', null, null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (50, 'MULTIPLE_CHOICE', '[{"data": "A기업과 B기업의 주가 및 순이익, 그리고 주가이익비율에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_IMAGE", "preface": "null", "dataType": "image", "data": ["img_1.png"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["A기업은 현재의 주가로 투자할 경우 10년간의 순이익으로 투자원금을 회수 할 수 있는 기업이다", "B기업은 A기업보다 상대적으로 저평가되었다고 평가할 수 있다", "A기업의 주가가 B기업의 주가보다 절대적으로 비싸다", "주식시장에서 A기업은 B기업에 비해 향후 성장성이 높다고 평가받는다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "주가이익비율(PER)은 주가를 주당순이익(EPS)으로 나눈 값이다. PER은 기업의 이익을 주식시장에서 얼마만큼 주가로 평가하고 있는 지를 판단할 수 있는 지표이다.\n지문 ①은 PER의 개념을 설명하는 것으로, PER 10.0배는 현재 주가로 투자할 경우 10년간의 순이익으로 투자원금을 회수할 수 있다는 의미이다.\n지문 ②에서 B기업의 PER는 4.0배(2만원/5,000원)이므로, A기업에 비해 상대적으로 저평가되어 있다.\n지문 ③에서 A기업의 PER가 10배, B기업의 PER가 4배이지만, A기업이 B기업보다 절대적으로 비싼 기업으로 평가하기는 단정짓기 어렵다. A기업의 성장성에 비해 실제 주가가 낮게 거래되고 있을 수도 있다.\n지문 ④에서 A기업의 PER가 B기업보다 높기 때문에, 성장성 측면에서 A기업을 주식시장에서는 높을 것으로 평가할 수 있다.\n"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', 'PER 적용', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (51, 'MULTIPLE_CHOICE', '[{"data": "A~C기업의 재무비율에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_IMAGE", "preface": "null", "dataType": "image", "data": ["img_1.png"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["A기업은 타인부채에 대한 상환능력에서 가장 불안정한 기업이다", "B기업은 이자비용을 지불하고 나면 실제 수익은 거의 없다고 해도 과언이 아니다", "C기업의 상황이 수년간 지속되었다면 한계기업으로 분류할 수 있다", "C기업은 3기업 중에서 수익성이나 안정성 측면에서 가장 불리한 기업이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "매출액 대비 영업이익률은 수익성지표이며, 이자보상비율은 안정성지표이다. \n지문 ①에서 A기업은 타인부채에 대한 상환능력 측면에서 가장 안정적이다\n지문 ②에서 B기업의 이자보상비율이 1이기 때문에, 영업이익과 이자비용이 같다는 의미이다\n지문 ③에서 수익대비 이자부담비용이 높은 기업을 한계기업으로 표현하고 수년간 이같은 상황이 지속되면, 소위 ‘좀비’기업이라고 부른다.\n지문 ④에서 C기업은 수익성과 안정성 측면에서 가장 열위의 기업이다"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '재무비율의 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (52, 'MULTIPLE_CHOICE', '[{"data": "회사가 파산신청을 하고 청산절차를 거치는 과정에서 가장 늦게 잔여재산을 받게 되는 자산으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["보통주", "은행대출", "선순위채권", "기업어음"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "주식은 회사의 자산 중 자기자본의 지분권을 표시한 유가증권이다. 주식을 보유한 주주는 잔여재산 청구권을 갖지만, 청산과정에서 타인자본이 자산에 대한 우선권을 행사한 다음에 남아있는 재산에 대해 청구권을 행사할 수 있다. 만일 타인자본이 모든 재산을 가져간다면, 주주 입장에서는 청구할 수 있는 잔여재산이 하나도 남지 않을 수 있다.\n따라서 주주 입장에서는 주가장부가치비율(PBR)이 중요하다. \n"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (53, 'MULTIPLE_CHOICE', '[{"data": "주식가치의 적정성을 평가할때 활용하는 지표로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["PER", "PBR", "PSR", "ELW"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "지문 1은 주가이익비율(또는 주가수익비율)으로, 주가를 주당 순이익으로 나눈 값이다.\n지문 2는 주가장부가치비율(또는 주가순자산비율)으로, 주가를 주당 순자산으로 나눈 값이다.\n지문 3은 주가매출비율으로 주가를 주당 매출액으로 나눈 값이다.\n지문 4는 파생결합증권의 하나인 주가워런트증권(Equity Linked Warrant)으로, 주식가치 평가지표와는 무관한 금융투자상품의 하나이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/15 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주가배율의 정확한 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (54, 'MULTIPLE_CHOICE', '[{"data": "주식회사의 주주에 대한 설명으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주주는 타인자본의 소유자이다", "주주는 원칙적으로 보유 지분보다 많은 주주총회 투표권을 갖는다", "주주는 회사 경영성과에 대해 무한책임을 진다", "주주는 주식의 가치가 상승할 경우 주식을 매도하여 자본차익을 얻을 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "주주는 주식회사에 자본을 출자하여 지분권을 소유한 투자자로서, 기업 입장에서는 자기자본의 주체이다.\n지문 1에서 주주는 타인자본이 아닌 자기자본의 지분 소유자이다.\n지문 2에서 주주는 보유 지분(주식)만큼만 의결권을 행사할 수 있다.\n지문 3은 주주의 유한책임권을 잘못 설명하고 있다.\n지문 4에서 주주는 보유 주식을 매각하여 자본차익을 얻을 수 있는 권리를 갖고 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주주 지위 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (55, 'MULTIPLE_CHOICE', '[{"data": "재무제표에 포함되는 항목으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["재무상태표", "현금흐름표", "주석", "산업연관표"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "재무제표는 기업의 모든 경영활동을 회계처리 방식으로 정리한 경영성적표이다. 재무제표는 크게 재무상태표와 손익계산서, 현금흐름표와 자본변동표, 그리고 주석으로 이루어진다.\n지문 1의 재무상태표는 자산/부채/자본으로 구성된 기업의 재무상태를 보여준다.\n지문 2의 현금흐름표는 일정 기간의 현금성 자산의 조달과 지출내역을 보여준다\n지문 3의 주석은 재무상태표 등의 보완설명 등 회계정책과 근거, 질적 정보 등 보충설명을 제공하는 참고사항이다.\n지문 4의 산업연관표는 한국은행이 작성하는 국민계정 분석도표로, 생산과정에 포함된 재화와 서비스의 생산과 처분을 경제활동별로 파악할 수 있는 표이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '재무제표 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (56, 'MULTIPLE_CHOICE', '[{"data": "기업의 재무상태와 경영성과를 평가하기 위해 수익성, 활동성, 안정성 등의 재무비율을 활용하는데, 이 중 안정성 비율에 해당하는 것으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자기자본이익률", "유동비율", "부채비율", "이자보상배율"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "재무비율은 기업의 재무제표 항목을 활용하여 기업의 경영성과와 안정성, 활동성 등을 평가할 수 있는 지표이다. 해당 기업의 경영성과를 직관적으로 이해할 수 있으며, 기업간 비교도 가능하다.\n지문 1의 자기자본이익률은 수익성 지표의 일부로, 자기자본 대비 순이익의 비율을 파악하여 투입된 자기자본 대비 이익을 얼마나 벌어들였는지를 파악하는 재무비율이다\n지문 2의 유동비율은 단기부채의 상환능력을 파악할 수 있는 안정성 지표의 하나이다.\n지문 3의 부채비율은 자기자본 대비 부채를 얼마나 부담하고 있는 지를 파악하는 안정성 지표이다.\n지문 4의 이자보상배율은 부채에 대한 이자를 영업이익 내에서 어느 정도 감당하고 있는 지를 파악하는 안정성 지표이다.\n"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '안정성비율 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (57, 'MULTIPLE_CHOICE', '[{"data": "기업의 재무상태와 경영성과를 평가하기 위해 수익성, 활동성, 안정성 등의 재무비율을 활용하는데, 이 중 수익성 비율에 해당하는 것으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["유동비율", "이자보상배율", "자기자본이익률", "옵션가치"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "기업의 재무제표를 활용하여 수익성이나 안정성을 파악할 수 있는 지표가 재무비율이다. 재무 비율에는 수익성/안정성/성장성 등을 파악할 수 있는 지표들이 있다.\n지문 1의 산식은 단기부채의 상환능력을 파악할 수 있는 유동부채를 의미한다.\n지문 2의 산식은 이자비용을 영업이익으로 어느 정도 감당할 수 있는 보여주는 이자보상배율이다.\n지문 3은 투입한 자기자본 대비 순이익을 얼마나 발생하였는지를 파악하는 자기자본이익률으로서, 수익성 지표에 해당한다.\n지문 4는 옵션의 가치를 권리행사 실현여부(내재가치)와 실현가능성(시간가치)로 구분한 산식이다.\n"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '수익성비율 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (58, 'MULTIPLE_CHOICE', '[{"data": "A 기업의 한해 경영성과를 다음과 같은 표로 정리하였다. A 기업의 1주당 배당수익률로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_IMAGE", "preface": "null", "dataType": "image", "data": ["img_1.png"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["5%", "10%", "20%", "50%"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "배당은 기업의 경영성과를 주주와 공유하는 이익배분의 일환이다. 기업의 이익잉여금에서 배당금을 지급하기 때문에, 배당의 유무와 규모는 기업의 재무건전성을 확인할 수 있는 지표 중 하나이다.\n주당 배당수익률은 1주당 배당금을 현재 주가로 나누어 산출한다. 현재 주가가 10,000원이고 주당 배당금이 500원이므로, 배당수익률은 5%이다. \n"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '배당수익률 계산', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (59, 'MULTIPLE_CHOICE', '[{"data": "증권유통시장에서 해당 기업의 주식을 신규로 거래하기 위해 해당 기업의 주식을 모집 또는 매출의 방식으로 주식을 발행하거나 매각하는 것을 의미하는 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주주총회", "유상증자", "기업공개", "액면분할"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "신규공모(Initial Public Offering) 또는 기업공개는 해당 기업의 주식을 (신주)모집하거나 (구주)매출하여 해당 기업을 주식시장에 공개하는 것을 의미한다. 구주매출은 기존 주주가 보유한 주식을 매각하는 방식이며, 신주모집은 기존 주주 이외 신주를 발행하여 투자자를 확보하는 방식이다.\n지문 1의 주주총회는 주식회사의 최고 의사결정기구로 주주가 참석하여 회사의 기본조직과 경영에 관한 중요한 사항을 의결하는 기관이다.\n지문 2의 유상감자는 지본금을 축소하면서 기존 주주에게 보상하는 방식이다.\n지문 4의 액면분할은 주식의 액면가를 일정 배율로 나누는 것을 말한다. 예를 들어 액면가 5,000원 주식을 액면가 1,000원으로 나누면, 기존보다 주식수가 5배 늘어난다.\n"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', 'IPO 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (60, 'MULTIPLE_CHOICE', '[{"data": "장내주식시장에 대한 설명으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["매수자와 매도자가 불특정 장소에서 필요에 따라 언제든지 거래가 가능하다", "결제와 청산의 과정이 장외시장보다 투명하지 않을 수 있다", "거래할 수 있는 상품이 표준화 및 규격화되어 있다", "매매규정이 불분명하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "주식 유통시장의 장내시장은 공인된 거래소에서 거래가 이루어지는 시장이다. 법적, 제도적으로 공인된 거래소이며, 불특정 다수가 참여하여 거래하기 때문에 거래할 수 있는 상품은 표준화, 규격화되어 있다. 거래의 공공성과 결제의 안정성을 담보해야 하기 때문에, 결제와 청산과정이 투명하게 관리된다. 거래는 거래소에서 규정한 거래시간 내에서만 이루어지며, 매매시스템과 거래규칙이 명확하게 제정되어 있다. \n지문 1과 2, 4는 ‘장외시장’의 특징을 언급하고 있다.\n"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '상장주식거래 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (61, 'MULTIPLE_CHOICE', '[{"data": "주식회사 주주에 관한 설명으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["모든 주주가 회사 이사회 결의에 참여하여 의결권을 행사할 수 있다", "주주는 회사가 청산될 때 잔여재산에 대해 청구권을 행사할 수 있다", "기존 주주는 회사가 유상증자를 할 때 원천적으로 배제된다", "주주는 회사가 유상감자할 때 보상을 받지 못한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "주주는 자기자본의 지분권을 소유한 투자자로서, 경영성과에 따른 이익배당을 청구할 수 있으며, 회사가 청산될 때 잔여재산에 대해 청구권을 행사할 수 있다.\n지문 1에서 주주는 주주총회에서 의결권을 행사할 수 있으며, 이사회는 주주총회에서 선임된 이사로 구성되어 회사의 중요한 업무집행을 결정한다.\n지문 2에서 주주는 회사청산시 잔여재산에 대한 청구권을 행사할 수 있다\n지문 3에서 회사가 유상증자를 주주배정/제3자배정/일반공모 방식으로 진행하며, 주주배정은 기존 주주에게 신주를 인수받을 수 있는 권리를 배정한다. 제3자배정은 기존 주주가 아닌 제3자에게 신주인수권을 배정하는 것이며, 일반 공모는 불특정 다수에게 공개적으로 유상증자를 진행한다. 원천적으로 기존 주주가 유상증자에서 배제되는 것은 아니다.\n지문 4에서 회사가 유상감자를 하면 주주에게 보상하면서 주식을 매입하여 소각한다.\n"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주주의 권리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (62, 'MULTIPLE_CHOICE', '[{"data": "주주총회는 주식회사의 최고 의사결정기관으로, 주주가 참석하여 회사의 핵심 사안을 결정한다. 다음 중 주주총회의 의사결정 사항으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["합병의 승인", "정관의 수정", "감사의 선임", "대표이사의 선임"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "주주는 보유한 주식수만큼 주주총회에서 의결권을 행사할 수 있다. 주주총회는 이사회가 소집하여, 주주총회 결의 사항으로는 이사와 감사의 선임과 해임, 정관의 수정, 합병의 승인, 자본의 감소 등이다.\n대표이사 선임은 상법에 따르면 이사회에서 선임한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주주의 권리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (63, 'MULTIPLE_CHOICE', '[{"data": "성장성이 높은 유망 스타트업이 자본확충을 위해 IPO를 통한 거래소 상장을 고민 중이다. 기업이 선택할 수 있는 기업공개 방안에 대한 설명으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기존 주주들이 보유하고 있는 주식을 일반투자자에게 매각하여 자본을 조달할 수 있다. 다만 기존 주주의 지분율이 줄어들 수 있다", "신주를 발행하여 일반투자자에게 매각하는 신주모집 방식만 가능하다", "기업공개 과정에서 회사의 일반적인 경영상황과 재무제표 등은 상장 이후 공개할 수 있기 때문에, 별도의 정보는 제공하지 않아도 된다", "주주발행 방식의 기업공개는 회사의 자본금을 늘리는 것과는 무관하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "IPO방식으로는 신주모집, 구주매각 등 여러가지 방식이 있다. 기업공개 과정에서 상장 전 투자자 모집을 원활히 하기 위해 회사의 일반적 경영상황과 재무제표 등을 공개하고 있다. 기업공개는 필연적으로 주식 수가 늘어나기 때문에 자본금이 늘어난다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', 'IPO 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (64, 'MULTIPLE_CHOICE', '[{"data": "주가배수에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주가가 동일하더라도 주당순이익이 작을수록 PER은 높아진다", "PBR은 주당순자산을 주가로 나눈 값이다", "PER이 높다면, 주당순이익은 평균인데 주가가 높거나 주가는 평균인데 주당순이익이낮은 경우로 나누어 볼 수 있다", "PBR이 1 이하라면 기업의 청산가치에 비해 주가가 저평가되어 있음을 의미한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "주가이익비율(PER, 또는 주가수익비율)은 주가를 주당순이익(EPS)으로 나눈 값이다. PER이 높다면 분자인 주가가 높거나 분모인 주당순이익이 낮은 경우이다.\n주가장부가치비율(PBR, 또는 주가순자산비율)은 주가를 주당순자산으로 나눈 값이다. PBR이 1보다 낮다면, 기업의 청산가치가 주가보다 높다는 것을 의미한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주가배율의 정확한 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (65, 'MULTIPLE_CHOICE', '[{"data": "주식 발행시장의 기능에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["원활한 자금조달에 기여한다", "모험자본의 참여를 통한 자본시장 활성화에 기여한다", "성장기업에 투자할 수 있는 투자수단을 제공한다", "발행을 통해 특정인에 대한 자본집중을 심화시킨다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "주식 발행시장은 기업입장에서 경영활동에 필요한 자본을 조달할 수 있으며, 투자자 입장에서는 기업에 투자할 수 있는 투자수단(주식)을 확보할 수 있는 기회이다. 유휴자본을 자본시장에 참여할 수 있도록 도와 자본시장을 활성화시킨다. \n발행을 하게 되면, 기존 주주의 지분율은 줄어들고 신규 주주의 참여가 이루어지기 때문에 특정인에 대한 자본집중을 심화시키기 보다는 자본의 분산을 도모한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '발행시장 기능 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (66, 'MULTIPLE_CHOICE', '[{"data": "□□기업이 주식을 새로이 발행하는 방식으로 자금조달을 계획하고 있다. □□기업이 선택할 수 있는 발행방식에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["재무적 협력사가 자금지원을 약속함에 따라, 해당 투자자를 대상으로 사모발행 방식의 주식 발행을 추진할 수 있다", "주주배정 방식으로 기존 주주에게 공개적으로 신주를 투자할 수 있는 기회를 제공할수 있다", "C기업이 비상장 기업일 경우, 일반 공모방식으로 불특정 다수에게 공개적으로 신주를 투자할 수 있는 기회를 제공하여 자본을 조달할 수 있다", "기존 주주에게 무상증자를 하면, 계획하였던 자본 조달을 달성할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "기업이 주식을 발행하는 방식은 크게 사모방식과 공모방식이 있다. 사모 발행은 특정 또는 소수의 투자자에 한정하여 주식을 발행하고 자본을 조달하는 방식이다. 공모 발행은 크게 기존 주주에게 신주를 투자할 수 있는 기회를 제공하는 주주배정방식과 불특정 다수에게 신주를 투자할 수 있도록 하는 일반공모 방식이 있다.\n한편 주식발행은 주주에게 유상으로 주식을 발행하는 유상증자와 무상으로 주식을 발행하는 무상증자로도 구분할 수 있으며, 무상증자는 자본의 새로운 유입이 없이 잉여자본으로 기존 주주에게 신주를 배분한다.\n무상증자로는 새로운 자본을 조달할 수 없다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자금조달 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (67, 'MULTIPLE_CHOICE', '[{"data": "기술적 분석 중 이동평균선에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["일정 기간의 주가 평균을 의미하며 이동평균을 구하는 방식은 단순산술평균부터 지수이동평균이나 기하이동평균 등 다양한 편이다", "이동평균선은 해당 기간에 투자자들이 거래한 평균단가로 이해할 수 있다", "이동평균선을 주가가 상향돌파하는 상황을 골든크로스(Golden cross)라고 하며 중요한 주식매도 신호로 인식한다", "이동평균선보다 주가가 아래에 위치한다면 기간 평균에 비해 주가가 낮게 형성되고 있음을 의미한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "이동평균선은 기술적 분석에서 중요한 보조지표이다. 일정 기간의 주가 평균치를 선으로 연결하여 현재의 주가와 비교하거나 이동평균선 자체의 흐름을 분석할 수 있다. \n이동평균선을 주가가 상향돌파하는 상황을 골드크로스로 부르며, 중요한 주식매수 신호로 인식된다. 반대로 이동평균선을 주가가 하향돌파하는 상황을 데드크로스(Dead Cross)라고 부르며, 중요한 주식매도 신호로 인식된다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/27 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '이동평균선 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (68, 'MULTIPLE_CHOICE', '[{"data": "기술적 분석에서 거래량에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "ㄱ.", "dataType": "text", "data": ["거래량이 많아졌다는 것은 매매의사를 가진 투자자들이 몰려들었다는 것을 의미하며, 주가에 중요한 변화가 발생하였음을 반영하는 현상이다", "거래량이 이전에 비해 줄어들었다면, 해당 주식의 추세가 반전될 여지가 높아졌다고 평가할 수 있다", "주가 상승구간에서 거래량이 동반하여 늘어나면, 해당 주식의 상승추세가 강화될 가능성이 높아졌다고 평가할 수 있다", "거래량은 매수자와 매도자가 체결한 수량이며 투자자의 의사가 반영된 결과이기 때문에, 거래량을 시장 참여자들의 에너지(힘) 수준을 파악할 수 있는 지표이다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄴ", "ㄹ", "ㄱ와 ㄷ", "해당사항 없음"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "지문에 서술한 모든 내용이 기술적 분석에서 ‘거래량’의 활용방안을 설명하고 있다. \n거래량은 시장참여자들의 의사가 반영된 결과물이기 때문에, 시장참여자의 에너지 정도를 파악할 수 있는 보조지표이다. 가격의 추세가 강화되는 구간에서 거래량도 동반하여 증가하는 경향이 많다. 추세가 약해지는 구간에서는 거래량도 감소한다. 따라서 거래량이 줄어드는 상황은 이전 추세가 반전될 여지가 높은 상황이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/25 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '거래량 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (69, 'MULTIPLE_CHOICE', '[{"data": "주식분석방법 중 기술적 분석에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기술적 분석은 주가의 흐름이나 여타 보조지표의 추세에서 나타나는 특징을 분석하여 매매시점을 포착하는 방식이다", "기술적 분석은 주가 변동이 투자심리와 수급상황의 변화 등에 의해 주로 결정된다고 가정하기 때문에 기본적 분석과는 전혀 분리된 주식 분석 방법이다", "기술적 분석에서 내재가치를 등한시한다면 주가의 근본적인 변화를 포착하기 힘들 수 있다", "기술적 분석에서 일반적으로 활용되는 것은 주가를 표시한 챠트이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "기술적 분석은 과거의 가격흐름에서 나타나는 특징적인 현상들을 파악하고 현재의 시장상황과 유사한 국면에서의 주가 흐름을 유추하여 매매시점을 포착하는 방식이다. 주가는 기본적으로 기업의 내재가치를 중심으로 형성되지만, 투자심리 등 다양한 변수에 의해 진폭을 형성할 수 있기 때문에, 기본적 분석과 병행해야만 주가의 적절한 가치평가와 매매에 대한 의사결정을 내릴 수 있다. 기술적 분석은 주로 주가 챠트를 통해 이루어지며, 주가 이외의 다양한 보조지표를 활용한다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/26 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '기술적 분석 기본적 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (70, 'MULTIPLE_CHOICE', '[{"data": "다음과 같은 주식에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["A주식 : 주가 10,000원 액면가 5,000원", "B주식 : 주가 2,000원 액면가 1,000원 (6개월 전 액면분할 실시 5000원→1000원)"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["A주식은 B주식보다 5배 비싸지만 두 주식의 주가는 모두 액면가 대비 2배이기 때문에 실질적으로 A주식은 B주식보다 2.5배 비싸다", "B주식의 발행주식수는 액면분할 이전에 비해 5배 늘어났다", "B주식의 액면분할 당시 분할이전의 주가보다 1/5 수준으로 하락하였을 가능성이 높다", "A주식과 B주식의 주가수익비율(PER)가 5.0배로 동일하다면 주가의 차이에도 불구하고 내재가치 측면에서는 비슷하다고 평가할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "액면분할은 기존 액면가격을 나누어 비율만큼 발행주식수를 늘리는 것이다. 기존 액면가 5000원에서 1000원으로 액면분할을 한다면, 발행주식수는 기존 1주에서 5주로 늘어난다.\n지문 ①에서 만일 A주식이 5,000원에서 1,000원으로 액면분할한다면, 직관적으로 주가 역시 10,000원에서 2,000원으로 1/5 낮아질 수 있다. 주가를 비교 평가할 때, 액면가 대지 주가는 참고지표일 뿐, 주가의 가치적도로 활용하지 않는다. 지문 ②와 지문 ③은 액면분할로 인해 통상적으로 발생할 수 있는 현상이다.\n지문 ④에서 두 기업의 PER가 5.0배로 동일하다면, 내재가치 측면에서 비슷하다고 평가할 수 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/24 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '액면가와 시가 구별', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (71, 'MULTIPLE_CHOICE', '[{"data": "□□회사는 비상장 회사이다. 투자자 A는 □□회사가 보유하고 있는 기술력이 유망할 것으로 판단되어, 이 회사 주식을 매수하려고 한다. A가 취할 수 있는 방법으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["□□회사의 주권이 한국금융투자협회에서 운영하는 ‘K-OTC시장’에서 거래할 수 있는 지를 확인하고, 거래가 가능하다면 적정 가격으로 매수 주문을 제출한다", "□□회사는 비상장 회사이기 때문에, 기업에 대한 정보를 파악하기가 용이하지 않다.최대한 감사보고서나 관련 뉴스 등을 취합하여 적정 매수가격을 산출해 본다", "인터넷 싸이트에서 □□회사의 주식을 보유하고 있다는 사람과 접촉하여, 매수자금부터 먼저 송금하고 주식을 건네받기로 약속하였다", "□□회사가 자금조달을 위해 유상증자를 시도할 때, 유상증자에 참여할 수 있는 방법을 모색하여 투자를 시도한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "비상장 회사의 주권은 거래소 등 장내시장이 아니라 장외시장에서 매매할 수 있다. 장외시장에서 거래되는 주권은 대체로 해당 회사에 대한 기업정보나 공시 등이 부족하기 때문에 투자정보를 적시에 충분히 얻기 힘들다. 또한 장외시장은 사적인 매매가 대부분이기 때문에, 결제위험을 감수하고 거래를 진행해야 한다. \n한국금융투자협회에서는 장외시장에서 발생할 수 있는 결제위험과 불투명한 거래방식을 개선하기 위해 ‘K-OTC’시장을 운영하고 있다. 호가정보와 시세정보가 투명하게 공개되고, 매매체결의 안정성이 담보된다. \n일반적인 거래가 어렵다면, 회사의 증자를 이용하여 자금을 납입하고 주권을 받는 방법도 가능하다. 다만 증자가 빈번하지 않고 증자에 참여할 수 있는 기회가 제한되는 경우가 대부분이다.\n사적인 거래과정에서 가장 큰 문제는 결제위험이다. 주권 보유여부도 확인하지 않고 매입자금부터 먼저 송금한다면, 결제위험에 노출될 수 있다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '비상장주식거래 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (72, 'MULTIPLE_CHOICE', '[{"data": "기업의 재무제표 중 ''주석''에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주석은 기업의 회계정책이나 재무제표 작성근거, 본문에 표시되지 않는 질적 정보 등 재무제표 이해에 필요한 보충 정보를 제공하는 제무제표의 항목이다", "주석은 지급보증이나 우발채무 등 재무상태표 등에서 찾기 어려운 내용을 제공한다", "주석에는 일정 기간 발생한 수익과 비용의 경영성과 내용을 표시하며, 매출과 순이익을 파악할 수 있다", "주석에는 관계기업의 현황과 요약 재무정보도 포함되어 있어, 관련기업으로부터 얻을 수 있는 수익/손실 여부 등을 파악할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "재무제표에는 재무상태표와 손익계산서, 자본변동표와 현금흐름표, 그리고 주석이 포함된다. 주석은 기업의 회계정책과 재무제표 작성근거, 우발채무나 관계기업의 현황 등 기업의 재무상황을 파악할 수 있는 보충자료가 자세하게 서술되어 있다. 따라서 주석은 재무제표의 본문을 보면서 반드시 참조해야 할 참고서이다. 지문 ③은 현금흐름표를 설명한 내용이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/23 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '재무제표 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (73, 'MULTIPLE_CHOICE', '[{"data": "\"삼성전자 우\"에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["“삼성전자 우”는 삼성전자 주식 중 우선주를 의미한다", "“삼성전자 우”를 보유한 주주는 주주총회에 참석하여 안건에 대해 투표할 수 있다", "“삼성전자 우”를 보유한 주주는 보통주 주주보다 이익배당에서 우선 순위에 있다", "“삼성전자 우”는 보통주보다 일반적으로 배당률이 높은 편이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "주권 중 보통주와 우선주의 구분은 무엇보다 의결권의 유무이다. 주주총회에서 안건에 대해 투표를 통해 표결에 참여할 수 있는 주주는 보통주를 보유한 주주이며, 우선주는 의결권은 없는 대신 이익배당에서 우선 순위에 있다. 일반적으로 국내에서 우선주는 보통주보다 배당률이 높은 편이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/21 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '우선주 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (74, 'MULTIPLE_CHOICE', '[{"data": "캔들챠트에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["시가보다 종가가 높다면, 캔들챠트의 몸통은 빨간색으로 표시된다", "시가보다 종가가 높다면, 결과적으로 주가가 장중에 상승세를 보인 것이다", "장중 고가와 저가를 캔들챠트의 몸통 위아래에 표시하여, 장중 가격 등락의 범위를 보여준다", "캔들챠트의 위에서 아래까지의 길이가 길어질수록, 장중 가격 등락은 매우 적은 편으로 해석한다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "국내 주식시장에서 활용하는 챠트는 대부분 캔들챠트이다. 일중에 관찰되는 시가와 고가, 저가와 종가를 한 개의 몸통에서 모두 표시할 수 있다. 국내에서는 시가보다 종가가 높다면 몸통을 빨간색으로 칠하고, 시가보다 종가가 낮다면 몸통을 파란색으로 표시한다. 장중 고가와 저가는 몸통 위아래에 선으로 표시한다. \n따라서 캔들챠트의 위에서 아래까지의 길이가 장중 가격 등락의 폭을 보여준다. 위에서 아래까지의 길이기 길어질수록, 장중 가격등락이 큰 편임을 알 수 있다. 캔들챠트는 일중 주가의 흐름과 변동성을 동시에 보여준다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/22 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '캔들챠트 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (75, 'MULTIPLE_CHOICE', '[{"data": "중앙은행이 금리인하를 통해 시중금리의 하락을 유도할 때 경제에 끼칠 것으로 예상되는 영향으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["저축의 증가를 통한 경제활동 활성화", "예금 등 금리상품 대비 주식, 부동산 등 실물경제 상대적 매력 증가", "대출 등 조달금리 하락으로 투자와 생산 증가에 영향", "생산활동, 투자활동 방향으로 시중의 유동성 이동"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "중앙은행의 금리인하는 시중 유동성을 저축 등 이자상품에서 실물경제 분야로 이동시켜 경제를 활성화시키려는 노력의 일환이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '기준금리 영향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (76, 'MULTIPLE_CHOICE', '[{"data": "<보기>의 (    ) 안에 들어갈 말로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["일반적으로 경기가 좋아지면 주가는 상승하나 채권 가격이 하락하고, 반대로 경기가 나빠지면 주가는 하락하나 채권 가격이 상승한다. 이는 경기라는 변수에 대해서 주식과 채권 가격이 (         )이기 때문이다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["상관계수가 1", "상관계수가 0", "상관계수가 마이너스", "상관계수가 플러스"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "상관계수는 -1부터 +1까지 산출되며, +1은 완전히 같은 방향으로 움직이는 것을, -1은 완전히 반대 방향으로 움직이는 것을 의미한다. 상관계수가 마이너스 라는 것은 대체로 반대방향으로 움직인다는 것을 의미한다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '상관계수 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (77, 'MULTIPLE_CHOICE', '[{"data": "경제지표에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["GDP(국내총생산)와 경기종합지수는 일반적으로 금융시장 참여자가 시장에서 가장 중요하게 생각하는 경제지표라 할 수 있다", "GDP수치는 분기별로 발표하는데 금리나 주가에 즉각적인 영향을 미치기 때문에 발표 직전 금융시장은 발표치 추이에 신경을 곤두세운다", "경기종합지수는 경제의 각 부문을 대표하는 경제지표들을 가공, 종합하여 하나의 지표로 나타난 것이며 선행지수, 동행지수, 후행지수로 나누어진다", "실업률, 전경련BSI(기업경기실사지수), 통계청CSI(소비자전망지수) 등도 금융시장에서 관심 가지는 기타 주요한 경제지표이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "금리나 주가에 직접적인 영향을 미치는 것은 GDP 지표 발표치 자체가 아니라 한국은행과 각 금융 기관이 전망하는 향후 GDP 추정치와 이에 대한 시장의 컨센서스 변화인 경우가 많다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '경제지표 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (78, 'MULTIPLE_CHOICE', '[{"data": "물가와 채권 금리의 관계에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["향후 물가가 크게 오를 것으로 예상되면 돈에 대한 수요가 증가해 시중 금리는 오르게 된다", "물가가 기대치보다 크게 오르게 되면 물가 안정을 제1의 목표로 삼는 한국은행은 기준금리를 올리는 등 시중 자금을 흡수해 돈의 공급을 줄이는데, 이는 시장 금리를 상승시키는 원인이 된다", "물가 상승으로 한국은행의 기준금리 인상 등 통화긴축이 예상될 경우 주가에 긍정적인 영향을 끼친다", "소비자 물가 지수는 한국은행의 통화 정책 결정 시 중요한 참고가 되기에 금융시장에 미치는 영향이 큰 편이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "물가 지수의 변화가 채권시장에 끼치는 영향은 직접적인 데 반해 주식시장에 끼치는 영향은 다소 간접적이다. 물가 상승으로 한국은행의 기준금리 인상 등 긴축 통화가 예상될 경우 단기적으로 주가에는 좋지 않은 영향을 끼칠 수 있다. 하지만 장기적으로 물가 수준의 상승은 곧 기업이 보유하고 있는 자산 가격의 상승, 기업이 창출하는 명목 이익의 상승으로 연결되므로 주가에 긍정적으로 작용한다는 분석이 많다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '물가의 영향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (79, 'MULTIPLE_CHOICE', '[{"data": "시중금리의 변화로 <보기>의 (    ) 안에 들어갈 말을 순서대로 올바르게 나열한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["경기하락예상 → (       )", "통화긴축정책 → (       )", "확장적 재정정책 → (       )", "주식시장 상승 → (       )"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금리 하락, 상승, 상승, 상승", "금리 하락, 하락, 상승, 상승", "금리 하락, 상승, 하락, 상승", "금리 하락, 상승, 하락, 하락", ""]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "기준금리 상향 등을 통한 통화 긴축정책은 시중 유동성을 흡수하며 금리상승을유발하며 확장적 재정정책 (경기부양 재정정책)은 국채발행과 경기부양 가능성 확대로 금리상승을 유발한다. 주식시장 상승은 경기지표의 호전과 채권시장 자금의 주식시장 이동을 통해 금리상승을 유발한다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '금리변동요인 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (80, 'MULTIPLE_CHOICE', '[{"data": "증시주변 자금에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자자들이 가장 관심 깊게 지켜보는 증시주변자금 지표는 고객예탁금과 신용잔고의 추이인데 일부 전문가는 고객예탁금과 신용잔고를 합한 총계 금액을 종합 증시주변자금으로 계측한다", "고객예탁금 : 언제든지 주식매입 자금으로 동원될 수 있는 대기자금 성격이 강하기에 증가할수록 주식시장에 호재로 해석하는 전문가들이 많다", "신용잔고 : 언젠가 주식의 매도 등을 통해 상환해야 하기에 일정 기간내 주식 매도가 발생할 가능성이 매우 높은 일종의 매도대기물량으로 계산된다", "주식 공매도 : 과열된 주가를 정상화시키기도 하며 증권시장의 유동성을 높이기도 하는 순기능이 있지만 불법적인 매매를 위해 주식 공매도가 실행되는 역기능도 존재한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "일부 전문가는 고객예탁금에서 신용잔고를 차감한 금액을 종합 증시주변자금으로 계측하기도 한다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '증시주변자금 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (81, 'MULTIPLE_CHOICE', '[{"data": "환율과 경제의 상호관계에 관한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자국 상품의 경쟁력에 비해 자국의 환가격이 약해지면 무역수지를 포함한 경상수지나 국제수지의 흑자 증가로 경제성장에 큰 플러스 요인이 된다", "다른 나라 통화들에 비해 우리나라의 환이 싸진다는 것은 다른 나라들의 입장에서는 우리나라에서 생산되는 상품 또는 주식, 부동산 등 국내 자산들의 가격이 떨어진다는 것과 같은 이야기다", "자국 상품의 경쟁력에 비해 자국의 환가격이 강해지면 국부의 가치 증가로 경제성장에 큰 플러스 요인이 된다. 이른바 전 세계적으로 확산되어 있는 소리 없는 전쟁, 환율전쟁의 이유이다", "1997년 IMF 외환위기 전까지만 하더라도, 미국과 일본의 플라자 합의로 한국이 수혜를 입었던 3년(1986~1988년)을 제외하고는, 한국은 수십년간 고질적으로 무역수지 적자를 지속하였다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "수 많은 국가들이 자국의 통화가치를 약하게 하여 외국 통화의 유입을 유도하는 물밑 정책을 펼치고 있다. 이른바 전 세계적으로 확산되어 있는 소리없는 전쟁, 환율전쟁이 그것이다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '환율의 영향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (82, 'MULTIPLE_CHOICE', '[{"data": "자본시장과 거시경제 변수 등에 대한 설명으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["시중 금리 수준이 높아지면 더 많은 대출이 일어나 시중에 통화량(유동성)이 더욱 풍부해진다", "한국은행은 기준금리 수준 결정, 시중 은행의 지급준비율 결정, 통안채 발행량 조정의 3가지 주요 방법을 통해 시중 통화량을 조절한다", "GDP는 한 나라의 경제 활동을 포괄적으로 나타내며 1년에 1번 연말에 발표된다", "수많은 국가들이 자국의 통화가치를 강하게 하여 국가의 달러 환산 부(富)가 높아지도록 유도하고 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "①시중 금리 수준이 높아지면 대출이 줄어들어 시중 유동성이 줄어들게 된다.\n③GDP는 분기별로 발표하기 때문에 금리나 주가에 직접적인 영향을 미치는 것은 지표 발표 자체가 아니라 한국은행과 각 금융 기관이 전망하는 향후 GDP 추정치와 이에 대한 시장의 컨센서스 변화인 경우가 많다.\n④수많은 국가들이 자국의 통화가치를 약하게 하여 외국 통화의 유입을 유도하는 물밑 정책을 펼치고 있다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '거시경제변수 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (83, 'MULTIPLE_CHOICE', '[{"data": "자본시장과 거시경제 변수 등에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식과 채권을 함께 투자하면 경제 환경 변수에서 위험이 줄어드는 효과를 얻을 수 있는데 이는 경제환경이라는 변수 하에서 주식과 채권 가격의 상관계수가 마이너스이기 때문이다", "이론적으로 주식의 비중을 30% 내외, 채권의 비중을 70% 내외로 분산투자할 때 전체 포트폴리오의 위험은 현저히 낮아져 채권만 100% 보유하는 것보다도 위험은 작으면서도 수익은 더 높은 것으로 알려져 있다", "채권의 금리들이 현저히 떨어진 초저금리 시대에도 경기가 좋지 않을 때 채권 가격이 상승하며 주식의 가격하락 위험을 상쇄시켜주는 주식+채권 포트폴리오 효과를 크게 기대할 수 있다", "초저금리 시대에 들어, 너무 낮은 금리의 채권으로서는 더 이상 포트폴리오 위험을 상쇄시키는 효과를 거두기 힘들기 때문에, 고배당주, 일부 해외 채권 등 다른 투자자산에 관심을 가지는 일도 초저금리 시대의 포트폴리오 투자 대안이 될 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "③중장기 채권의 금리들이 현저히 떨어져 있을 경우 채권의 금리가 추가하락할 수 있는 여지가 적어져 경기가 좋지 않을 때 채권 가격이 상승하며 주식의 가격하락 위험을 상쇄시켜주는 주식+채권 포트폴리오 효과를 크게 기대하기 어렵다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '거시경제변수 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (84, 'MULTIPLE_CHOICE', '[{"data": "행동편향을 통한 실수가 금융투자상품과 서비스를 선택할 때 빈번히 발생하는 이유에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융투자상품에 대한 경험과 기회가 적다", "많은 금융투자 의사결정이 감정적으로 이루어진다", "투자의사결정의 실수가 있었다면 이를 반복하지 않기 위해 노력한다", "금융투자자들이 금융상품을 복잡하게 여겨 제대로 알고 의사결정을 하려 하지 않는다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "지문 1의 경우, 금융상품에 대한 학습경험의 기회가 적기 때문에 행동편향에 따라 행동한다.\n지문 2의 경우, 많은 금융투자 의사결정이 비용 대비 효용을 따지기보다는 스트레스, 걱정, 손실이나 후회에 대한 두려움에 떠밀려 의사결정을 한다.\n지문 3의 경우, 금융소비자가 행동편향에 따라 투자의사를 결정하면 혹독한 대가를 치룬 후 후회하는 모습을 반복할 수 있다. 이런 투자의사결정의 실수를 극복하기 위해서 내가 가진 행동편향을 이해할 필요가 있다.\n지문 4의 경우, 대부분의 금융투자자들은 금융투자상품을 복잡하게 여겨서 제대로 알고 의사결정을 하려는 시간과 노력을 기울이지 않고 행동편향에 따라 선택한다.\n"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 발생이유 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (85, 'MULTIPLE_CHOICE', '[{"data": "다음 신문 기사의 내용을 통해 김씨의 행동편향으로 예상되는 것으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["<기사> 투자 경험이 없는 김씨는 어느 날 친구로부터 주식으로 돈을 벌었다는 얘기에 귀가 솔깃했다. 좋은 종목 있으면 추천해달라고 친구에게 요청했고, 친구는 증권 브로커 B씨를 소개해주었다. 이씨는 전문가처럼 주식에 관해 유창하게 설명해 주었고 바이오 종목 1개를 추천했다. 김씨는 1천만원을 투자했고, 이익이 났다. 김씨는 투자에 대해 자신감이 붙었고 금액도 1억원으로 늘리고 투자종목도 늘렸다. 그러나 몇 개 종목에서 손해가 나기 시작했고 원금까지 손실을 입기 시작했다. 덜컥 겁이나 이제라도 그만해야 싶었지만 손실이 커져 그만할 수 없었다. 급기야 원금을 반이나 날린 후에 진작 그만두었어야 한다고 후회했다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["군중심리 편향", "사후확증 편향", "손실회피 편향", "자기과신 편향"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "지문 1의 경우, 투자 경험이 없는 김씨가 친구가 수익이 났다는 얘기만을 듣고 귀가 솔깃해 추천을 권유한 것은 군중심리 편향에 가깝다.\n지문 2의 경우, 사후확증 편향은 이미 발생한 일의 결과를 알고 나면 그 일이 벌어질 수 밖에 없었고 이미 그렇게 될 줄 알고 있었던 것처럼 믿게 되는 오류로 상기 기사내용과는 거리가 멀다.\n지문 3의 경우, 원금까지 손실을 입기 시작한 이후 그만하지 못한 것은 손실을 회피하려는 편향 때문이다.\n지문 4의 경우, 투자에 대한 경험이 없었으나 초기 투자 성공으로 투자에 대한 자신감이 붙어 투자 금액을 쉽게 늘린 것은 자기과신 편향의 영향으로 볼 수 있다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '사후확증 편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (86, 'MULTIPLE_CHOICE', '[{"data": "한 설문조사 결과 운전자의 88%가 자신이 평균보다 운전을 잘한다고 생각하며 그 중 80% 이상이 자신의 운전실력이 상위 30% 안에 든다고 생각한다고 했다. 이렇듯 사람들은 본인에 대한 과도한 믿음, 즉 자기과신을 하는 경향이 강한다. 이러한 자기과신 편향이 투자의사결정에 영향을 주었을 때의 행동으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["그 팀장 설명도 그럴싸한데 다가 지난 번에도 그 말대로 되더라니까. 이번에도 맞을 거야", "처음에는 조금 불안하기도 했는데 매달 꼬박 꼬박 수익금이 들어오더라. 그래서 더 돈을 넣으려고. 너도 해봐", "낮은 금리의 퇴직연금 포트폴리오를 조정해야지 조정해야지 생각만하고 계속 그대로 놨둬 기회를 놓쳐버렸어", "내가 1년 간 지켜봤는데 이 주식은 계속 오르지 않고 올랐다가 내려갔다 반복만 해. 이 정도 내려가면 다시 올라가니까 지금 사야해"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "지문 1의 경우, 자기자신 뿐만 아니라 타인의 말을 그대로 믿고 행동하는 것도 그런 대상을 내가 잘 골랐다는 자기과신 편향의 영향이다. \n지문 2의 경우, 폰지 사기의 전형적인 수법으로 몇번의 수익금 수령이 초기 불안을 없애고 자기과신을 통해 더 많은 돈을 투자한다.\n지문 3의 경우, 현상유지 편향이 영향을 주었을 때의 행동으로 조정할 의사가 있거나 조정해야 할 상품을 그대로 유지하게 한다.\n지문 4의 경우, 자기과신 편향은 이렇다 할 대표성이 없는 몇차례의 경험을 통해 더욱 단단해져 이번에도 지난번처럼 될 거라 믿고 행동하게 만든다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '현상유지편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (87, 'MULTIPLE_CHOICE', '[{"data": "다음 투자 격언은 어떤 행동편향에 대한 주의를 주고 있는가?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["우리가 위험에 빠지는 이유는 무언가를 몰라서가 아니다. 확실히 안다고 착각해서다. - 마크 트웨인"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["심적회계", "기준점 편향", "손실회피 편향", "자기과신 편향"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "지문1의 경우, 사람들은 돈에 대한 기준이나 가치를 모두 같은 것으로 생각하지 않고 각각의 용도에 따라 다르게 생각하며 취급하는 것을 말한다.\n지문2의 경우, 사람들이 합리적이거나 객관적인 판단이 아닌 사실이나 숫자를 기준으로 삼고, 이를 바탕으로 결정을 내리는 편향이다.\n지문3의 경우, 사람들이 이득보다 손실이 더 크게 느껴, 이익 증가보다 손실 증가에 민감하게 느끼는 것을 말한다.\n지문4의 경우, 사람들은 자신의 능력 또는 판단이 스스로에게 유리한 결과를 가져다 줄 것이 믿고 행동한다. 이러한 자기과신 편향은 자신이 가지고 있는 능력이나 정보, 지식 수준을 실제보다 더 높거나 정확하다고 믿게 만들어 섣불리 위험한 투자를 할 수 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자기과신편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (88, 'MULTIPLE_CHOICE', '[{"data": "군중심리는 주식시장이 급격히 오르거나 떨어질 때 자주 작동하여 시장을 과열시키거나 쉽게 얼어붙게 한다. 이러한 군중심리에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["남들이 주식으로 돈을 벌었다는 소식이 들리면 소외감이 커져 군중심리가 발동한다", "경험이 적은 금융투자자이거나, 자신감이 없는 금융투자자일수록 군중심리에 현혹되지 않는다", "군중심리에 빠지는 것은 다른 이들과 다른 결정을 내리는 것보다, 그들과 함께하는 것이 더 마음이 편하고 쉽기 때문이다", "사회에 순응하고 싶어하는 인간의 본능 때문에 대다수와 다르게 행동함으로써 군중에서 소외되고 싶지 않아 군중심리가 생긴다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "군중심리의 기저에는 사회에 순응하고 군중에서 소외되고 싶지 않은 인간의 본능이 깔려 있다. 이에 따라 남들이 투자를 통해 돈을 벌었다거나 하면 거기서 소외되지 않게 투자에 뛰어든다. 경험이 적은 금융투자자이거나, 자신감이 없는 금융투자자일수록 군중심리에 더 취약하다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '군중심리편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (89, 'MULTIPLE_CHOICE', '[{"data": "심리학적 분석에 따르면 인간의 사고 체계는 두가지 시스템에 의해 작동한다. 빠르게 직관적으로 생각하는 시스템(휴리스틱)과 느리고 노력이 필요한 시스템(알고리즘)이 그것이다. 사람들은 금융투자 의사결정시에 후자을 사용한다고 착각하지만 전자를 사용해 결정하는 경우가 많다. 금융투자 시 전자의 시스템은 이익과 손실상황에서 어떤 성향을 보이는가?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["이익상황에서는 위험회피성향을, 손실상황에서는 위험회피성향을 보인다", "이익상황에서는 위험회피성향을, 손실상황에서는 위험보유성향을 보인다", "이익상황에서는 위험보유성향을, 손실상황에서는 위험보유성향을 보인다", "이익상황에서는 위험보유성향을, 손실상황에서는 위험회피성향을 보인다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "향후 주식시장에 대한 긍정적인 전망이 우세할 때 이미 수익이 난 계좌를 보유한 투자자는 긍정적인 전망에도 불구하고 이익을 실현하고 싶어한다. 미래의 불확실성이 싫기 때문에 당장의 이익을 챙겨야 할 것 같아서이다. 반대로 주식시장에 대한 부정적인 전망이 우세 할 때 이미 손실이 난 계좌를 보유한 투자자는 시장상황이 부정적이어서 지금의 손실규모가 더 커질 수 있음에도 사람들은 손실을 확정 짓지 않고 위험을 보유하려고 한다. 이익상황에서는 위험회피성향을, 손실상황에서는 위험보유성향을 보이고 있는 것이다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '손실회피성향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (90, 'MULTIPLE_CHOICE', '[{"data": "합리적인 투자자라면 시장 상황이나 미래 가치 등을 판단하여 보유하고 있는 주식의 매도 여부를 결정한다. 그러나 일반적으로 그 종목의 적정가치를 따져보려 하지않고 자신이 구입한 가격만을 최우선 결정요인으로 판단하는 경우가 많다. 이러한 편향을 일컫는 말은 무엇인가?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["확증편향", "기준점 편향", "시기추론 편향", "도박사의 오류"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "사람들은 객관적인 판단이 아닌 사실이나 숫자를 기준으로 삼고, 이를 바탕으로 결정을 내리는데 이를 기준점 편향이라한다. 기준점 편향을 앵커링효과라고 도 한다.  앵커링효과에서 앵커(Anchor)는 배를 정박시킬 때 배가 떠내려가지 않도록 하는 닻을 의미하며 앵커링효과는 배를 정박시키는 닻을 내리듯 사람의 생각이나 판단이 그 기준점을 벗어나지 않는 것을 말한다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '기준점편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (91, 'MULTIPLE_CHOICE', '[{"data": "금융상품 투자시 ‘과거의 운용실적이 미래의 수익을 보장하지 않습니다.’라는 문구는 다음 중 어떤 행동편향을 방지하기 위한 것인가?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["손실회피 현향", "사후확증 편향", "매몰비용의 오류", "과거추세지속가정 편향"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "과거의 운용실적이 미래의 수익을 보장하지 않는다는 것은 투자의사 결정을 할 때, 과거의 수익만을 보고 앞으로도 그런 수익이 지속될 것이라고 판단하는 것은 위험하다는 것을 알리는 것이다. 과거의 수익은 미래의 수익을 담보하는 대표성이 있는 것이 아닌데 앞으로도 과거처럼 수익이 날거라고 믿고 행동하는 과거추세지속가정 편향을 막기위한 것이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '과거추세지속가정편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (92, 'MULTIPLE_CHOICE', '[{"data": "투자를 하기 전에 빠지기 쉬운 대표적인 행동편향으로 짝지어 놓은 것으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["군중심리 편향, 심적회계, 현재중시 편향", "군중심리 편향, 확증편향, 손실회피 편향", "사후확증 편향, 심적회계, 현상유지 편향", "사후확증 편향, 손실회피 편향, 현재중시 편향"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "투자를 하기 전에 빠지기 쉬운 대표적인 행동편향은 군중심리 편향(주위에서 투자를 통해 돈을 벌었다는 소식을 들었을 때 투자를 하지않은 자신만 바보가 된 거 같은 기분이 들어 투자시장에 뛰어든다), 심적회계(예상치 못했던 성과급이나 연말정산 후 세금 환급액이 들어오면 없던 돈인 셈치고 평상시 위험하다고 생각했던 주식에 몰빵 투자한다), 현재중시 편향(자신의 미래 재무목표에 맞는 투자가 아니라, 즉각적인 욕망을 충족하려는 충동에 따라 단기 고수익만을 목표로 투자한다)이다.\n손실회피 편향이나 확증편향, 사후확증 편향은 투자 후 빠지기 쉬운 대표적인 행동편향이다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (93, 'MULTIPLE_CHOICE', '[{"data": "다음은 방어형 투자자들이 빠지기 쉬운 대표적인 행동편향 중 하나를 설명한 것이다. 이에 맞는 행동편향은 무엇인가?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["저금리시대가 지속되면서 합리적인 금융투자를 통해 미래 자산을 만들어 가야하지만 위험 추구 성향이 낮은 방어형 투자자들은 기본적으로 손실을 두려워해 투자를 꺼려한다. 이들도 저금리 기조가 고착화되어 안정형 상품으로는 미래에 자신이 쓸 자산을 만들기 어려운 것을 인정하면서도 투자에는 여전히 소극적이다. 이는 변화를 좋아하지 않고 불확실한 것을 꺼리기 때문이다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기준점 편향", "도박사의 오류", "현상유지 편향", "매몰비용의 오류"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "현상유지 편향의 주된 원인은 변화에 대한 거부감이다. 이런 편향은 금융투자 관성으로 이어지는데, 저금리 시대에도 과거의 관행을 그대로 유지하며 안정형 상품을 유지하고 있는 것이 대표적인 사례이다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '현상유지편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (94, 'MULTIPLE_CHOICE', '[{"data": "형동경제학에서 기존 전통경제학과 거리를 두고 있는 내용에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["인간의 선택이 온전히 합리적이지 않다고 주장한다", "불확실한 상황에서 인간의 의사결정은 여러 심리적 편향에 흔들린다", "인간은 돈과 관련된 선택과 행동에서는 합리적이지 않은 모습이 더욱 두드러진다", "모든 경제주체는 처한 환경과 자신이 처한 상황을 이해한 후 이성적인 선택을 내린다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "전통적인 주류 경제학은 우리가 경제적 또는 재무적 의사결정을 할 때 ‘인간은 합리적’이라는 가정에서 출발한다. 모든 경제주체는 다양한 환경과 자신이 처한 상황을 이해한 후 이성적인 선택을 내린다는 것이다. 반면 행동경제학은 그 간의 전통 경제학과 달리 인간의 선택이 온전히 합리적이진 않다고 주장한다. 더욱이 돈과 관련된 선택과 행동에서는 이런 합리적이지 않은 모습이 더욱 두드러진다. 행동경제학은 사람들의 선택이 앞뒤가 맞지 않고 목적에도 부합하지 않는 등 일관성이 없는 경우가 빈번한 것을 실험을 통해 확인했다. 불확실한 상황에서 인간의 의사결정은 어림짐작이나 주먹구구식으로 여러 심리적 편향에 흔들린다는 것을 밝혀냈다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동경제학 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (95, 'MULTIPLE_CHOICE', '[{"data": "주식시장에 대한 긍정적인 전망이 우세하여도 이미 수익이 난 계좌의 이익을 실현하고 싶어하거나, 반대로 주식시장에 대한 부정적인 전망이 우세하여도 이미 손실이 난 계좌를 그대로 보유하고자 하는 편향을 설명하는 것으로서 행동경제학의 대표적 이론으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["전망이론", "게임이론", "기대효용이론", "기대-가치이론"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "지문1의 경우, 전망이론은 행동경제학자 대니얼 카너먼과 아모스 트버스키가 주장한 이론으로 사람들이 이익보다 손실에 더 민감하여 이익상황에서는 안전한 선택을, 손실상황에서는 위험한 선택을 하는 것을 밝혀냈다. \n지문2의 경우, 게임이론은 여러 경제주체가 서로를 이기기 위해 경쟁하는 상황에서 그 결론이 어떻게 도출되는지를 설명하기 위한 이론이다. ''게임''이란 이름이 붙은 것은 경쟁자들이 서로를 이기기 위해 전략을 구사하는 상황이 승부를 내는 게임을 연상시키기 때문이다.\n지문3의 경우, 기대효용이론은 행동이 초래하는 결과가 불확실한 상황에서 합리적인 경제주체의 판단은 결과에 관한 효용의 기대치에 입각하여 이루어진다는 이론이다.\n지문4의 경우, 기대-가치이론은 기대와 가치가 동기의 결정요인이라고 가정한다. 어떤 행동을 하는 동기가 생기는 것은 기대요인과 가치요인이 함께 있어야 한다는 것이다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '전망이론 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (96, 'MULTIPLE_CHOICE', '[{"data": "투자를 하다보면 큰 경제 이슈가 발생했을 때, 미디어에 수많은 소위 전문가라는 사람들이 나와 이런 일이 일어날 줄 이미 오래전부터 알고 있었다는 듯이 이렇게 된 원인을 아주 논리적으로 해석하고 설명하는 것을 볼 수 있다. 이처럼 어떤 일이 발행하였을 때 이미 발생한 일의 결과를 알고 나면 그 일이 벌어질 수 밖에 없었고 이전부터 그렇게 될 줄 알고 있었던 것처럼 믿게 된다는 것을 설명하는 행동편향으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기준점 편향", "군중심리 편향", "현재중시 편향", "사후확증 편향"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "지문1의 경우, 기준점 편향은 앵커링 효과라고도 하는데, 배가 떠내려가지 않도록 닻을 내리듯이 생각이나 판단이 기준점을 벗어나지 않는 말한다.\n지문2의 경우, 군중심리 편향은 군중심리에 빠져 다른 금융투자자들의 행동이 합리적인지 따져보지도 않고 막연히 그 행동을 따라하는 것을 말한다.\n지문3의 경우, 현재중시 편향이란 미래에 비해 현재를 중시하는 것으로 즉각적인 욕망을 충족하려는 충동에 따라 행동하는 것을 말한다.\n지문4의 경우, 사후확증 편향은 투자 선택에 있어서 마치 내가 이럴 줄 알았다는 착각을 불러 일으킨다. 그러나 합리적인 투자를 위해서는 그것이 사전이 아닌 사후 진단이라는 것을 기억해야할 필요가 있다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '사후확증 편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (97, 'MULTIPLE_CHOICE', '[{"data": "자기과신 편향을 가지고 있는 사람들이 이에 따른 투자 실패 경험이 있을 때에도 같은 실수를 반복하는 이유를 설명하는 편향으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["내 잘못이 아니라 시장 탓으로 돌린다", "내 잘못이 아니라 상황 탓으로 책임을 회피한다", "투자 실패한 경험은 잊고 성공한 경험만 기억한다", "과거의 성과란 단순한 우연이나 특별한 상황의 산물일 수 있다고 생각한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "사람들은 자신의 능력 또는 판단이 스스로에게 유리한 결과를 가져다 줄 것이라고 믿고 행동한다. 이를 ‘자기과신 편향’이라고 한다. 특정 금융투자상품 또는 투자 정보에 대한 자신의 판단이 다른 사람들의 평가나 판단보다 더 정확하다고 생각하고 과몰입할 뿐 아니라 위험요소는 상대적으로 낮게 평가한다. 투자 위험을 가중시키는 일이다. 사람들이 투자 실패 경험을 반복하는 이유는 지나친 자기과신으로 실수를 외부 탓으로 돌리거나 기억 자체를 잊어버리려고 한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자기과신편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (98, 'MULTIPLE_CHOICE', '[{"data": "<보기>의 (    ) 안에 들어갈 말로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["2001년 바버와 오딘 교수는 주식투자에 있어서 남녀간 차이가 뚜렷이 존재한다는 연구 결과를 발표했다. 이 연구를 통해 남자가 여자보다 (     )편향이 높고, 주식투자에 있어서 남자는 (     )편향으로 인한 과다한 거래로 여자보다 수익률이 낮다고 밝혔다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자기과신", "시기추론", "매몰비용", "현재중시"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "UC-Davis 바버(Brad Barber) 교수와 UC-Berkeley의 오딘(Terrance Odean) 교수는 1991년부터 1997년까지 6년간 무려 35,000 가구의 주식계좌를 살펴본 후 주식투자에 있어서 남녀간 성(性)적 차이가 뚜렷이 존재한다는 연구 결과를 발표했다.\n남자가 여자보다 자기과신 편향이 높아 주가의 상승과 하락을 예상할 수 있다는 확신으로 빈번한 매매를 했을 확률이 높다. 이로 인한 결과는 거래비용 증가와 수익률 악화로 이어질 수 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '매몰비용편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (99, 'MULTIPLE_CHOICE', '[{"data": "군중심리 편향은 투자 뿐만 아니라 소비 등 다양한 분야의 인간 행동에서 일어난다. 군중심리에서 비롯된 행동을 설명한 용어로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["모방소비", "편승효과", "스노브 효과", "밴드왜건 효과"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "지문1의 경우, 모방소비란 다른 사람의 소비 행동을 그대로 따라하는 소비를 말하는 것으로 군중심리에 영향을 받는다.\n지문2의 경우, 편승효과란 소비자가 어떤 재화를 소비할 때, 다른 소비자들이 많이 소비하는 소비 형태를 따라감으로써 수요가 증가하는 현상을 뜻하는 경제학 이론으로 군중심리의 한 형태이다.\n지문3의 경우, 스노브 효과(snob effect)는 다른 사람들이 어떤 상품을 많이 소비하고 있기 때문에, 자신은 그 재화의 소비를 중단하거나 줄이는 것을 말하는 것으로 군중심리에 반대되는 개념이다.\n지문4의 경우, 밴드왜건 효과는 서커스나 퍼레이드 행렬의 선두에서 요란한 퍼포먼스를 펼치며 행렬을 인도하는 마차(밴드왜건, Bandwagon)의 뒤를 따라 사람들의 긴 행렬이 줄줄이 이어지는 모습을 빗대어 표현한 것으로 군중심리를 설명한다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '스노브효과 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (100, 'MULTIPLE_CHOICE', '[{"data": "사후확증 편향은 인간이 기억을 재구성하는 특성 때문에 발생한다. 사람들의 기억을 되돌아 볼 때 불완전한 부분을 자신이 믿고 싶은 것으로 메꾸는 경향이 있는 것이다. 금융투자시 이러한 사후확증 편향에 빠지지 않기 위한 태도로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융투자 과정에서 자신이 저지른 실수를 인정한다", "금융투자에서 필요한 것은 자신감이라는 것을 명심한다", "투자 과정에서 있었던 실수를 통해 배우려는 태도를 취한다", "금융투자시 나의 선택이 틀릴 수 있음을 받아들이고 대비책을 세워 놓는다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "사후확증 편향에서 벗어나기 위해서는 투자 과정에서 자신이 저지른 실수를 인정하고 실수를 통해 배우려는 태도가 필요하다. 또한 나의 선택이 틀릴 수 있음을 받아들이고 대비책을 세워 놓을 필요가 있다. 금융투자자들의 지나친 자신감은 반복된 실수를 가져올 수 있다는 것을 명심하고 자신의 장점과 단점을 현실적으로 직시하는 노력이 필요하다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '사후확증 편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (101, 'MULTIPLE_CHOICE', '[{"data": "각 행동편향과 이에 빠지지 않기 위한 행동을 짝지은 것으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["손실회피 편향: 이익과 손실을 똑같은 가치로 평가하려고 지속적으로 노력한다", "군중심리 편향: 지나친 자신감은 반복된 실수를 가져올 수 있다는 것을 명심한다", "심적회계: 돈에 관한 행위를 할 때는 사용처에 상관없이 동일한 잣대를 가지려고 노력한다", "기준점 편향: 처음 산 가격이나 초기에 알게 된 정보에만 의존하지 않고 다양한 기준으로 투자 대상을 살펴보려 노력한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "지문2의 경우, 사후확증 편향에 빠지지 않기 위한 행동이다. 군중심리에 빠지지 않기 위해서는 군중에서 한걸음 떨어져 다른 금융투자자들이 왜 그런 행동을 보이는지 충분히 따져 본 후 선택하는 태도가 필요하다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (102, 'MULTIPLE_CHOICE', '[{"data": "각 행동편향과 이에 빠지지 않기 위한 행동을 짝지은 것으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["확증편향 : 늘 반대 의견을 구하려고 노력한다", "시기추론 편향 : 보유한 포트폴리오의 점검을 통해 부적한 부분이 있는지 파악하고 이를 방치하고 있었는지 판단한다", "매몰비용의 오류 : 투자가 잘못되어 가고 있다는 것을 발견했다면 과거에 들어간 비용에서 벗어나 합리적인 결정을 내리려고 노력한다", "도박사의 오류 : 독립적인 확률적 사건에 섣부른 기대를 반영하여 인과관계가 있을 것으로 보고 의사결정을 하는 행동을 삼가해야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "지문2의 경우, 현상유지 편향에 빠지지 않기 위한 행동이다. 시기추론 편향에 빠지지 않기 위해서는 앞으로의 10년을 생각하면 아주 멀게 느껴지지만, 지난 10년을 생각해보면 똑같은 10년이지만 꽤 빨리 흘렀다고 느낀다. 이렇듯 자신의 미래를 역방향으로 예측하면 그 시간을 짧게 느끼고 미래에 대비하여 장기 투자해야 한다는 필요성도 더 절실히 느낄 수 있다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '매몰비용편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (103, 'MULTIPLE_CHOICE', '[{"data": "다음을 설명하는 행동편향으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["원금 손실의 위험 때문에 공격적인 투자를 못하던 보수적인 투자자도 투자를 통해 얻은 수익금만을 투자할 때는 태도가 달라진다. 수익금은 어차피 내 돈이 아니었으니 날려도 된다는 태도로 매우 공격적인 투자를 한다. "]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["심적회계", "기준점 편향", "손실회피 편향", "도박사의 오류"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "심적회계에 대한 설명이다.  사람들은 돈에 대한 기준이나 가치를 모두 같은 것으로 생각하지 않고 각각의 용도에 따라 다르게 생각하며 취급한다. 이를 ‘심적 회계(Mental Accounting)’라고 하는데, 사람 마음 속에서 돈에 대한 기준이 항목에 따라 다르다는 것이다. 어디에 쓰는 돈이건 본질적으로 가치가 같다는 점에서 편향이다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '심적회계편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (104, 'MULTIPLE_CHOICE', '[{"data": "다음 투자자들의 대화를 듣고, 이를 토대로 A씨에게 예상되는 행동편향으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["A : 올해 유망 종목이라고 해서 샀는데 오르긴 커녕 아직도 20%나 손해가 나있어.", "B : 이제라도 정리해야 되는 거 아냐?", "A : 아 몰라~. 어여튼 원금 회복되기 전에는 못팔아. 지금도 돈생기면 조금씩 사고 있는데 언젠가는 오르겠지. 아우 내가 정말 미쳐!"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기준점 편향", "현재중시 편향", "손실회피 편향", "매몰비용의 오류"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "지문 1의 경우, 투입 원금이 기준점으로 잡히는 기준점 편향 때문에 주식을 매도하려고 할 때 현재 상황이나 미래 가치 보다 구입했던 가격이 최우선 기준점이 되어 매도 가격에 영향을 미친다.\n지문 2의 경우, 현재중시 편향이란 미래에 비해 현재를 중시하는 것으로 즉각적인 욕망을 충족하려는 충동에 따라 행동하는 것을 말하는 것으로 해당 대화 내용과는  거리가 멀다.\n지문 3의 경우, 사람들은 이익보다 손실이 더 크게 느끼는데 이에 따라 손실 난 투자를 그대로 유지하려고 하는 경향이 있다.\n지문 4의 경우, 매몰비용의 오류는 가지고 있는 주식을 보유할지, 매각할지를 결정할 때 주식의 가격이 앞으로 어떻게 될 지를 고려하는 것이 아니라 들어간 돈 즉 매입가를 가장 중요시 여기는 편향을 말한다.\n"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '현재중시편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (105, 'MULTIPLE_CHOICE', '[{"data": "다음 투자자들의 대화를 듣고, 이를 토대로 투자자들에게 영향을 미치는 행동편향으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["A : 내가 최근에 소개 받은 단톡 주식카페가 있는데 거기서 추천한 종목이 하나 있는데 같이 투자할래?", "B : 정말? 핫한 주식 맞아?", "A : 어 카페 회원들 다 극찬하고 있어! 지금도 좀 올랐는데 앞으로 더 좋을 거래!", "B : 그래? 내가 아는 사람도 대박 종목 하나 잡아서 크게 벌었다던데 그런 종목인가 보다. 하자! 하자!", "A : 넌 친구 잘 둔지 알아"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["심적회계", "기준점 편향", "군중심리 편향", "도박사의 오류"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "군중심리에 빠져 다른 투자자들의 행동이 합리적인지 따져보지도 않고 막연히 그 행동을 따라하는 것을 말한다. 투자 초보자들이 겪는 가장 일반적인 행동편향 중 하나이며, 집단적인 관심이 커질 경우 버블을 만들기도 한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '군중심리편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (106, 'MULTIPLE_CHOICE', '[{"data": "다음 투자자들의 대화를 듣고, 이를 토대로 B씨에게 예상되는 행동편향으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["A : 그 때 샀던 바이오주 지금도 잘나가? 그 대표라는 사람 말대로 신약 허가도 받았고?", "B : 아니 지금은 좀 떨어졌어.", "A : 왜? 신약 허가 못 받았어?", "B : 응. 그래도 괜찮아 조만간 다시 진행할꺼래.", "A : 그 대표 말과 달랐구나. 큰소리 치더만.", "B : 야 니가 뭘 안다고 그래~ 원래 다 그런 거거든 그리고 이 회사는 정말 믿을 만한 회사야. 알지도 못하면서."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["확증 편향", "시기추론 편향", "현상유지 편향", "도박사의 오류"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "확증편향에 빠진 금융투자자는 기존의 믿음에 부합하는 정보만 찾고 그 정보를 더 중요하게 여기고, 모순된 정보의 중요도는 무시하거나 임의로 낮춘다. 또 정보를 자기 믿음에 맞게끔 재해석하기도 하는데, 심지어 모순된 정보 마저도 아전인수식으로 자기 믿음에 유리하게 해석한다. 자기 믿음에 부합하는 정보만 기억하고, 모순된 정보는 잊어버리거나 자기 믿음에 부합한다고 기억해 버리는 것이다. 이는 상황을 직시하지 못하게 하여 그릇된 선택을 하게 만들 수 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '확증편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (107, 'MULTIPLE_CHOICE', '[{"data": "투자자들의 대화를 듣고, 이를 토대로 C씨에게 예상되는 행동편향으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["A : 주식투자는 장기로 해야 한다는데 10년, 20년 후 장기적으로 괜찮을 분야가 뭐가 있을까?", "B : 야 장기는 무슨~, 당장 돈 쓸 일이 태산인데 그래 가지고 어느 세월에 돈벌겠냐? 빨리 오를 종목을 찾아야지! 크게 오르는 종목들이 즐비하고 당장 돈 쓸 일이 태산인데 말이야.", "A : 그런가? 장기투자를 해야 안정적이라고 해서 말이야.", "B : 야 환매를 해서 이익을 얻지 못하려면 뭐 하러 투자를 하냐!"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["확증 편향", "현재중시 편향", "현상유지 편향", "사후확신 편향"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "현재중시 편향에 빠진 투자자들은 미래에 비해 현재를 중시하여, 즉각적인 욕망을 충족하려는 충동에 따라 행동하려고 한다. 이에 따라 금융투자에서도 당장의 이익만을 추구하는 단기 투자에 그치고 노후 같은 장기 목표에 대한 투자를 등한시 한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '현재중시편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (108, 'MULTIPLE_CHOICE', '[{"data": "다음은 행동편향에 빠지지 않기 위한 방법 중 하나로, 오버나이트 테스트에 대한 설명이다. 오버나이트 테스트를 통해 극복하고자 하는 행동편향으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["당신이 보유한 종목들이 투자 손해를 보고 있다. 만약 누군가가 오늘 당신이 보유한 모든 종목을 팔아 현금화해준다면 다음날 그 현금으로 당신이 가지고 있던 그 종목을 다시 매수할 것인가? 만약 다시 매수하지 않을 거라면 그런 종목을 지금은 왜 그대로 가지고 있는가? 오버나이트 테스트는 내가 보유하고 있는 종목들의 가치가 오르거나 내린 주식이 아닌 현금일때도 그 주식을 매수할 것인가를 생각해보고 계속 보유여부를 합리적으로 따져 보는 것이다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["군중심리 편향", "현재중시 편향", "시기추론 편향", "매몰비용의 오류"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "매몰비용의 오류에서 벗어나는 방법은 투자가 잘못되어 가고 있다는 것을 발견했다면 재빠르게 정신을 차리고 되돌아서는 것뿐이다. 매몰비용을 포기하고 합리적인 결정을 내려야 하는데 투자에서는 오버나이트 테스트를 해보는 것도 좋은 방법 중 하나이다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '매몰비용편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (109, 'MULTIPLE_CHOICE', '[{"data": "투자 초보자들이 빠지기 쉬운 오류 중 하나는 투자의 패턴을 발견했다고 착각하는 것이다. 다음과 같은 생각을 갖는 A씨가 가진 행동편향으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["내가 몇 달간 쭉 지켜 지켜봤는데 이 주식은 3일 연속 하락하면 그 다음에는 꼭 오르더라고 그래서 지금 사야해"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["현상유지 편향", "손실회피 편향", "도박사의 오류", "매몰비용의 오류"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "도박사의 오류란 서로 독립적인 확률적 사건이 서로 확률에 영향을 미친다는 착각에 기인한 오류이다. 주가가 오르거나 내릴 확률은 반반일 뿐인데 며칠 연속 떨어졌으니 오를 때가 되었다거나 반대로 며칠 연속 올랐으니 오늘은 떨어질거라 판단하는 것이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '도박사의오류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (110, 'MULTIPLE_CHOICE', '[{"data": "현상유지 편향을 보여 주는 예시로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["퇴직연금(DC)의 경우 최초 운용지시를 한 상품으로 그대로 두거나 방치하는 경우가 많다", "퇴직을 앞둔 직장인들의 가장 큰 후회 중 하나는 노후 준비를 소홀하게 했다는 것이다", "손실이 난 투자 포트폴리오를 그대로 가져가면서, 그 손실이 늘어나도 팔지 않는 경향이 있다", "저금리시대가 지속되고 있는데도 가계금융 자산의 70% 이상이 원리금보장형으로 그대로 운용되고 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "현상유지 편향은 변화를 좋아하지 않고 불확실 것과 마주하는 것을 꺼려하는 행동 상태를 말한다. 금융회사가 추천한 상품을 가입한 후 재평가하지 않고 처음 그대로 방치하거나, 손실난 포트폴리오를 귀찮아서 그대로 가져가기도 한다. \n지문 2의 경우, 시기추론 편향의 영향으로 사람들은 보통 먼 미래의 일은 추상적이고 낙관적으로 생각하는데 이로 인해 노후 준비도, 장기투자도 쉽게 하지 못한다.\n"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '현상유지편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (111, 'MULTIPLE_CHOICE', '[{"data": "투자를 하고 난 후, 수익이 났을 때 빠지기 쉬운 대표적인 행동편향 만으로 올바르게 짝지은 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자기과신 편향, 현상유지 편향, 확증편향", "자기과신 편향, 손실회피 편향, 확증편향", "손실회피 편향, 현상유지 편향, 확증편향", "자기과신 편향, 사후확증 편향, 확증편향"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "투자가 성공적이어서 수익이 났다면 앞으로 더 큰 성과를 낼 수 있다는 자신감을 얻는다. 이런 경우 보통 투자 금액이 이전보다 훨씬 더 커진다.(자기과신 편향)\n또, 이런 성과가 나올 수밖에 없었던 이유를 갖다 붙인다. 결과가 이렇게 될 줄 알고 있었다고 느끼며 행동에 확신이 붙는다. 주변 사람들에게 자신의 성공기를 확신에 찬 모습으로 떠벌리고 다닌다.(사후확증 편향) 만일 자신의 선택이 아니라 주변 사람의 추천을 통해 얻은 수익이라면 그 대상에 대한 신뢰가 매우 커져 그 사람 말만 계속 믿고 투자하려 한다.(확증편향)"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (112, 'MULTIPLE_CHOICE', '[{"data": "금융투자시 자신의 투자성향을 확인하고 그에 맞게 포트폴리오를 구성하는 것은 매우 중요하다. 공격형 투자자들이 빠지기 쉬운 대표적인 행동편향 만으로 올바르게 짝지은 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["확증편향, 자기과신 편향, 도박사의 오류", "확증편향, 현상유지 편향, 매몰비용의 오류", "손실회피 편향, 도박사의 오류, 기준점 편향", "현상유지 편향, 자기과신 편향, 매몰비용의 오류"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "공격형 투자자들은 투자성향상 주식 같은 위험자산에 큰 고민없이 선뜻 투자한다. 그리고 자신의 선택에 대해 확신을 가지며 자신의 생각과 다른 의견에는 잘 귀기울이지 않는다.(확증편향) 이런 행동이 수익으로 이어졌다면 자신의 투자 감각을 과신하며 모든 게 자신의 능력 때문이라고 확신한다.(자기과신 편향) \n주식투자를 쉽게 생각하며 분산하지 않고 몰빵 투자를 하는 등 더 용감한 투자를 하기도 한다. 다른 사람들이 수익을 내고 있다는 소리를 전해 들으면 그 상품에 대해서도 자산의 능력을 믿고 쉽게 투자를 고려한다.(군중심리 편향) \n만약 성과를 내지 못하고 손실을 본다 해도 손실의 원인을 분석하기보다는 손해를 만회하고자 하는 욕구가 더 강해지며 다음 번에는 실패하지 않을 거라 믿는다.(도박사의 오류)"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (113, 'MULTIPLE_CHOICE', '[{"data": "감정에 휘둘리지 않는 합리적인 의사결정의 한 방법으로 행동편향을 극복하기 위한 행동으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융투자시 자신이 이성적인 존재임을 믿고 선택한다", "금융투자시 자신이 겪었던 행동편향이 무엇인지 확인한다", "금융투자시 투자 원칙과 기준을 세우고 투자 과정을 객관적으로 기록한다", "금융상품과 서비스에 대한 의사결정을 해야 할 때 행동편향의 영향을 받고 있는지 항시 살펴본다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "지문 1의 경우, 행동경제학에서는 인간의 선택이 온전히 이성적이지 않고 앞뒤가 맞지 않거나 목적에도 부합하지 않는 등 일관성이 없다고 보았다.\n행동편향을 극복하기 위해서는 행동편향이 무엇인지 인지하고 자신이 겪었던 행동편향을 확인하며, 행동편향에 흔들리지 않도록 투자 원칙과 기준을 세우고 투자 과정을 기록하며, 의사결정시 행동편향을 영향을 받고 있는지 스스로 확인해야 한다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (114, 'MULTIPLE_CHOICE', '[{"data": "행동편향의 영향에서 벗어날 수 있기 위해서는 감정이나 상황에 흔들리지 않는 명확한 의사결정이 필요하다. 이를 잘 보여주는 사례로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["하이리스크-하이리턴을 명심한다", "투자과정을 기록하고 모니터링 한다", "과거의 실패는 잊고 새로운 마음으로 투자한다", "자산배분과 리밸런싱 등 투자원칙을 세우고 기록한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "지문 1의 경우, 기대수익률에 대한 투자 위험을 인지함으로서 자기과신이나 확증편향에 빠진 맹목적인 투자를 지양하게 할 수 있다. \n지문 2의 경우, 투자과정을 기록함으로서 자신의 감정적인 선택의 결과를 확인할 수 있고 이를 통해 반복된 실수를 피할 수 있다.\n지문 3의 경우, 투자는 과거의 실수를 반복하지 않기 위한 리뷰가 필요하다. 새로운 기대만을 가지고 임하는 투자는 실패를 반복할 수 있다.\n지문 4의 경우, 자산배분의 원칙을 세우고 리밸런싱 기준을 통해 투자를 관리하는 것은 행동편향의 영향에서 벗어나 초심을 유지할 수 있게 도와준다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (115, 'MULTIPLE_CHOICE', '[{"data": "금융투자자들이 행동 편향에서 벗어나 합리적인 의사결정을 하기 위한 방법으로서 ''투자과정 기록 및 모니터링''에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기억은 지극히 주관적이어서 왜곡되기도 하지만 기록은 객관적이다", "어떤 생각으로 투자 대상을 선택하였고 어떻게 관리할 계획인지 기록한다", "투자결과 중 성공한 투자만을 골라 이유를 평가하고 기록하여 성공전략에 집중한다", "기록내용을 모니터링 해 다음 투자시 자신의 행동편향에 휘둘리지 않고 합리적인 의사 결정을 하는데 이용한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "자신의 투자 경험을 기록하는 것은 반복된 실수를 피하는 가장 효과적인 방법이다. 기억은 지극히 주관적이어서 왜곡되기도 하지만, 이에 비해 기록은 객관적이다. 자신이 어떤 생각으로 투자 대상을 선택하였고 어떻게 관리를 할 계획인지를 기록한다. 성공한 투자의 성공 이유는 물론 실패한 투자의 실패 원인이 무엇이었는지 냉정하게 평가하고 기록해 둔다. 이는 다음 투자시 자신의 행동편향에 휘둘리지 않고 합리적인 의사 결정을 하는데 지속적으로 도움을 준다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (116, 'MULTIPLE_CHOICE', '[{"data": "행동편향에 대한 설명으로 <보기>의 (      ) 안에 들어갈 말을 순서대로 올바르게 나열한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["( ㉠ )은 사람들이 경제적 또는 재무적 의사결정을 할 때 인간은 합리적이라는 가정에서 모든 경제 주체는 이성적인 선택을 한다고 전제한다. 그러나 ( ㉡ )은 인간의 선택이 온전히 합리적이진 않으며, 사람들의 선택이 앞뒤가 맞지 않고 목적에도 부합하지 않는 등 일관성이 없는 경우가 빈번함을 실험을 통해 확인했다.  현재는 금융 시장을 이해하고 올바른 선택을 하기 위해서는 ( ㉢ )이 얘기하는 인간의 ‘행동편향’을 이해하고 극복하고자 노력하는 것이 필요하다는 인식이 점점 더 확산되고 있다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["㉠ 행동경제학, ㉡ 전통경제학, ㉢ 전통경제학", "㉠ 행동경제학, ㉡ 전통경제학, ㉢ 행동경제학", "㉠ 전통경제학, ㉡ 행동경제학, ㉢ 전통경제학", "㉠ 전통경제학, ㉡ 행동경제학, ㉢ 행동경제학"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "전통경제학은 우리가 경제적 또는 재무적 의사결정을 할 때 ‘인간은 합리적’이라는 가정에서 출발한다. 모든 경제주체는 다양한 환경과 자신이 처한 상황을 이해한 후 이성적인 선택을 내린다는 것이다.  행동경제학은 그 간의 전통 경제학과 달리 인간의 선택이 온전히 합리적이진 않다고 주장한다. 더욱이 돈과 관련된 선택과 행동에서는 이런 합리적이지 않은 모습이 더욱 두드러진다. 행동경제학은 사람들의 선택이 앞뒤가 맞지 않고 목적에도 부합하지 않는 등 일관성이 없는 경우가 빈번한 것을 실험을 통해 확인했다. 불확실한 상황에서 인간의 의사결정은 어림짐작이나 주먹구구식으로 여러 심리적 편향에 흔들린다는 것을 밝혀냈다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동경제학 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (117, 'MULTIPLE_CHOICE', '[{"data": "행동경제학은 심리학과 경제학이 만나 인간의 판단과 의사결정을 설명하는 학문이다. <보기>의 (      ) 안에 들어갈 말을 순서대로 올바르게 나열한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["심리학적 분석에 따르면 인간의 사고 체계는 두가지 시스템에 의해 작동한다. 먼저 빨리 생각하는 ( ㉠ )인 시스템 1(자발적인 통제 없이 저절로 빠르게 작동)와 그 다음 느리고 ( ㉡ )인 시스템 2(노력이 필요한 정신 활동이 요구되는 때에만 작동)이다. 선택의 순간, 시스템 1과 시스템 2가 충돌한다. 대부분 자신이 ( ㉢ )로 판단한다고 여긴다. 자신이 논리적이며 합리적이고 이성적으로 결정할 수 있다고 믿는 것이다. 그러나 실제로는 ( ㉣ )이 작동하여 처리하는 경우가 많고 또 반복적이기까지 하다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["㉠ 기본적인 사고체계, ㉡ 노력이 필요한 사고 체계, ㉢ 시스템 2, ㉣ 시스템 1", "㉠ 기본적인 사고체계, ㉡ 노력이 필요한 사고 체계, ㉢ 시스템 1, ㉣ 시스템 2", "㉠ 노력이 필요한 사고 체계, ㉡ 기본적인 사고체계, ㉢ 시스템 2, ㉣ 시스템 1", "㉠ 노력이 필요한 사고 체계, ㉡ 기본적인 사고체계, ㉢ 시스템 1, ㉣ 시스템 2"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "심리학적 분석에 따르면 인간의 사고 체계는 두가지 시스템에 의해 작동한다. 먼저 빨리 생각하는 기본적 사고체계인 시스템 1(자발적인 통제 없이 저절로 빠르게 작동)와 그 다음 느리고 노력이 필요한 사고체계인 시스템 2(노력이 필요한 정신 활동이 요구되는 때에만 작동)이다. 선택의 순간, 시스템 1과 시스템 2가 충돌한다. 대부분 자신이 시스템 2로 판단한다고 여긴다. 자신이 논리적이며 합리적이고 이성적으로 결정할 수 있다고 믿는 것이다. 그러나 실제로는 시스템 1이 작동하여 처리하는 경우가 많고 또 반복적이기까지 하다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동경제학 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (118, 'MULTIPLE_CHOICE', '[{"data": "투자의 중요한 특징 중 하나는 ''결정을 해야 한다''는 것이다. 무엇을 언제 얼마나 사서 언제 얼마나 팔아야 할지를 결정을 해야 한다. 이러한 결정은 투자자가 가지고 있는 행동편향에 크게 영향을 받는다. 행동편향을 대하는 태도로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융투자시 인간은 행동편향에 빠지기 쉬운 존재임을 인정한다", "오랜기간 형성된 행동편향을 바로 잡는 것도 마음만 먹으면 단기에 고칠 수 있다", "행동편향으로 인해 투자 의사결정시 합리적이지 않은 선택을 할 수 있음을 인지한다", "금융투자에 대한 기대만이 아니라 변동성 등 투자의 위험을 충분히 인지하고 대비하는 투자 태도를 갖는다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "지문 2의 경우, 오랜 기간 동안 형성된 자신이 행동편향을 바로 잡는 것 역시 오랜 시간이 소요될 수 있다.일상생활하는 가운데 의사결정시마다 자신의 행동편향들을 의식하고 점검하려는 관심과 노력을 지속적으로 기울여 자신이 행동편향들을 극복해 나가려는 마음가짐과 자기성찰이 필요하다.\n우리가 편향에 빠지기 쉬운 존재임을 인정하고 받아들이는 것이 중요하다. 사람들의 의사결정은 계획적이고 신중하기보다 직관적이고 자동적으로 이루어지는데, 그런 행동이 믿음처럼 좋은 결과를 만들어 내지 못하고 손해로 이어진다. 이런 편향에 빠지는 것은 인간이기에 당연한 것임을 받아들인다. 자신이 편향에 얼마나 취약하고 또 그 결과가 자신에게 손해를 준다는 것을 인지하는 겸손한 태도를 취하는 것이다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (119, 'MULTIPLE_CHOICE', '[{"data": "금융투자회사에 해당하는 것으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["증권회사", "선물회사", "자산운용회사", "상호저축은행"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "우리나라 금융회사는 은행, 비은행 금융회사, 보험회사, 금융투자회사, 기타 금융회사로 분류된다. 이중에 금융투자회사는 증권회사, 선물회사, 자산운용회사, 투자자문회사, 부동산신탁회사, 종합금융회사가 있다. 상호저축은행은 비은행 금융회사다.\n지문1 증권회사는 대표적인 금융투자회사다.\n지문2 선물회사는 금융투자업의 투자중개업(주로 선물)을 주로 하는 금융투자회사다.\n지문3 자산운용회사는 금융투자업 중 집합투자업을 주로 하는 금융투자회사다.\n지문4 상호저축은행은 비은행 금융회사로 비은행 금융회사에는 상호저축은행, 여신금융전문회사, 대부업자, 상호금융이 있다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '금융투자회사 종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (120, 'MULTIPLE_CHOICE', '[{"data": "금융투자회사의 6가지 기본업무 중에 ‘개인투자자가 주식과 채권 등을 사고 팔수 있도록 역할’을 하는 업무로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자매매업", "투자중개업", "신탁업", "투자자문업"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "지문1 투자매매업은 금융투자회사가 ‘회사의 고유재산으로 투자’하는 것을 말한다. \n지문2 개인투자자가 투자하기를 원하는 주식, 채권 등을 단순하게 사고 팔 수 있도록 ‘중개’하는 역할이다. 중개는 시장에서 사자와 팔자를 연결하는 기능만 한다는 것이다. 따라서 중개기능을 수행한 금융투자회사에 가격체결 수준을 이유로 문제를 제기 할 수 없다. \n지문3 신탁업은 위탁자가 법률행위에 의하여 자기재산을 수탁자에게 이전하고, 수탁자는 그 재산을 신탁목적에 따라 관리 또는 처분하며, 그 이익을 일정한 수익자에게 귀속시키는 제도다.\n지문4 투자자문업은 특정인을 위하여 금융투자상품의 가치 또는 투자판단에 대한 자문을 제공하는 업(業)을 말한다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '금융투자업 종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (121, 'MULTIPLE_CHOICE', '[{"data": "투자자 A씨가 알고있는 집합투자업에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["집합투자업은 일반적으로 알고 있는 펀드운용을 하는 것이다", "자산운용사 중에는 특정 종류의 자산을 사모로만 운용할 수 있는 회사도  있다", "집합투자업을 영위하는 회사들은 회사명에 OO자산운용 또는 OO투자신탁운용 등을 주로 사용한다", "개인투자자 입장에서 볼 때 내가 투자하고 있는 펀드를 어느 자산운용사가 운용하는 지는 전혀 중요하지 않다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "지문1 집합투자업은 펀드 운용이 주요 업무다.\n지문2 사모전문집합투자기구를 운용할 수 있는 사모전문운용사를 말한다.\n지문3 자산운용사가 주로 사용하는 명칭은 OO자산운용, OO투자신탁운용이다. \n지문4 모든 투자자는 자신이 투자하고 있는 펀드를 운용하는 자산운용사가  어디인지 알고 자산운용사에 대한 평가를 펀드의 평과와 동시에 해야 한다.  펀드투자의 첫걸음은 투자한 펀드를 운용하는 자산운용사에 대한 판단이다.  따라서 전혀 중요하지 않은 것이 아니라, 정말 중요하다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '집합투자업 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (122, 'MULTIPLE_CHOICE', '[{"data": "<보기>의 (    ) 안에 들어갈 내용으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["(          )은 특정 투자자로부터 금융투자상품에 대한 투자판단 전부 또는 일부를 일임받아 투자자별로 구분하여 금융투자상품을 운용하는 업(業)을 말한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자일임업", "투자자문업", "투자매매업", "집합투자업"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "투자일임업은 특정 투자자로부터 금융투자상품에 대한 투자판단 전부 또는 일부를 일임 받아 투자자별로 구분하여 금융투자상품을 운용하는 업(業)이다.\n지문2 투자자문업은 특정인을 위하여 금융투자상품의 가치 또는 투자판단에 대한 자문을 제공하는 업(業)을 말한다.\n지문3 투자매매업은 금융투자회사가‘회사의 고유재산으로 투자’하는 것을 말한다. \n지문4 집합투자업은 우리가 일반적으로 알고 있는 펀드운용을 하는 것이다. 정확하게는 ‘2인 이상의 투자자로부터 모은 금전 등을 투자자로부터 일상적인 운용지시를 받지 아니하면서 재산적 가치가 있는 투자대상자산을 취득, 처분 그 밖의 방법으로 운용하고 그 결과를 투자자에게 배분하여 귀속하는 것’을 말한다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자일임업 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (123, 'MULTIPLE_CHOICE', '[{"data": "<보기>의 (    ) 안에 들어갈 내용으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["금융투자회사가 투자일임업을 수행할 때 사용하는 계좌를 일반적으로 (       )라고 부른다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["DB(Defined Benefit)", "DC(Defined Contribution)", "Wrap Account", "IRP(Individual Retirement Pension)"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "지문1 확정급여형 퇴직연금(Defined Benefit)은 기존의 퇴직금제도와 유사하게근속연수에 평균임금을 곱하여 퇴직금이 산정되는 구조다. \n지문2 확정기여형 퇴직연금(Defined Contribution)은 퇴직금을 직원의 퇴직연금계좌로 지급하는 구조로 운용수익률에 따라 퇴직금의 규모가 변동한다.\n지문3 Wrap Account = Wrap(감싸다) + Account(계좌) 고객의 자산현황, 투자목표, 위험허용도 등을 반영하여 가장 적합한 포트폴리오를 제공하고 그에 따른 고객의 의사결정에 따라 일임받은 투자권한 내에서 주문집행, 결제 등의 업무를 처리하고 사전에 정한 일정비율의 수수료를 받는 종합자산관리서비스\n지문4 개인형 퇴직연금(Individual Retirement Pension)은 퇴직금 및 여유자금을 운용할 수 있는 퇴직금 전용계좌다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자일임업 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (124, 'MULTIPLE_CHOICE', '[{"data": "생애 처음으로 투자를 시작하는 개인투자자 A씨가 한 행동 중에 현행 제도와 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["우체국을 방문해서 주식매매 계좌를 신규개설 하였다", "증권회사에 가서 펀드매매 계좌를 개설하고 펀드를 매수하였다", "증권회사 앱을 다운받아 비대면 계좌를 개설하고 주식을 매수하였다", "은행을 방문해서 개설한 주식매매 계좌에 100만원을 입금하고, 은행직원에게 주식 매수 주문을 하였다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "지문1 우체국에서 주식매매를 위한 위탁계좌를 개설할 수 있다.\n지문2 증권회사는 펀드 판매사로서 펀드 매매를 위한 계좌개설과 펀드 매수를 중개할 수 있다.\n지문3 증권회사의 비대면 채널을 통한 주식 매매 거래의 전형이다.\n지문4 은행은 증권회사를 대신해서 주식매매 계좌의 개설을 대행해 줄 수 있다. 그러나 주식매수 주문은 은행에서는 할 수 없고, 해당 계좌의 원 소속인 증권사를 통해서 매매 주문을 할 수 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '금융투자회사 종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (125, 'MULTIPLE_CHOICE', '[{"data": "투자자 A씨가 온라인에서 본 유사투자자문업자의 행동(광고)중에 현행 자본시장법에 비추어 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["유사투자자문업자는 모두 불법으로 법적 근거가 없다", "안내문에는 불특정 다수인을 대상으로 발행되는 간행물, 전자우편 등에 의한 조언이 가능하다고 한다", "금융감독원에 신고된 유사투자자문업자는 월 30만원 이하로 1대1 투자자문을 하는 것은 합법적이라고 한다", "자신에게 돈을 맡기면 매월 5%씩 안정적인 수익률을 보장한다는 광고와 함께 자신의 계좌수익률 사진을 보내왔다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "지문1 유사투자자문업은 자본시장법 101조(유사투자자문업의 신고)에 따라 금융위원회에 신고한 자는 합법적인 유사투자자문업자다. 관련 규정을 어긴 자만 불법이다.\n지문2 자본시장법 101조(유사투자자문업의 신고)의 규정으로 맞는 말이다.\n지문3 1대1 투자자문은 불법이며 불특정 다수를 상대로 조언을 해야 합법이다.\n지문4 유사투자자문업자의 일임계약은 불법이고, 수익률 약정 등도 과장광고로 금지대상이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '유사투자자문 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (126, 'MULTIPLE_CHOICE', '[{"data": "공모주와 스팩(SPAC)투자에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["공모주 청약은 증권회사 계좌없이 은행에서도 할 수 있다", "공모주 청약은 IPO(Initial Public Offering)를 주관하는 주관사나 공동주관사를 통해서만 청약이 가능하다", "스팩이 만기 시점까지 합병하지 못하는 경우에는 투자자에게 투자금의 원금과 이자를 반환하는 수익구조를 가진다", "스팩은 공모를 통해 M&A자금을 먼저 마련하며 상장한 후 다른회사와 합병하는 것을 유일한 사업목적으로 하는 Paper Company다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "지문1 공모주 청약을 위해서는 금융투자회사를 통해서 청약을 하고, 배정받는 주식을 보유하거나 매매를 통해 이익을 얻을 수 있다. 따라서 증권회사의 계좌는 반드시 있어야 청약이 가능하다.\n지문2 주관사나 공동주관사를 통해서만 해당 공모주의 청약을 할 수 있기 때문에 해당 공모주를 주관(또는 공동주관)하는 증권사의 계좌가 필요하다.   \n* IPO(Initial Public Offering) 또는 상장(Listing) : 기업이 발행한 주권이 한국거래소 (KRX)가 정하는 요건에 따른 증권시장에서 거래가 될 수 있는 자격을 부여 받는 것\n지문3 스팩의 큰 특징중의 하나로 합병이 불발될 때는 원금과 이익금을 돌려받는 구조다.\n지문4 스팩은 합병을 목적으로 하는 명목상의 회사다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '공모주 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (127, 'MULTIPLE_CHOICE', '[{"data": "투자자 A씨가 채권에 투자하기를 원할 때, A씨가 해야 하는 행동으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["OO은행 채권을 직접 매수하기위해 OO은행의 예금계좌를 개설했다", "증권회사 영업점을 방문해서 만기가 2년 남은 OO은행 채권을 매수했다", "장내 국채시장에 들어가서 30년 만기 국채를 500만원씩 두 번 분할 매수했다", "증권사 계좌로 HTS를 통해 장내 채권시장에서 장외채권인 사모사채를 1억원 매수하였다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "지문1 채권매매를 직접하기 위해서는 증권회사의 위탁계좌를 개설해야 한다. 은행을 방문해서 신탁을 통해 매매를 지시할 수는 있으나, 이때도 예금계좌로 활용할 수는 없다.\n지문2 증권회사 영업점을 활용한 장외시장을 통한 채권매매의 전형적인 경우다.\n지문3 장내 국채시장은 국채전문딜러로 지정된 증권회사, 기관만이 참여 가능한 시장이다. 개인이 참여할 수도 없고 거래 단위로 주로 100억 단위\n지문4 증권사 HTS로 거래하는 장내 채권시장은 상장채권만을 거래할 수 있고 사모사채인 장외채권은 매매할 수 없다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권거래 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (128, 'MULTIPLE_CHOICE', '[{"data": "펀드에 투자를 원하는 투자자 A씨가 알고 있는 펀드에 대한 설명으로 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["펀드 기준가를 산출하는 등 신탁회계를 담당하는 곳이 사무수탁회사다", "펀드평가를 하는 집합투자기구 평가회사를 통해 펀드 평가정보를 얻을 수 있다", "펀드는 가입하는 곳에서 운용하기 때문에 어느 금융회사에서 가입하는지가 아주 중요하다", "펀드는 운용재산을 보관하는 수탁회사가 별도로 있어 자산운용과 자산보관이 분리되어 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "지문1 사무수탁회사는 펀드회계를 담당하는 회사다.\n지문2 펀드 평가사를 통해 객관적인 펀드 정보를 얻을 수 있다.\n지문3 펀드는 가입을 하는 판매사와 운용을 하는 운용회사가 분리되어 있다. 가입하는 곳에서 운용한다는 것은 틀린 말이다.\n지문4 수탁회사의 기능에 대한 올바른 설명이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '펀드 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (129, 'MULTIPLE_CHOICE', '[{"data": "투자자 A씨는 투자하고 있는 사모펀드의 자산운용회사가 파산했다는 신문기사를 보았다. A씨의 생각과 행동으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자산운용회사가 파산했기 때문에 투자자산에 대한 손해가 발생할 것이다", "자산운용회사가 파산했지만 펀드를 가입한 판매회사에게 100% 보상 받을 수 있을 것이다", "내 펀드를 운용하는 운용회사가 파산했기 때문에 내가 지정하는 자산운용사로 자산을 옮겨서 운용을 계속하면 된다", "자산운용회사가 파산했지만 내가 가입한 펀드는 투자하고 있는 자산의 시장가치가 상승했기 때문에 별 걱정하지 않는다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "지문1 운용회사의 파산이 투자자산의 손실을 의미하지는 않는다. 운용회사가 파산하더라도 투자자산의 시장가치가 하락하지 않으면 손실은 발생하지 않는다.\n지문2 운용회사가 파산했다고 해서 판매사가 항상 보상을 하지는 않는다.\n지문3 운용회사가 파산해서 자산운용사를 변경할때에는 금융감독당국이 지정하는 다른 자산운용사로 자산이 이동한다.\n지문4 운용회사가 파산했지만 펀드의 투자자산의 시장가치가 상승하면 손해를 보지는 않는다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '펀드 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (130, 'MULTIPLE_CHOICE', '[{"data": "<보기>의 (    ) 안에 들어갈 내용으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["(       )는 Wrap Account의 일종으로 하나의 계좌에서 다양한 일임운용서비스를 통합적으로 제공하는 금융투자상품이다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["UMA", "CMA", "ELS", "DLS"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "지문1 UMA(Unified Managed Accounts)에 대한 설명이다.\n지문2 CMA(cash management account) 어음관리계좌 또는 종합자산관리계좌라 불린다. 고객이 예치한 자금을 CP나 양도성예금증서(CD), 국공채 등의 채권에 투자하여 그 수익을 고객에게 돌려주는 금융상품이다. \n지문3 ELS(Equity Linked Securities) 주가지수 또는 특정주식가격의 변동과 연계되어 수익률이 결정되는 증권\n지문4 DLS(Derivative Linked Securities) 주가 외 기초자산(금리, 통화, 상품, 신용위험 등) 가격의 변동과 연계되어 수익률이 결정되는 증권"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자일임업 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (131, 'MULTIPLE_CHOICE', '[{"data": "투자자 A씨의 거래형태에 대한 설명으로 사실과 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["증권회사 MTS로 위탁계좌에 있는 돈으로 ETF를 매수하였다", "ETF매수를 위해 은행을 방문하여 특정금전신탁에 가입하여 운용지시를 하였다", "은행에서 특정금전신탁으로 매수하는 ETF나 증권회사를 통해 매수하는 ETF는 동일한 것으로 투자비용도 동일하다", "증권회사를 통해 ETF를 매매할때는 실시간 매매가 가능하지만 은행의 특정금전신탁을 통한 ETF매매는 계좌별 실시간 매매는 어렵다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "지문1 증권회사 위탁계좌와 MTS를 활용한 직접매매형태다.\n지문2 ETF에 투자하는 특정금전신탁에 가입하는 형태로 은행의 신탁을 통해서 ETF를 거래할 수 있는 방법이다.\n지문3 특정금전신탁을 통해서 매매하던지 직접 매매하던지 매매하는 ETF는 동일하다. 그러나 투자비용에서는 위탁계좌를 통한 매매와 특정금전신탁을 통한 매매는 큰 차이가 있다. 특정금전신탁이 수취하는 신탁보수가 위탁계좌에는 없고, 매매수수료도 차이가 난다.\n지문4 증권회사를 통한 ETF매매는 한국거래소에 직접 주문을 내지만, 특정금전신탁을 통한 매매는 고객의 운용지시와 특정금전신탁의 주문이 증권사를 통해서 이루어 지기 때문에 아무리 빨라도 실시간 매매라고 할 수는 없다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '실전주식거래 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (132, 'MULTIPLE_CHOICE', '[{"data": "<보기>의 (    ) 안에 들어갈 내용으로 적합한 것을 모두 묶은 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "dot", "dataType": "text", "data": ["금융상품 한눈에(http://finlife.fss.or.kr/)에서는 (       ) 등의 금융상품 금리와 수익률, 보험료 등을 한번에 비교 조회할 수 있다"]}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["㉠예금  ㉡대출  ㉢연금저축  ㉣보험  ㉤펀드"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["㉠, ㉣", "㉠, ㉡, ㉤", "㉡, ㉢, ㉣, ㉤", "㉠, ㉡, ㉢, ㉣, ㉤"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "지문1, 지문2, 지문3, 지문4 ‘금융상품 한눈에’ 에서는 예금과 대출, 연금저축, 보험, 펀드 모두를 한 번에 비교할 수 있다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '비교공시 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (133, 'MULTIPLE_CHOICE', '[{"data": "증권회사의 비대면 계좌를 개설하기 위해 필요한 것과 가장 거리가 먼것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["본인 명의의 스마트폰", "주민등록증 또는 운전면허증", "실명확인이 가능한 본인명의 금융기관 계좌", "증권회사 HTS(Home Trading System)가 설치된 PC"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "지문1 본인명의의 스마트 폰이 필요하다.\n지문2 신분증은 실명확인을 위한 신분증 촬영을 위해 필요하다.\n지문3 실명확인을 위해 필요하다.\n지문4 HTS는 개설된 계좌를 사용하여 거래에 필요한 프로그램이다. 비대면계좌를 개설하기 위해 반드시 필요한 것은 아니다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '계좌개설절차 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (134, 'MULTIPLE_CHOICE', '[{"data": "주식매매를 위한 대기자금도 단기 실세금리 수준의 이자율을 받을 수 있도록 비대면 주식계좌 개설시 함께 계좌를 개설하는 단기 금융투자상품으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ELS", "CMA", "DLS", "IRP"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "지문1 ELS(Equity Linked Securities)는 주가지수 또는 특정주식가격의 변동과 연계되어 수익률이 결정되는 증권\n지문2 CMA(cash management account)어음관리계좌 또는 종합자산관리계좌라 불린다. 고객이 예치한 자금을 CP나 양도성예금증서(CD)·국공채 등의 채권에 투자하여 그 수익을 고객에게 돌려주는 금융상품\n지문3 DLS(Derivative Linked Securities) 주가 외 기초자산(금리, 통화, 상품, 신용위험 등) 가격의 변동과 연계되어 수익률이 결정되는 증권\n지문4 개인형 퇴직연금 IRP (Individual Retirement Pension)개인별로 퇴직금 및 여유자금을 운용할 수 있는 퇴직금 전용계좌"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', 'CMA 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (135, 'MULTIPLE_CHOICE', '[{"data": "스마트 폰을 이용하여 주식매매를 하는 MTS 사용시 사실과 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["MTS 로그인을 위해서는 공동인증서 만을 사용할 수 있다", "비밀번호 대신 지문으로 거래가 가능한 증권회사도 있다", "비대면계좌 개설시 모바일 OTP를 발급 받는 경우 OTP기기를 따로 가지고 다닐 필요가 없다", "한국거래소가 부여한 코드를 입력하지 않아도 종목명 입력이나 음성 검색이 가능하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "지문1 MTS 로그인은 공동인증서 뿐만 아니라 개별금융회사의 인증서( 예:카카오페이인증, KB모바일 인증 등)나 간편인증, 생체인증으로 로그인이 가능하다.\n지문2 MTS 거래시에도 비밀번호 대신 지문으로 거래가 가능하다.\n지문3 모바일 OTP 서비스를 이용하는 경우 별도의 OTP기기는 필요하지 않다.\n지문4 주식종목의 검색방법은 직접입력 또는 음성입력을 통해 검색이 가능하다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '실전주식거래 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (136, 'MULTIPLE_CHOICE', '[{"data": "주식거래를 위해 MTS를 사용할 때 거래의 안전을 위해 필요한 것이 OTP인데 OTP 발급 방법으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["사용기간이 만료된 OTP 재등록", "비대면계좌 개설 시 모바일 OTP 발급", "다른 금융투자회사에서 이용 중인 OTP 등록", "본인의 신분증을 가지고 영업점을 방문하여 OTP 발급"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "지문1 사용기간이 만료된 OTP는 재등록이 불가능하다.\n지문2 비대면 계좌개설이 많은 최근에 가장 선호되는 방법중 하나\n지문3 여러 금융투자회사를 거래하는 경우 가장 효율적인 활용방법\n지문4 가장 오래된 방법이나 기기를 항상 소지하고 있어야 하는 점에서 모바일 OTP에 비해서는 번거로움"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '실전주식거래 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (137, 'MULTIPLE_CHOICE', '[{"data": "MTS를 사용한 국내주식 매매에 있어 사실과 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["메뉴 바에서 메뉴를 찾아 들어가 매매 주문을 낼 수 있다", "국내주식을 매매하는 화면에서는 ETF를 매매 할 수 없다", "MTS의 장점은 장소에 제약을 받지 않고 거래할 수 있다는 점이다", "트레이딩 아이콘을 클릭해서 바로 국내주식 매매를 할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "지문1 MTS에서 업무처리를 할때 업무메뉴 단위를 찾아 들어가는 방법\n지문2 국내주식을 매매하는 화면에서는 ETF매매를 할 수 있다. ETF도 일반 주식종목처럼 한국거래소에 상장되어있는 종목이기 때문이다.\n지문3 MTS 거래는 주식 매매에 있어 시간과 장소의 제약을 받지 않는다.\n지문4 메뉴바를 통한 접근과 함께 업무처리를 단축 시키기 위한 방법으로 흔히 활용된다. 자주 사용하는 메뉴 항목을 아이콘 처리해서 메뉴 접근 시간을 단축하는 방법이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '실전주식거래 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (138, 'MULTIPLE_CHOICE', '[{"data": "국내주식 매매에 있어 사실과 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["국내주식 매매화면은 호가 매수가격과 호가 매도가격을 보여준다", "매수잔량 또는 매도잔량은 해당 가격에 주문을 한 주식 수를 의미한다", "종목마다 한국거래소가 부여한 코드가 있는데 그 코드를 기억하고 있어야 매매가 가능하다", "주식 매매 보통 한 화면에서 매수와 매도 메뉴가 함께 위치하고 있지만 색깔과 화면 제목으로 구분이 가능하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "지문1 매수/매도 호가에 대한 설명이다.\n지문2 매수/매도잔량에 대한 설명이다.\n지문3 코드를 기억할 필요는 없다. 종목명 검색을 텍스트 또는 음성으로 할 수 있다.\n지문4 국내주식 매매 화면은 색으로 구분이 가능하고, 메뉴명칭으로도 손쉽게 구분할 수 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '실전주식거래 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (139, 'MULTIPLE_CHOICE', '[{"data": "<보기>의 (    ) 안에 들어갈 말로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["주식 매매 시 가격을 지정해서 주문하는 것을 ( ㉠ )주문이라고 하고 가격은 정하지 않고 주문을 내면 접수된 시점에 가장 유리한 가격으로 매매가 체결되는 주문을 ( ㉡ )주문이라 한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["㉠ 지정가  ㉡ 시장가", "㉠ 시장가  ㉡ 대용가", "㉠ 지정가  ㉡ 대용가", "㉠ 시장가  ㉡ 조건부지정가"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "지문1 ‘ 지정가주문’과 ‘시장가주문’에 대한 설명\n지문2 증권을 매매할 때 위탁증거금으로 현금 대신 유가증권을 사용할 수 있다. 이때 대용 유가증권의 가격을 ‘대용가’라고 한다.\n지문3 & 지문4 ‘조건부 지정가’는 지정가로 거래를 시도하다가 거래가 체결되지 않으면 장 마감 시점에 시장가도 전환하며 매매 체결을 하는 주문거래 방식이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주문종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (140, 'MULTIPLE_CHOICE', '[{"data": "시장가 주문에 대한 설명으로 <보기>의 (    ) 안에 들어갈 말로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["시장가 주문이란 투자자가 종목과 ( ㉠ )은 지정하되 (  ㉡ )은 지정하지 않고 주문을 내면 주문이 접수된 시점에서 가장 유리한 가격으로 매매가 성립되는 주문을 말한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["㉠ 수량  ㉡ 비율", "㉠ 수량  ㉡ 가격", "㉠ 가격  ㉡ 수량", "㉠ 가격  ㉡ 비율"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "지문1~4 시장가 주문이란 투자자가 (종목), (수량)은 지정하되 (가격)은 지정하지 않고 주문을 내면 주문이 접수된 시점에서 가장 유리한 가격으로 매매가 성립되는 주문을 말한다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주문종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (141, 'MULTIPLE_CHOICE', '[{"data": "해외주식 주문에 대한 설명으로 <보기>의 (    ) 안에 들어갈 말로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["해외주식 매매를 할 때 해외 주식시장의 주요 매매시간이 한국시간으로 심야인 경우에  해외주식시장이 열리기 전에 미리 (        )주문을 할 수 있다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["시장가", "지정가", "대용가", "지정가 예약"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "지문4 해외주식시장이 열리기 전에 지정가 예약주문을 통해 주식매매를 하는것에 대한 설명이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주문종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (142, 'MULTIPLE_CHOICE', '[{"data": "투자자 A씨는 월요일에 OO전자 주식을 매수 하였다. 주식이 결제되어 투자자 A씨의 계좌로 입고되는 것은 언제일까?(월요일 ~ 금요일 사이에 공휴일은 없는 것으로 가정)", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["월요일", "화요일", "수요일", "목요일"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "지문1 매수당일 평가금액이 표시되지만, 결제되어 입고 되지는 않는다.\n지문3 매매가 체결된 주식의 결제시점은 체결일(월요일, D일)로부터 3영업일(D+2일)로 규정되어 있어 수요일이 정답이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '실전주식거래 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (143, 'MULTIPLE_CHOICE', '[{"data": "펀드 매매의 결제와 입출고와 관련해서 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["펀드를 매수할 때와 매도할 때 결제일과 기준가 적용일이 다르다", "펀드는 주식매매와 달리 펀드별로 결제일과 기준가 적용일이 다를 수 있다", "펀드는 매수를 청구한 시간에 따라 적용하는 기준가격이 다르게 적용될 수 있다", "펀드는 원칙적으로 주식의 편입비율과 투자자산의 종류가 같으면 펀드 기준가를 적용하는 기준시간이 모두 동일하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "지문1 매수의 경우에는 D+1일, D+2일 등 펀드마다 다르게 적용되고, 매도의 경우에도 기준가 적용일과 결제일이 다를 수 있다.\n지문2 주식매매는 동일한 날짜에 매매된 경우에는 동일한 날짜에 결제되지만, 펀드의 경우에는 다르게 적용되는 것이 일반적이다.\n지문3 펀드의 투자설명서에 명시된 시간을 기준으로 기준가격이 다르게 적용된다.\n지문4 펀드는 주식의 편입비율과 투자자산의 종류가 같다고 해도 펀드 기준가를 적용하는 기준시간이 다를 수 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '펀드 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (144, 'MULTIPLE_CHOICE', '[{"data": "펀드매수에 있어 일반적으로 적용하는 매수시간(15시 30분, 17시)을 적용하지 않는 펀드를 가장 올바르게 나열한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["레버리지 펀드", "주식형 펀드", "채권형 펀드", "MMF"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "지문1 레버리지 펀드는 당일의 레버리지를 위한 선물포지션 결정을 위해 통상의 15시 30분보다 2시간 정도 빠른 시간을 지정한다.\n지문2 일반적으로 15시 30분을 기준으로 한다.\n지문3 일반적으로 17시를 기준으로 한다.\n지문4 일반적으로 17시를 기준으로 한다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '레버리지투자 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (145, 'MULTIPLE_CHOICE', '[{"data": "펀드매매와 관련한 설명 중 <보기>의 (    ) 안에 들어갈 말로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["펀드의 매수와 매도시에 적용하는 기준가 적용일과 기준시간 등은 펀드를 가입하는 시점에 법적으로 반드시 교부받아야 하는 (            )를 통해 확인하는 것이 가장 정확하다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자확인서", "투자설명서", "자산운용보고서", "자산보관관리보고서"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "지문1 투자확인서는 가입시점에 펀드에 대한 설명을 정확하게 듣고 투자설명서를 교부 받았다고 확인하는 확인서에 불과하다.\n지문2 투자설명서는 가입시점에 반드시 설명을 듣고, 교부받아야 하는 필수서류이며 펀드 매매에 따른 기준가 적용과 관련 사항이 기재되어 있다.\n지문3 운용보고서에도 같은 내용이 기재되어 있기도 하다. 그러나 가입시점에 법적 교부서류인 투자설명서와는 다르다.\n지문4 자산보관관리보고서는 기 가입하고 있는 펀드를 대상으로 발송된다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자자보호 서류 이해', null, null);
