insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (147, 'MULTIPLE_CHOICE', '[{"data": "개인전문투자자에 대한 <보기>의 설명에서 올바른 것을 모두 묶은 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "ㄱ.", "dataType": "text", "data": ["코넥스 등에 상장된 기업에 대해 금액규제 없이 투자할 수 있다", "파생상품투자자가 의무적으로 이수하는 사전교육, 모의투자가 면제된다", "사모펀드에서 규제하는 최저 가입금액에 대한 제한이 없이 가입이 가능하다", "주식에 투자하는 사람은 차액결제제도(CFD)를 이용해서 기존에 활용하던 신용, 미수, 주식담보대출보다 더 큰 레버리지를 사용할 수 있다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["㉠, ㉡", "㉠, ㉢, ㉣", "㉡, ㉢, ㉣", "㉠, ㉡, ㉢, ㉣"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "지문 4가지가 다 맞는 내용임"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자자종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (148, 'MULTIPLE_CHOICE', '[{"data": "개인전문투자자에 대한 설명으로 괄호 안에 들어갈 단어로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["개인전문투자자 자격요건 중 필수요건은 금융투자상품 잔고 (     )이상이다(최근 5년 중 1년 이상 금융투자상품 잔고)"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["1천만원", "3천만원", "5천만원", "1억원"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "지문3 자격요건은 필수요건과 선택요건이 있다. 필수요건은 금융투자상품(주식, 펀드 등)잔고 5천 만원(최근 5년 중 1년 이상 금융투자상품잔고) 이상이다. 금융투자회사별 잔고 합산도 가능하다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자자종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (149, 'MULTIPLE_CHOICE', '[{"data": "개인전문투자자에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인전문투자자가 직접 금융투자협회에 신청해야 한다", "개인전문투자자는 필수요건을 갖추고 선택요건 중 한 가지를 충족하면 된다", "자산요건은 부채를 차감한 순재산이 5억원 이상일 것이다", "소득요건은 본인의 직전연도 소득금액이 1억원 이상이거나 부부가 합산해서 직전연도 소득합계액이 1.5억원 이상일 것이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "지문1 개인전문투자자 신청 서류를 금융투자회사(주로 증권회사)에 제출한다. \n각 금융투자회사는 자체심사를 통해 자격부여 여부를 판단한다. \n지문2 필수요건과 선택요건이 있다. 선택요건은 소득요건, 전문가요건, 자산요건이 있다.\n지문3 자산요건은 순재산 5억원 이상(부부합산 필수, 거주 부동산관련 금액 제외)으로 부채를 차감한 순재산 5억 이상이다.\n지문4 소득요건은 본인의 직전연도 소득금액이 1억 원 이상이거나 부부가 합산해서 직전연도 소득합계액이 1.5억 원 이상이면 충족된다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자자종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (150, 'MULTIPLE_CHOICE', '[{"data": "가치평가방법 등에 대한 투자자의 이해가 어려운 상품으로서, 최대 원금손실 가능성이 일정수준(20%~30%)이상으로 투자자보호가 필요한 주요상품을 무엇이라 하는가", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["저난도 금융투자상품", "고난도 금융투자상품", "고수익 금융투자상품", "중위험 금융투자상품"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "지문2 2019년 11월에 금융감독원이 발표한 ‘고위험 금융상품 투자자 보호 강화를 위한 종합개선방안’에 따르면, 고난도 금융투자상품은 다음과 같이 규정된다. ‘고난도 금융투자상품’은 파생상품 내재 등으로 가치평가방법 등에 대한 투자자의 이해가 어려운 상품으로서, 최대 원금손실 가능성이 일정수준 (20%~30%)이상인 상품이다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '고난도상품 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (151, 'MULTIPLE_CHOICE', '[{"data": "고난도 금융투자상품에 대한 설명으로 괄호 안에 들어갈 단어로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["", "투자자는 숙려기간 내에 별도 청약 승낙 의사표시가 없을 경우 자동적으로 청약이 (  ㉠  )된다는 사실을 알아야 한다. 물론 해당 금융투자회사는 이러한 사항에 대한 통지가 ( ㉡ )화되어 있다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["㉠ 철회  ㉡ 의무", "㉠ 확정  ㉡ 의무", "㉠ 철회  ㉡ 선택", "㉠ 확정  ㉡ 선택"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "지문1  투자자는 숙려기간 내에 별도 청약 승낙 의사표시가 없을 경우 자동적으로 청약이 (철회)된다는 사실을 알아야 한다. 물론 해당 금융투자회사는 이러한 사항에 대한 통지가 (의무화)되어 있다. 그리고 숙려기간 중에는 투자권유를 한 금융투자회사는 청약의 승낙을 권유 종용하는 행위가 금지되어 있어 \n오로지 투자자의 의지로 청약의 승낙 여부를 판단해야 한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '고난도상품 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (152, 'MULTIPLE_CHOICE', '[{"data": "<보기>의 (    ) 안에 들어갈 말로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["투자에 있어서 투자자가 수익을 내기 위해 부담한 위험에 대비해서 어느 정도 수준의 수익을 달성했는지를 나타내는 수익률을 (         )이라고 한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["절대수익률", "상대수익률", "중장기수익률", "위험조정수익률"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "지문1 절대수익률은 위험이나 기간에 대한한 고려없이 산출한 수익률이다.\n지문2 상대수익률은 비교대상이 되는 다른 투자대안과 비교하는 상대적인 개념의 수익률이다.\n지문3 중장기 수익률은 수익률을 측정하는 기간에 대한 고려를 통해 단기(주로 1년 이하)대비 더 긴 기간을 측정한 수익률이다.\n지문4 보기는 위험 조정 수익률에 대한 설명이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '위험조정수익률 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (153, 'MULTIPLE_CHOICE', '[{"data": "포트폴리오 매니저에게는 운용의 목표와 기준이 되는 지표이고, 투자자의 입장에서는 해당펀드의 운용결과가 이 기준대비 얼마나 효율적이었는지 판단하는 기준이 되는 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["위험", "수익률", "벤치마크", "미실현이익"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "지문3 포트폴리오 매니저가 포트폴리오를 운용할 때는 운용의 목표와 기준을 정해서 운용한다. 이때 이 운용의 목표와 기준이 되는 기준지표가 우리가 흔히 이야기하는 벤치마크다. 포트폴리오 매니저에게는 운용의 목표와 기준이 되는 기준지표지만, 투자자의 입장에서는 해당펀드의 운용결과가 이 기준지표 대비 얼마나 효율적이었는지를 판단 하는 기준이 된다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '벤치마크 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (154, 'MULTIPLE_CHOICE', '[{"data": "투자실적 평가에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["벤치마크는 포트폴리오 매니저에게 운용의 목표와 기준이 되는 기준지표다", "투자자의 입장에서는 해당펀드의 운용결과가 이 기준지표 대비 얼마나 효율적이었는지를 판단하는 기준이 된다", "벤치마크는 ‘투자전략을 효과적으로 실행했는지 여부를 판단하는 기준이 되는 독립적인 수익률’로 정의할 수 있다", "펀드를 기준으로 벤치마크는 각 펀드의 투자설명서에 내용이 기재되어 있고, 상황에 따라 유연하게 변경가능하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "지문1 벤치마크는 포트폴리오 매니저에게 운용의 목표와 기준이 되는 기준지표가 맞다. \n지문2 투자자의 입장에서는 해당펀드의 운용결과가 이 기준지표 대비 얼마나 효율적이었는지를 판단하는 기준으로 활용된다.\n지문3 벤치마크는 ‘투자전략을 효과적으로 실행했는지 여부를 판단하는 기준이 되는 독립적인 수익률’로 정의할 수 있다. \n지문4 펀드를 기준으로 벤치마크는 각 펀드의 투자설명서에 내용이 기재되어 있고, 임의로 변경할 수 없다. 이는 투자설명서 정정사항에 해당하여 정당한 절차를 거친 이후에 효력을 갖는다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '벤치마크 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (155, 'MULTIPLE_CHOICE', '[{"data": "벤치마크(기준지표) 중에 섹터/스타일 지수의 성격과 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["대형주지수", "중소형지수", "가치주지수", "종합주가지수"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "벤치마크의 종류인 시장지수와 섹터/스타일지수의 구분에 대한 문제이다.\n지문1 섹터/스타일 지수\n지문2 섹터/스타일 지수\n지문3 섹터/스타일 지수\n지문4 종합주가지수는 대표적인 시장지수의 일종이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '벤치마크 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (156, 'MULTIPLE_CHOICE', '[{"data": "<보기>의 (    ) 안에 들어갈 말로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["투자실적을 평가하는 지표중에 수익률과 위험요소를 동시에 고려하여 성과를 측정하는 지표를 (         ) 라고 한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["수익률", "벤치마크", "위험조정 성과지표", "절대수익 추구 성과지표"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "지문1 단순수익률은 위험요소를 고려하지 않는 수익률 기준이다.\n지문2 벤치마크는 수익률을 비교평가하는 기준으로 위험요소인 변동성을 별도록 고려하지는 않는다.\n지문3 수익률과 위험요소를 동시에 고려하여 성과를 측정하는 지표를 위험조정 성과지표(RAPM : Risk ?Adjuisted Performance)라 한다. 선택하는 수익률과 위험지표와 위험을 조정하는 방식에 따라 단위 위험당 초과수익률과 위험조정 수익률로 나눌 수 있다. \n지문4 절대수익 추구 성과지표라는 용어는 없으나, 투자자가 목표로 하는 절대수익률을 기준으로 할 수는 있다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '벤치마크 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (157, 'MULTIPLE_CHOICE', '[{"data": "상장기업의 주식을 취득해서 보유하는 경우에 발생하는 주주의 권리로서 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주주총회에 참석할 권리", "배당 가능이익에 대해 배당을 받을 권리", "매년 정기적으로 보유하고 있는 주식에 비례해서 이자를 받을 권리", "회사의 경영에 중대한 영향을 미치는 행위에 대한 다수 주주의 결정에 반대해서 주식매수청구권을 행사 할 권리"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "지문1 주총에 참석해서 주요 의사결정에 참여하는 것은 주주의 기본적인 권리에 속한다.\n지문2 주주로서 배당가능이익에 대해 주총의 의결에 따라 배당을 받을권리가 있다.\n지문3 이자를 받을 권리는 채권을 보유한 채권자의 권리이다. 주주의 권리는 아니다.\n지문4 주식매수청구권에 대한 권리도 주주의 권리에 속한다. 주로 소액주주에게 해당한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주주의 권리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (158, 'MULTIPLE_CHOICE', '[{"data": "투자자 A씨가 현재 보유중인 주식을 발행한 상장회사의 주주총회에서 특별결의가 이뤄진다고 전해 들었을 때, 주주로서 행사할 수 있는 권리에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["합병승인의 특별결의에 반대하면 주식매수청구권을 청구할 수 있다", "주식 매수가격은 주주와 당해 법인간의 협의에 의해서 결정하는 것이 원칙이다", "주식매수청구권은 소액주주의 경우 의사표시가 없어도 적용을 해주기 때문에 그냥 기다리면 된다", "주식매수 청구를 받은 당해 법인은 매수의 청구기간이 종료하는 날부터 1월 이내에 당해 주식을 매수하여야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "상법상 주식매수청구권이 인정되는 경우는\n① 회사의 영업양도 등을 함에 있어 필요한 특별결의\n② 합병승인의 특별결의\n③ 분할 합병승인의 특별결의\n④ 주식의 포괄적 교환승인의 특별결의\n⑤ 주식의 포괄적 이전승인의 특별결의의 경우이다. 이런 경우에 반대하는 주주는 회사에 대하여 주식매수청구권을 행사 할 수 있도록 상법에 규정하고 있다. 이런 규정은 다수가 찬성하는 중대한 사안이라도 반대하는 주주에게 보유주식을 회사에게 적정가격으로 팔 수 있는 권리를 부여한 것이다. \n지문3 주식매수청구권은 주주의 일방적인 의사표시에 의하여 성립한다. 의사표시가 없으면 주식매수청구권을 행사하지 않는 것으로 간주된다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주총 특별결의 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (159, 'MULTIPLE_CHOICE', '[{"data": "상장회사의 상장폐지 사유로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["파산신청", "주가급등", "공시의무 위반", "감사인의 감사의견 거절"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "지문1 상장법인이 파산신청을 하면 관리종목으로 지정된다. 그리고 법원의 파산선고 결정이 나면 즉시 상장폐지 된다.\n지문2 단순 주가급등으로는 상장폐지 대상이 될 수 없다.\n지문3 상장법인이 최근 1년간 공시의무 위반 누계 벌점이 15점 이상인 경에는 관리종목으로 지정된다. 관리종목 지정 후 최근 1년간 누계벌점이 15점 이상 추가되었을 경우와 고의, 중과실로 공시의무를 위반했을 경우에는 상장폐지 조건에 해당된다.\n지문4 반기 검토보고서 상 감사의견이 ‘부적정’ 또는 ‘의견거절’인 경우 관리종목으로 지정된다. 2년 연속으로 감사보고서 상 감사의견이 감사범위 제한 한정인 경우에는 상장폐지 절차가 개시된다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '상장폐지 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (160, 'MULTIPLE_CHOICE', '[{"data": "<보기>에서 설명하고 있는 제도로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["주식시장에서 상장폐지가 결정 된 종목은 일반적으로 투자자에게 최종 매매 기회를 주기 위해 일정기간 동안 제한된 범위 내에서 매매를 할 수 있도록 허용한 후에 상장폐지 한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자동매매", "정리매매", "시장가매매", "조건부 지정가매매"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "지문2 정리매매는 상장폐지 되는 주식을 보유하고 있는 투자자들에게 마지막으로 매매를 통해 유동성을 부여하는 시한부 매매라고 볼 수 있다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '상장폐지 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (161, 'MULTIPLE_CHOICE', '[{"data": "투자자 A씨가 투자 시작시점부터 투자관리까지의 상황에 대한 설명으로 사실과 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자자 A씨는 계좌개설 사실과 관련정보를 SMS를 통해 통보받고 확인 하였다", "투자자 A씨는 자신의 거래내역을 매월 전자우편으로 통보받아 확인하고 거래회수를 줄여야겠다고 생각했다", "투자자 A씨가 투자하고 있는 펀드의 자산운용보고서를 받아서 위험부담수준 및 투자목표의 달성여부를 확인했다", "투자자 A씨는 공모펀드 가입시점에 한 장짜리 리플릿으로 설명을 듣고 그 리플릿을 교부받아서 공모펀드에 가입하였다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "지문1 투자하고 있는 계좌의 개설에 대한 현황을 통보받아 정확한 정보를 확보한다.\n지문2 관련 거래내역을 통보받아 확인하고 적절한 조치를 취한다.\n지문3 현재 투자하고 있는 사항에 대해 정확한 정보에 따른 적절한 조치를 위할 수 있는 동기를 갖는다.\n지문4 공모펀드 가입을 할 때에는 리플릿이 아니라 투자설명서로 설명하고 교부하여야 한다. 이를 확인하는 서류에 설명 듣고, 교부받았음을 확인해야 한다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '금융거래 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (162, 'MULTIPLE_CHOICE', '[{"data": "금융투자 및 바람직한 투자자의 행동에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["거래하고 싶은 금융회사가 제도권 금융회사인지를 확인하기 원하면 금융감독원의 ‘금융통합 민원사이트’에 접속해서 확인하면 된다", "주식계좌 거래내역 통보방법을 최초 등록할 때 직접조회로 선택하였다면, 본인이라 해도 거래내역 사본을 요구할 수 없다", "투자자 A씨는 현재의 위험감내수준, 투자목표를 충족하고 있는지를 확인하기 위해 투자실적을 주기적으로 리뷰하면서 투자를 하고 있다", "OO펀드에 투자중인 B씨는 펀드실질투자수익률 조회를 통해 부담하는 비용대비 투자성과를 점검해서 적극적으로 투자의사결정에 반영 중이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "지문1 금융통합 민원사이트 활용방법이다.\n지문2 직접조회를 선택했다고 해도 본인인 경우 거래내역 사본을 요구 할 수 있다.\n지문3 투자거래 및 성과를 주기적으로 활용해서 적절한 투자행위를 하는 상황으로 볼 수있다.\n지문4 펀드실질투자수익률 조회 서비스를 활용해서 투자에 대한 적절한 조치를 취하는 바람직한 상황에 해당한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '금융거래 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (163, 'MULTIPLE_CHOICE', '[{"data": "투자자 A씨의 바람직한 투자행동으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자자 A씨는 금융투자회사에서 추천상품 상품에 대해 이해가 되지 않아 가입을 연기하고 다시 방문하기로 했다", "투자자 A씨는 펀드 가입시 고객 보관용으로 교부받은 서류를 보관하고 있으면서 자산운용보고서가 오면 변화된 내용을 확인해 본다", "투자자 A씨는 담당 PB인 B씨의 도움을 받아 자신의 계좌를 잘 관리하고 있으며, 혼자서도 잘 관리할 수 있는 능력을 갖추고 있다", "투자자 A씨는 금융회사에게 투자상품, 조언 또는 권고사항, 거래에 대해 어떻게 보상을 받는지에 대해 알 방법이 없어서 그냥 넘어가기로 했다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "지문1 상품이 이해되지 않는다면 가입절차를 중단하고 이해한 이후에 가입하는 것이 바람직하다.\n지문2 고객 보관용 서류를 잘 보관하고, 이후 수령하는 서류와 확인하여 투자를 이어가는 것이 바람직하다.\n지문3 관리자의 도움과 투자자 자신의 노력으로 혼자 관리할 수 있는 능력과 자신감을 갖는 것이 중요하다.\n지문4 불이익이 발생할 때 보상받을 수 있는 방법을 문의하고 도움을 받는 것이 중요하고 그냥 넘어가는 것은 바람직한 투자방법이 아니다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '피해방지요령 숙지', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (164, 'MULTIPLE_CHOICE', '[{"data": "금융투자시 발생할 수 있는 투자 위험 및 시장 리스크에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융투자시 투자자가 직면할 수 있는 위험 및 리스크는 매우 다양하며 일반적으로 통제와 관리가 불가능한 시장위험에 대해서는 투자비중을 줄이거나 피하는 것이 유효한 전략이다", "금융투자시 투자자는 다양한 리스크에 직면할 수 있으며 이러한 사전적으로 발생가능한 리스크를 가정하고 이에 대한 관리와 대비책을 통해 리스크 이후 발생할 수 있는 투자기회를 잡는 것이 필요하다", "금융투자시 발생할 수 있는 다양한 위험 중에서도 직접적 시장위험은 시장의 직접적인 움직임에 따라 발생할 수 있는 원금손실위험을 말하며 이는 투자자가 통제하기 어려운 시장의 변화에 따라 발생하게 된다", "금융투자시 발생하는 다양한 위험 중 투자자가 통제할 수 있는 위험을 비체계적 위험이라고 하며 투자자산의 분산을 통해 통제가 가능하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "금융투자시 발생할 수 있는 직접적 시장위험은 투자한 자산 가격의 부정적인 움직임으로 인한 손실위험을 말하며 개별자산위험으로도 불리운다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '위험과 리스크 구별', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (165, 'MULTIPLE_CHOICE', '[{"data": "금융투자 의사결정에 따른 적절한 리스크 관리방법으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자자 A는 연간 5%의 목표수익을 안정적으로 달성하기 위해 최근 3년간 연평균 7% 이상의 수익을 거둔 주식에 투자하기로 하였다", "투자자 A는 연간 5%의 목표수익을 안정적으로 달성하기 위해 연 3%의 이자가 발생하는 채권과 함께 최근 3년간 연평균 7% 이상의 수익을 거둔 주식에 투자하기로 하였다", "최근 금융투자를 시작한 투자자 A는 증권사 직원 B씨가 제시한 유망한 주식 종목 대신 다양한 성장주에 투자하는 펀드상품에 가입하여 리스크 관리를 도모하였다", "최근 주식투자를 시작한 투자자 A는 투자에 따르는 다양한 위험 및 리스크 요인에 대해 사전 점검을 한 이후 직접적 시장위험을 통제하기 위해 10개의 유망주식 포트폴리오를 짜고 원금을 나눠서 투자하기로 하였다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "통제 가능한 직접적 시장위험에 대해서는 분산투자를 통해 리스크를 관리할 수 있다. 집중투자 방식으로는 직접적 시장위험을 통제하기 어렵다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '리스크 관리방법 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (166, 'MULTIPLE_CHOICE', '[{"data": "금융투자시 투자자가 직면할 수 있는 유동성 리스크에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["유동성 리스크는 투자한 금융상품의 구조적 특징으로 인해 발생할 수 있으며 특히 시장 조정시 발생할 가능성이 크다", "투자자가 좋은 투자기회를 발굴한 경우 즉각적인 투자를 통해 수익기회를 창출할 수 있도록 유동성 관리가 필요하다", "유동성 리스크는 실질적으로 투자시 필연적으로 발생하는 리스크로 이에 대한 대비책으로 투자자는 투자한 금액에 해당하는 현금을 보유하는 것이 필요하다", "개인 투자자가 유동성 리스크를 관리하기 위해 사용하는 방법 중 투자시점을 분산하여 투자하는 것은 매우 효과적인 방법이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "유동성 리스크에 대한 관리는 투자한 이후 투자자가 원하는 시점에 현금화 할 수 있는지의 여부와 함께 투자시점을 분산함으로써 투자기회 발굴 시 즉시 투자할 수 있도록 유동성을 관리하는 것을 말한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '리스크 종류 숙지', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (167, 'MULTIPLE_CHOICE', '[{"data": "금융투자시 투자자가 직면할 수 있는 다양한 위험과 리스크에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자자는 금융투자시 통제 불가능한 시장위험과 통제 가능한 리스크 모두에 노출될 수 있으며 리스크는 적극적인 관리를 통해 투자기회로 만들 수 있다", "투자자는 투자대상 자산가격 자체의 변동성에서 발생할 수 있는 직접적 시장위험을 적극적인 분산투자와 자산배분을 통해 통제할 수 있다", "유동성 리스크 관리를 위해 투자자가 활용하는 분산투자는 평균매입단가 인하효과로 이어져 수익률 개선에도 좋은 영향을 미칠 수 있다", "실제 투자에 경험이 많지 않은 개인투자자 입장에서 새로운 금융자산에 투자시에는 다른 투자자들의 동향을 면밀히 분석하고 이들의 투자트랜드에 맞춰 투자하는 전략이 유효하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "투자자는 군중심리 리스크를 경계해야 하며 투자자에게 가장 좋은 군중심리 리스크 관리 방법은 자신만의 투자원칙과 방식을 세우고 철저히 실천하는 것이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '리스크 종류 숙지', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (168, 'MULTIPLE_CHOICE', '[{"data": "다음 투자자 A의 실제 투자사례를 참고할 때, 발생 가능한 리스크를 모두 고른 것으로 올바른 것은? ", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["2021년 1월 투자자 A는 증권사 직원을 찾아가 보유하고 있던 원금 5천만원에 대한 투자상담을 받고 다음과 같은 투자 포트폴리오를 구성하였다. (참고로 원금 5천만원 중 2천만원은 1년 후 전세 보증금을 올려줄 때 사용될 자금이다)", "<투자자 A의 투자내역>", "2천만원 ? 향후 실적 기대감이 높은 IT섹터 B기업 주식", "2천만원 ? 부동산 펀드 (만기 3년)", "1천만원 ?  최근 지인을 통해 소개 받은 주식 종목(합병 기대감으로 최근 주가가 30% 상승중인 종목)"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["직접적 시장위험, 유동성 리스크", "유동성 리스크, 군중심리 리스크", "간접적 시장위험. 유동성 리스크, 군중심리 리스크", "직접적 시장위험. 간접적 시장위험. 유동성 리스크. 군중심리 리스크"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "투자자 A의 투자 포트폴리오를 분석해 보면, 먼저 시장의 움직임에 따른 통제 불가능한 간접적 시장위험 뿐만 아니라, 전체 자산 중 60% 이상을 특정 주식종목에 분산하지 않고 집중투자함에 따른 직접적 시장위험에 함께 노출되어 있다. 또한 전체 자금 중 2천만원이 만기 3년짜리 부동산 펀드에 묶임으로써 1년 후 자금 필요시점에서의 가용 자금이 3천만원으로 줄어 들었고, 3천만원이 모두 주식자산에 집중됨으로써 증시 조정에 따른 수익률 하락 시 유동성 리스크에 직면할 가능성도 있다. 마지막으로 철저한 분석없이 지인의 소개에 따른 투자로 인해서 군중심리 리스크에도 노출되었다고 볼 수 있다.\n"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '리스크 종류 숙지', null, null);
