insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (171, 'MULTIPLE_CHOICE', '[{"data": "자산배분전략과 포트폴리오 운용 프로세스에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자산배분전략과 포트폴리오 운용의 첫번째 과정은 투자목표 설정단계로 이 과정에서는 투자기간 및 세금, 자금의 성격 등에 대한 검토가 이루어진다", "자산배분전략과 포트폴리오 운용의 두번째 과정은 투자실행단계로서 실질적인 자산배분 및 포트폴리오 구성이 이루어진다", "자산배분전략과 포트폴리오 운용단계 중 투자실행단계에서는 투자목적 및 세금에 기반하여 구체적인 투자수단을 찾는 과정이 진행된다", "자산배분전략과 포트폴리오 운용단계 중 사후관리 단계는 시장의 변화를 모니터링 하고 구축된 포트폴리오가 목표한 방향으로 잘 작동하는지 관찰하는 과정이 진행된다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "자산배분전략과 포트폴리오 운용 과정 중 투자목적 및 세금, 자금의 성격 등 투자자의 세부제약조건을 고려하는 단계는 첫번째 단계인 투자목표 설정단계에서 이루어 진다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자산배분 올바른 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (172, 'MULTIPLE_CHOICE', '[{"data": "전략적 자산배분에 대한 설명으로 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["단기적인 시장변화에 적극적으로 대응하는 의사결정 과정이다", "투자자의 투자성향이 중요한 판단기준이 된다", "위험자산과 안전자산 간 투자비중을 결정하는 과정이다", "내재가치에 근거한 자산배분이 이루어진다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "전략적 자산배분은 장기적 투자목표에 따른 의사결정 과정으로 투자자의 위험감내 수준 등 투자성향에 근거한 투자목표가 중요한 판단기준이며, 이러한 투자성향에 따른 위험자산과 안전자산간 비중의 결정과 내재가치에 근거한 자산배분이 이루어지는 과정이다. 반면 전술적 자산배분은 단기적인 시장변화에 적극적으로 대응하는 의사결정 과정으로 시장변화에 대한 투자자의 투자타이밍 포착 등이 중요한 판단 기준이며 시장 변화에 따라 적극적으로 투자자산을 교체하거나 비중을 조절함으로써 역투자전략을 수행하는 과정이다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '전략적 자산배분 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (173, 'MULTIPLE_CHOICE', '[{"data": "전략적 자산배분 프로세스에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["전략적 자산배분의 첫 단계에서 투자자는 투자성향, 투자자금의 성격, 세금 등과 같은 투자제약조건에 기반한 투자목표를 수립해야 한다", "전략적 자산배분을 위해 투자자는 투자대상 자산집단 및 각각의 자산집단별 세분화된 투자가능 자산을 선별하는 것이 필요하다", "전략적 자산배분을 위해 투자자는 미래 기대수익률과 미래 위험에 기반한 포트폴리오의 산출이 필요하다", "전략적 자산배분의 목적은 장기적인 자산배분에 근거하기 때문에 투자자는 기대수익률이나 내재위험 등 단기적 지표보다는 장기적인 내재가치에 기반하여 자산을 선별하는 것이 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "전략적 자산배분은 장기적 투자목표에 따른 의사결정 과정으로 투자자의투자목표에 장기적으로 부합할 수 있는 투자자산 집단 및 세부 자산 포트폴리오를 구성하는 것이 필요하고 이를 위해서는 미래 기대수익과 미래 위험을 추정하여 이에 기반하는 것이 중요하다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '전략적 자산배분 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (174, 'MULTIPLE_CHOICE', '[{"data": "전술적 자산배분 프로세스에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자자산의 미래 기대수익 등 본질가치에 기반한 단기 투자전략을 구성해야 한다", "단기적으로 시장의 변화방향을 예상하여 사전적으로 자산구성을 변화시켜 나가는 매우 적극적인 자산배분 전략이다", "본질적으로 역투자전략이라고 할 수 있으며 이는 자산의 본질가치와 시장가격 간의 비교를 통해 매수-매도를 수행하는 전략을 말한다", "전술적 자산배분전략을 수립할 때 투자자는 자산집단의 가격이 평균반전과정을 따른다는 가정 하에 구성하는 것이 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "전술적 자산배분전략은 단기적으로 시장의 변화 방향을 예상하고 사전적으로 자산구성을 변화시켜 나가는 자산배분전략으로 자산의 본질가치 보다는 마켓타이밍에 따라 적극적으로 자산을 교체하는 전략이다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '전술적 자산배분 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (175, 'MULTIPLE_CHOICE', '[{"data": "개인투자자의 자산배분 실행 과정에 대한 설명으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인투자자는 기본적으로 단기적인 시장에 적극적으로 대응하는 전술적 자산배분 보다는 장기적인 투자목표에 기반한 전략적 자산배분에 보다 쉽게 접근하기 때문에 전략적 자산배분에 편중되는 경향이 있다", "개인투자자의 비합리적 투자는 전략적 자산배분의 결여와 전술적 자산배분에 대한 편중에 그 원인이 있다고 할 수 있다", "개인투자자가 자산배분전략을 실행함에 있어 가장 중요한 것은 위험과 관계없이 높은 기대수익률을 가진 자산을 선별하여 포트폴리오를 구성하는 것이라고 할 수 있다", "개인투자자의 자산배분전략은 기본적인 투자전략의 목표나 기간이 기관투자자와 다를 수 밖에 없으므로 기관투자자의 자산배분전략은 전혀 도움이 되지 않는다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "개인 투자자의 경우 기본적으로 마켓타이밍에 기반하여 투자하는 경향이 많아 전술적 자산배분에 대한 의존이 높은 편이고 이로 인해 비합리적 투자가 이루어지는 경우가 많다. 개인 투자자가 포트폴리오를 구성할 때는 수익률에만 의존하는 것이 아니라 위험 대비 기대수익률을 평가하는 것이 필요하고, 이를 위해서는 벤치마킹할 수 있는 기관 투자자나 전문가의 자산배분전략이나 포트폴리오를 참고하는 것이 좋다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자산배분 올바른 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (176, 'MULTIPLE_CHOICE', '[{"data": "다음과 같은 증권사 시장전망에 동의한 투자자가 취할 수 있는 자산배분전략으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["현재 시장 판단 및 향후 전망 :", "- 2020년 3월부터 시작된 코로나 바이러스의 영향으로 글로벌 경기는 극심한 침체국면에 빠짐", "- 경기 회복을 위해 미국을 비롯한 주요국들은 금리인하 등 통화완화정책과 함께 막대한 유동성 공급을 통한 재정정책을 펼침", "- 이로 인해 글로벌 경기는 점진적인 경기회복 추이를 보이고 있으나 그 부작용으로 인플레이션(물가상승)이 발생할 가능성이 높아짐"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["그동안 시장에 공급된 막대한 유동성으로 인해 향후 인플레이션 가능성이 매우 커진 상황이기 때문에 인플레이션 국면에 유리한 원자재 투자 비중을 늘린다", "완전히 해결되지 않은 코로나 바이러스 국면에서 안전자산이면서도 물가상승에 대한 대비가 가능한 물가연동채권(TIPS)에 대한 투자 비중을 늘린다", "완전히 해결되지 않은 코로나 바이러스 국면과 높은 시장변동성에 대응하기 위해 안전자산인 국채 투자비중을 늘린다", "그동안 시장에 공급된 유동성이 향후 경기 회복을 위한 다양한 사회간접시설 확충 사업 등에 쓰일 것으로 예상하여 인프라펀드에 대한 투자비중을 늘린다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "경기 둔화 국면에서 중앙은행은 금리인하를 통한 통화정책을 사용하고, 아울러 정부는 적극적인 재정정책을 통해 유동성을 공급하여 경기를 회복시키게 된다. 이로 인해 다양한 자산가격은 상승하게 되며 경기도 점진적으로 회복하는 효과가 있으나, 시장에 풀린 유동성으로 인해 인플레이션 문제가 부각되며 시간이 지나면 금리인상이나 유동성 축소와 같은 국면이 전개될 가능성이 높다. 따라서 경기둔화국면이 완화되면서 인플레이션 가능성이 높은 시장상황에서는 금리상승에 취약한 채권이나 주식의 비중을 줄이고 인플레이션 헷지 기능이 있는 원자재, 인프라, 물가연동채권 등의 비중을 확대하는 전략이 필요하다"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자산배분 올바른 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (177, 'MULTIPLE_CHOICE', '[{"data": "경기순환 국면과 그에 따른 전술적 자산배분전략으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["일반적으로 경기둔화국면에서 유동성 공급으로 인해 물가상승이 예상되는 국면에서는 인플레이션 헷지를 위한 자산인 원자재와 회사채에 대한 투자비중 확대가 필요하다", "일반적으로 경기호조가 지속되고 이로 인한 물가상승이 예상되는 국면에서는 인플레이션에 대비할 수 있는 자산인 원자재와 주식 등 위험자산에 대한 투자비중 확대가 필요하다", "일반적으로 경기둔화가 심화되고 물가가 하락하는 국면에서는 안전자산 성격의 국고채 및 현금에 대한 투자비중 확대가 필요하다", "일반적으로 경기호조가 지속되는 동시에 물가가 하락하는 국면에서는 위험자산 성격의 주식이나 회사채에 대한 투자비중 확대가 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "일반적으로 경기둔화국면에서 유동성 공급으로 인해 물가상승이 예상되는 국면에서는 인플레이션 헷지를 위한 자산인 원자재나 인프라에 대한 투자 비중을 확대해야 하며, 향후 물가상승으로 인한 부작용을 억제하기 위해 금리인상 등의 정책이 예상되기 때문에 채권 등의 자산비중을 줄이는 것이 필요하다. 다만 채권 중에서도 인프레이션 헷지 기능이 있는 물가연동채권(TIPS)의 경우는 예외이다.  "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '전술적 자산배분 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (178, 'MULTIPLE_CHOICE', '[{"data": "개인투자자가 주식 포트폴리오 운용을 구성하는 목적 및 방법에 관한 설명으로 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인 투자자가 주식을 활용하여 포트폴리오를 구성하는 이유는 주식이 가진 높은 내재 변동성과 이에 따른 리스크를 줄이는데 목적이 있다", "개인 투자자가 주식을 활용하여 포트폴리오를 구성할 때 투자자는 단기적인 시장의 움직임보다는 장기적인 관점에서 구성하고 포트폴리오의 변경은 최소화하는 것이 필요하다", "개인 투자자가 주식을 활용하여 포트폴리오를 구성하는 경우 투자자는 시장상황이나 대내외 변수와 같은 거시적인 요인들이 아닌 투자 대상 개별주식의 내재 가치와 시장가격에 의존하는 전략이 필요하다", "개인 투자자가 주식을 활용하여 포트폴리오를 구성할 때 가장 중요한 것은 리스크 감소 보다는 수익 극대화에 있다고 할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "주식은 대표적인 위험자산으로 높은 수익을 기대할 수 있는 대신 단기적으로 변동성이 매우 크고 가격을 예측하기 어렵다는 특징이 있다. 따라서 투자자는 주식 포트폴리오를 구성할 때 리스크 관리에 최우선 순위를 두고 단기적으로 시장의 변화에 적극적으로 대응하는 것이 필요하며, 기대 수익률 뿐만 아니라 기업이 가진 내재가치와 함께 다양한 대내외 변수들을 함께 고려하는 것이 필요하다. 결국 개인 투자자가 주식을 활용하여 포트폴리오를 구성하는 이유는 주식이 가진 고유의 내재 변동성과 개별주식 투자에 따른 리스크를 줄이는데 목적이 있다"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (179, 'MULTIPLE_CHOICE', '[{"data": "주식 포트폴리오를 구성하기 위해 투자자가 수행해야 하는 사전적 분석에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식 포트폴리오를 구성하기 위해 사전적으로 개별주식에 대한 분석이 필요하며, 그 분석 방법은 크게 기본적 분석과 기술적 분석으로 구분하고 있다", "기본적 분석은 주식의 본질가치와 시장가격 간에 괴리가 발생할 수 있다는 것을 전제로 주식의 가치를 분석하는 방법을 말한다", "기술적 분석은 시장 참여자의 패턴을 분석하는 방법으로 과거 주식가격 움직임의 양상이 미래에도 반복된다는 가정에 따라 가치를 분석하는 방법을 말한다", "기본적, 기술적 분석 등 다양한 가치분석을 통해 지속적으로 초과수익을 얻을 수 있으므로 주식 포트폴리오 구성시 최우선적으로 고려해야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "일반적으로 개인 투자자의 입장에서 주식분석을 통해 지속적으로 초과수익을 얻는 다는 것은 매우 어려운 일이며, 이는 주식 고유의 높은 변동성과 함께 주식의 가격을 예측한다는 것이 현실적으로 불가능하기 때문이다. 따라서 투자자는 주식포트폴리오를 구성할 때 이러한 기본적, 기술적 분석을 보조적인 활용수단으로 가져가는 것이 필요하다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (180, 'MULTIPLE_CHOICE', '[{"data": "개인투자자의 주식 포트폴리오 구성 프로세스에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["공격적 투자성향을 가진 투자자라면 성장주에 대한 투자비중이 높은 주식 포트폴리오를 구축하는 것이 적합하다", "안정적 투자성향을 가진 투자자라면 가치주나 배당주에 대한 투자비중이 높은 주식 포트폴리오를 구축하는 것이 적합하다", "공격적 투자성향을 가진 투자자라면 베타전략을 추종하는 주식 포트폴리오를 구축하는 것이 적합하다", "안정적 투자성향을 가진 투자자라면 시장수익률을 추종하는 주식 포트폴리오를 구축하는 것이 적합하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "베타전략은 주가지수 혹은 특정지수를 추종하여 수익을 내고자 하는 운용방식으로 안정적 투자성향을 가지고 있는 투자자가 주식포트폴리오 구축시 활용하기에 적합한 전략인 반면에 알파전략은 벤치마크지수 대비 적극적으로 초과수익을 내고자 하는 운용방식으로 공격적 투자성향을 가지고 있는 투자자가 주식포트폴리오 구축시 활용하기에 적합한 전략이다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (181, 'MULTIPLE_CHOICE', '[{"data": "투자자가 다양한 유형의 주식에 적절하게 자산배분을 함으로써 보다 효율적인 주식 포트폴리오를 구성하도록 하는 스마트베타전략에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["로우볼 전략은 주가변동성이 낮은 주식에 집중 투자하여 수익률을 높이고자 하는 전략이다", "밸류 전략은 기업가치 대비 주가가 낮게 형성되어 있는 기업에 집중 투자하여 수익률을 높이고하 하는 전략이다", "퀄리티 전략은 재무적으로 우량한 기업에 집중 투자하여 수익률을 높이고자 하는 전략이다", "모멘텀 전략은 주가가 많이 오른 주식은 팔고, 주가가 많이 떨어진 주식을 집중투자하여 수익률을 높이고자 하는 전략이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "모멘텀 전략은 주가상승세가 강하게 나타나는 주식에 집중 투자하여 수익률을 높이고자 하는 전략을 말한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (182, 'MULTIPLE_CHOICE', '[{"data": "개인투자자의 주식 포트폴리오 전략에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["적극적 포트폴리오 관리전략은 시장지수 대비 높은 성과를 내기 위해 다양한 유형의 주식에 적극적으로 최대한 분산하여 포트폴리오를 구축하는 전략을 말한다", "적극적 포트폴리오 관리전략은 특정 트랜드나 테마를 좇아 포트폴리오를 구성하는 경향이 많아 포트폴리오의 변동성이 높아질 수 있다", "소극적 포트폴리오 관리전략은 보다 안정적인 주식 포트폴리오를 유지하고자 하는 투자자에게 적합한 전략으로 시장지수를 추종하는 포트폴리오를 구축하고 이를 추종하는 방식이다", "소극적 포트폴리오 관리전략은 주식시장의 특정 지수를 초과하는 수익을 내는 것이 목적이 아니라 주식시장 자체의 성장에 따른 수익을 추종하는 전략이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "적극적 포트폴리오 관리전략은 투자하고자 하는 시장지수를 선정하고 지수의 수익률을 상회하는 성과를 목표로 포트폴리오를 관리하게 되며, 따라서 특정 섹터나 종목에 대한 자산배분이나 투자비중 유지 보다는 시장의 트랜드나 테마를 좇아 포트폴리오를 구성하여 투자하는 전략이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (183, 'MULTIPLE_CHOICE', '[{"data": "핵심-위성 포트폴리오 관리전략에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["소극적 포트폴리오 관리전략과 적극적 포트폴리오 관리전략의 장점을 섞어서 투자효과를 높이는 목적으로 사용한다", "적극적 포트폴리오 관리전략의 비중은 70~80%, 소극적 포트폴리오 관리전략의 비중을 20~30% 의 비중으로 섞어서 수익률을 적극적으로 개선시키는 전략이다", "핵심-위성 포트폴리오 관리전략은 변동성을 적절한 수준에서 관리하면서도 시장지수보다 높은 수익률을 내고자 하는 목적으로 많이 사용된다", "투자자는 핵심-위성 포트폴리오 관리전략을 수행함에 있어 초기 설정한 투자 비중을 꾸준히 유지하는 것이 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "핵심-위성 포트폴리오 관리전략은 시장지수를 안정적으로 추종할 수 있는 대표종목으로 전체 포트폴리오 중 70~80%를 구성하고, 나머지 20~30%의 비중은 시장의 트렌드를 추종하여 단기적으로 시장을 주도하는 테마 섹터나 종목으로 포트폴리오를 관리하는 방법이다. 즉 소극적 포트폴리오 관리전략의 비중을 70~80%로, 적극적 포트폴리오 관리전략의 비중을 20~30%로 유지하는 전략이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (184, 'MULTIPLE_CHOICE', '[{"data": "다음과 같은 증권사 시장전망에 동의한 투자자가 핵심-위성 포트폴리오 관리전략에 따라 구성해 볼 수 있는 포트폴리오로 가장 적합한 것은? (단, 투자자는 안정적 투자성향으로 가정)", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["주식시장 전망 :", "코로나 발생 이후 글로벌 경기침체가 심화되었고 이에 대한 각국 중앙은행 및 행정부의 금리인하 및 양적완화 정책이 실시됨. 이에 따른 대규모 유동성 공급은 글로벌 경제를 회복국면으로 이끌고 있고, 이에 따라 기업들의 실적도 반등 추세를 이어감. 특히 코로나로 인한 재택근무 확대 및 비대면 비즈니스 활동의 증가로 인해 반도체 및 언텍트 테마 관련업종의 실적 향상이 두드러질 것으로 기대됨. 다만 코로나 이슈가 여전히 진행 중이고, 변이 바이러스의 등장 등 시장에 변수가 여전히 많아 신중한 투자가 필요함"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["시장 대표지수 성과추종형 인덱스펀드 투자 70% + 반도체업종 관련주식 투자 30%", "반도체업종 관련주식투자 70% + 시장 대표지수 성과추종형 인덱스펀드 투자 30%", "언텍트테마 관련주식 투자 70% + 반도체업종 관련주식 투자 30%", "반도체업종 관련주식 투자 70% + 언텍트테마 관련주식 투자 30%"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "핵심-위성 포트폴리오 관리전략은 시장지수를 안정적으로 추종할 수 있는 대표종목으로 전체 포트폴리오 중 70~80%를 구성하고, 나머지 20~30%의 비중은 시장의 트렌드를 추종하여 단기적으로 시장을 주도하는 테마 섹터나 종목으로 포트폴리오를 관리하는 방법이다. 투자자 A는 기존 주식투자 경험이 없는 투자자로서 전체 자산 중 일부 자산을 주식 포트폴리오에 투자하는 만큼 소극적 포트폴리오 관리전략의 비중을 70~80%로, 적극적 포트폴리오 관리전략의 비중을 20~30%로 유지하는 핵심-위성 전략이 바람직하다. 따라서 시장 대표지수를 추종하는 인덱스펀드에 대한 투자를 70% 수준으로 하고, 향후 시장 전망이 우호적인 반도체 또는 언텍트 관련 종목에 대한 투자를 30% 수준으로 하는 것이 바람직하다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (185, 'MULTIPLE_CHOICE', '[{"data": "개인투자자가 채권 포트폴리오를 구성하는 목적 및 방법으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["채권은 대표적인 안전자산의 성격을 갖추고 있으므로 채권 포트폴리오를 통해 전체 포트폴리오의 변동성을 낮출 수 있다", "채권은 유동성이 풍부하고 종류가 다양해서 개인 투자자가 채권 실물을 통해 포트폴리오를 구성하기에 매우 용이한 자산이다", "채권은 발행 주체 및 구조에 따라 매우 다양한 종류가 발행되고 있기 때문에 포트폴리오의 효용성이 높다", "개인 투자자가 채권을 통해 포트폴리오를 구성할 때 여러가지 방법이 있을 수 있으나 그중 간접투자상품을 활용하는 것이 효율적이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "채권은 유동성이 풍부한 자산이긴 하지만 다른 자산 대비 시장에서 거래되는 규모가 크고, 주식과 같이 대중화된 거래소를 통한 거래보다는 기관들 간에 1:1로 장외시장에서 주로 거래가 이루어지는 형식이어서 개인 투자자가 직접적으로 실물에 투자하기에 어려움이 적지 않은 자산이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (186, 'MULTIPLE_CHOICE', '[{"data": "개인투자자가 채권 포트폴리오를 구성할 때 활용할 수 있는 다양한 채권과 그 특징에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["한국 국채 ? 이자는 낮은 반면 안정적 ? 포트폴리오 변동성 축소", "브라질 국채 ? 높은 이자율과 변동성 ? 포트폴리오 수익률 제고", "하이일드 채권 ? 높은 이자율과 변동성 ? 포트폴리오 수익률 제고", "우량등급 회사채 ? 국채 대비 높은 이자율과 낮은 변동성 ? 포트폴리오 효율성 제고"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "우량등급 회사채는 일반적으로 국채 대비 높은 이자율을 얻을 수 있으나 변동성은 국채 대비 높은 경우가 대부분이기 때문에 포트폴리오 구성 시 포트폴리오 효율성을 높일 수 있지만 리스크 관리가 필요하다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (187, 'MULTIPLE_CHOICE', '[{"data": "채권 포트폴리오를 구성하기 위해 사전적으로 검토해야 할 사항과 그 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["신용등급 ? 신용등급이 높은 채권은 금리가 낮은 반면, 신용등급이 낮은 채권은 금리가 높다", "만기 ? 만기가 긴 채권일수록 높은 금리를 제시하면서 동시에 위험도가 낮아지는 효과가 있다", "듀레이션 ? 일반적으로 듀레이션이 긴 채권은 시장금리에 대한 민감도가 높기 때문에 금리 하락시기에는 듀레이션이 긴 채권의 비중을 높여야 유리하다", "발행주체 ? 일반적으로 채권의 발행주체가 국가나 정부기관일수록 금리는 낮다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "채권의 만기가 길어 질수록 시장 금리 변화에 노출될 가능성이 높아지기 때문에 위험도가 높아지게 된다. 따라서 장기 채권일수록 제시금리는 높아진다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (188, 'MULTIPLE_CHOICE', '[{"data": "개인투자자가 채권 포트폴리오를 구성하기 위해 인지해야 할 채권의 특징과 그에 따른 포트폴리오 구성방법으로 가장 거리가 먼 것은? ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["채권은 다른 자산 대비 거래 단위가 크고, 장외시장에서 거래되는 비중이 높은 편이기 때문에 개인 투자자는 펀드나 ETF 등 간접투자상품을 통해 포트폴리오를 구성하는 것이 효율적이다", "채권은 발행 주체나 조건에 따라 매우 다양한 금리와 수익률 구조를 가지고 있기 때문에 포트폴리오를 구성하기에 효율적인 자산이다", "채권은 대표적인 안전자산의 성격을 갖고 있기 때문에 포트폴리오의 안정성을 높이는 특징이 있으나 수익률 측면에서는 다른 자산대비 매력도가 떨어지는 단점이 있다", "채권은 종류에 따라 매우 다양한 수익구조와 변동성이 있을 수있으므로 다양한 종류의 채권에 분산투자함으로써 자산배분 효과를 높일 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "채권은 발행 주체나 조건에 따라 매우 다양한 수익구조를 가지고 있으며 신용등급이 낮은 하이일드 채권이나 이머징국가에서 발행된 국채 등은 주식이나 부동산 등 다른 자산과 비교해서도 높은 금리와 매력적인 수익률을 보유하고 있다고 할 수 있다. 다만 이러한 채권은 상대적으로 높은 변동성을 가지고 있기 때문에 안전자산으로 분류하기는 어렵다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (189, 'MULTIPLE_CHOICE', '[{"data": "채권 포트폴리오의 수익률 및 변동성에 가장 큰 영향을 미치는 금리의 움직임과 이에 따른 포트폴리오 구성에 대한 설명으로 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["향후 시장금리 상승으로 인해 채권가격이 하락할 것으로 예상되는 시점에는 듀레이션이 짧은 채권으로 포트폴리오를 구성하는 것이 필요하다", "향후 시장금리 하락으로 인해 채권가격이 하락할 것으로 예상되는 시점에는 듀레이션이 긴 채권으로 포트폴리오를 구성하는 것이 필요하다", "향후 시장금리 상승으로 인해 채권가격이 상승할 것으로 예상되는 시점에는 듀레이션이 긴 채권으로 포트폴리오를 구성하는 것이 필요하다", "향후 시장금리 하락으로 인해 채권가격이 상승할 것으로 예상되는 시점에는 듀레이션이 짧은 채권으로 포트폴리오를 구성하는 것이 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "채권 포트폴리오는 시장금리와 매우 높은 상관관계를 갖고 있기 때문에 투자자는 시장금리의 움직임에 따라 채권 포트폴리오의 듀레이션을 조절해야 한다. 일반적으로 듀레이션이 긴 채권은 금리민감도가 높고 변동성이 큰 반면 듀레이션이 짧은 채권은 상대적으로 변동성이 낮다. 따라서 투자자는 시장금리 상승으로 인해 채권가격이 하락하는 구간에서는 듀레이션이 짧은 채권으로 포트폴리오를 구성하고, 반대로 시장금리 하락으로 인해 채권가격이 상승하는 구간에서는 듀레이션이 긴 채권 포트폴리오로 구성해야 한다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (190, 'MULTIPLE_CHOICE', '[{"data": "다음과 같은 증권사 시장전망에 동의한 투자자가 구성해 볼 수 있는 채권 포트폴리오의 내용으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["현재 시장 판단 및 향후 전망 :", "2020년 3월부터 시작된 코로나 바이러스의 영향으로 글로벌 경기는 극심한 침체국면에 빠짐. 경기 회복을 위해 미국을 비롯한 주요국들은 금리인하 등 통화완화정책과 함께 막대한 유동성 공급을 통한 재정정책을 펼침.", "이로 인해 글로벌 경기는 점진적인 경기회복 추이를 보이고 있으나, 그 부작용으로 인플레이션(물가상승)이 발생할 가능성이 높아진 상황임"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["인플레이션이 예상되는 국면에서 향후 중앙은행의 금리인상이 예상되기 때문에 듀레이션이 짧은 채권의 비중을 늘린다", "금리의 변동성이 높아질 가능성이 높아지는 상황이므로 듀레이션이 짧은 채권의 비중을 늘린다", "글로벌 경기가 점진적인 회복추이를 보일 가능성이 높기 때문에 듀레이션이 짧은 고금리 채권의 비중을 늘린다", "글로벌 경기가 점진적인 회복추이를 보일 가능성이 높기 때문에 만기가 긴 채권의 비중을 늘려서 수익률을 제고한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "경기침체구간에서는 경기 회복을 위해 금리인하 등의 통화정책과 함께 유동성공급 등 적극적인 재정정책이 나오게 된다. 이에 따라 경기는 점차 회복세를 보이지만 시중에 풀린 유동성에 따른 부작용으로 시장은 인플레이션 리스크에 직면하게 되고, 이에 대한 억제책으로 금리인상과 함께 유동성 축소가 진행되게 된다. 문제에서 제시된 시장상황은 경기회복과 함께 향후 인플레이션 억제를 위한 금리인상 및 유동성 축소가 예상되는 국면이므로 이러한 경우 투자자는 만기와 듀레이션이 짧은 채권의 비중을 늘려 변동성을 축소해야 하고, 경기 회복에 따른 수혜를 받을 수 있는 고금리 채권의 비중을 늘리는 방향으로 채권 포트폴리오 전략을 가져가는 것이 필요하다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (191, 'MULTIPLE_CHOICE', '[{"data": "글로벌 경기의 흐름과 채권 가격의 영향에 따른 채권 포트폴리오의 구성 및 관리방향으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["글로벌 시장에 위기 상황이 발생하게 되면 시장에서는 안전자산에 대한 선호현상이 높아지고, 이에 따라 선진국 국채의 가격이 상승할 가능성이 높으므로 포트폴리오에서 비중을 높여야 한다", "글로벌 경기가 호조세를 지속하며 안정세가 지속되는 구간에서는 안전자산 성격의 국채 보다는 금리가 높은 하이일드 채권의 비중을 높이는 것이 필요하다", "글로벌 경기가 호조세를 보이며 물가상승이 출현하는 구간에서는 금리인하 가능성이 높으므로 듀레이션이 긴 채권의 비중을 높이는 것이 필요하다", "글로벌 경기가 침체국면을 지속하는 구간에서는 금리인하 가능성이 높으므로 듀레이션이 긴 채권의 비중을 높이는 것이 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "다양한 채권의 종류 중에서 특히 안전자산으로서의 성격이 강한 선진국 국채나 우량회사채 등은 글로벌 시장에 위기상황이 발생하거나, 글로벌 경기침체가 지속되는 구간에서 수요가 많아지며 가격이 상승하는 특징이 있기 때문에 이러한 구간에서 비중을 늘리는 전략이 필요하다. 반면 글로벌 경기가 호조세를 지속하는 구간에서는 상대적으로 금리가 높은 위험자산 성격의 하이일드 채권의 비중을 높이는 전략이 필요하다. 아울러 경기가 침체국면을 지속하는 구간에서는 향후 금리인하 등의 정책이 나올 가능성이 높기 때문에 이에 따른 롱듀레이션 채권 비중을 늘리는 전략이 필요한 반면, 글로벌 경기의 호조세가 지속되며 인플레이션 우려가 가시화 되는 구간에서는 향후 금리 상승 가능성이 높기 때문에 듀레이션이 짧은 채권의 비중을 늘리는 전략이 필요하다"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (192, 'MULTIPLE_CHOICE', '[{"data": "개인투자자가 주식과 채권을 활용하여 포트폴리오를 구성하는 전략에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식과 채권은 전통적으로 낮은 상관관계를 가지고 있는 자산이기 때문에 두 자산을 적절한 비중으로 혼합하여 투자하면 포트폴리오의 분산투자효과를 높일 수 있다", "상관관계가 낮은 주식과 채권에 분산하여 투자하면 경제환경변수에 따른 간접적 시장리스크를 줄일 수 있다", "상관관계가 낮은 주식과 채권에 분산하여 투자하면 자산이 보유한 고유의 리스크인 직접적 시장리스크를 줄일 수 있다", "너무 낮은 금리의 채권으로 주식이 보유한 변동성을 상쇄시키기 힘든 국면에서는 채권보다는 고배당주로 관심을 돌려는 것도 좋은 전략이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "주식과 채권은 대표적인 위험자산과 안전자산으로 두 자산간 상관관계는 마이너스를 보이거나 매우 낮다. 따라서 두 자산간 분산투자를 통해 투자자는 포트폴리오의 변동성을 낮추고 분산투자 효과를 높일 수 있다. 두 자산간 분산투자를 통해 투자자는 포트폴리오가 대외적인 시장변수, 즉 간접적 시장리스크를 감소시키는 효과를 얻을 수 있다. 그러나 이러한 주식과 채권에 대한 분산투자가 각각의 자산이 보유하고 있는 직접적 시장리스크를 줄이는 데 영향을 미칠 수는 없다. 주식과 채권 각각의 직접적 시장리스크를 줄이기 위해서는 주식과 채권자산 각각 분산투자를 통해 해결할 수 있다.  "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (193, 'MULTIPLE_CHOICE', '[{"data": "파생상품을 활용하여 포트폴리오를 구성하는 목적 및 활용방법으로 가장 거리가 먼 것은? ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인 투자자는 선물이나 옵션과 같은 파생상품의 증거금 제도를 활용하여 레버리지 투자효과를 높일 수 있다", "개인 투자자는 선물이나 옵션과 같은 파생상품을 활용하여 투자자산 가격하락 위험을 헷지할 수 있다", "개인 투자자는 파생상품을 활용한 레버리지 투자를 통해 투자자산의 변동성을 줄이고 투자위험을 낮출 수 있다", "개인 투자자는 다양한 파생상품 중에서도 특히 장내에서 거래되는 선물이나 옵션과 함께 이들 상품을 기초로 발행되는 파생결합상품의 활용하기에 적합하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "개인 투자자가 파생상품을 활용하는 목적은 레버리지 활용, 투자자산가격의 하락위험 헷지, 차익거래 등이 대표적이다. 파생상품은 개인 투자자들이 주로 활용하는 장내파생상품과 기관들간 계약에 의한 장외파생상품으로 구분된다. 개인 투자자들은 장내거래소를 통한 선물, 옵션 그리고 이러한 파생상품을 기초자산으로 발행된 파생결합상품을 주로 활용할 수 있다. 파생상품은 증거금제도를 활용한 레버리지 투자가 가능하다는 특징이 있으며 이는 투자수익을 높일 수 있다는 장점이 있는 반면 원금을 초과하여 손실이 발생할 수 있기 때문에 변동성을 키우고 투자위험이 높아질 수 있다는 단점이 있다.   "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '파생상품 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (194, 'MULTIPLE_CHOICE', '[{"data": "파생상품 포트폴리오를 구성하는 경우 발생할 수 있는 리스크로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["파생상품을 활용하여 포트폴리오를 구성할 때 과도한 레버리지 투자로 인해 투자원금을 초과하여 손실이 발생할 수 있다", "파생상품은 다른 금융상품 대비 유동성이 낮고 파생상품을 판매하는 금융투자회사의 헷지거래는 이러한 낮은 유동성을 더욱 유발시킬 가능성이 있다", "파생상품은 주로 다양한 수익구조를 만들기 위해 선물이나 옵션을 기초로 금융공학기법을 활용하는 경우가 많아 상품구조가 복잡하다는 단점이 있다", "파생상품은 역외거래가 거의 없고, 대부분 역내거래 형태로 이뤄지고 있어 상품성이 제한될 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "파생상품을 거래하는 거래소는 국내외 매우 다양하며, 최근에는 역외 파생상품을 기초로 하여 다양한 구조의 상품이 만들어 지고 거래되고 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '파생상품 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (195, 'MULTIPLE_CHOICE', '[{"data": "파생상품을 활용하여 포트폴리오의 위험을 헷지하는 전략에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자자가 주식선물을 활용하는 경우 편입할 주식 포트폴리오에 대해 매수포지션을 취할 수 있기 때문에 하락위험을 헷지할 수 있다", "투자자는 KRX에 상장된 지수, 통화, 금리, 주식 등 다양한 자산을 기초로 하는 파생상품을 활용하여 포트폴리오의 가격하락위험을 헷지할 수 있다", "금리형 상품에 투자하는 포트폴리오의 경우 현물의 방향과 반대방향의 선물 포지션을 취함으로써 금리 변동시 포트폴리오의 손실을 헷지할 수 있다", "옵션을 활용하는 경우 풋옵션을 매입하여 미래 투자자산 가격 하락으로 인한 손실을 방어하는 것이 가능하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "주식선물의 경우 현재 편입하고 있는 주식 포트폴리오에 대해 매도 포지션을 취함으로써 주식포트폴리오의 현물가격이 하락하더라도 선물매도에 따른 차익으로 상쇄하는 헷지효과를 가져올 수 있다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '파생상품 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (196, 'MULTIPLE_CHOICE', '[{"data": "파생상품을 활용한 포트폴리오 운용전략에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자자가 선물을 활용하는 경우에는 포트폴리오의 위험을 상쇄시킬수는 있으나 추가적인 수익창출의 기회는 없다", "투자자가 옵션을 활용하는 경우에는 포트폴리오의 위험을 헷지하는 것 뿐만 아니라 포트폴리오 수익을 개선시키는 것도 가능하다", "투자자는 보유한 주식을 매개로 콜옵션을 발행하여 상대방에게 매도함으로써 옵션 프리미엄을 통한 추가수익을 얻을 수 있다", "투자자는 보유한 주식을 매개로 풋옵션을 발행하여 상대방에게 매도함으로써 옵션 프리미엄을 통한 추가수익을 얻을 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "투자자는 보유한 주식을 매개로 콜옵션을 발행하여 거래 상대방에게 매도하는 대신 옵션프리미엄을 수취할 수 있으며, 이러한 전략을 커버드콜 전략이라고 한다. 이 전략에서의 핵심은 콜옵션 매도시 얻게 되는 프리미엄 수입이며 이는 단지 주식을 보유하는 것에서 옵션을 활용함으로써 얻게 되는 추가수익이다. 반면에 풋옵션을 매도함으로써 옵션프리미엄을 수취하는 전략을 커버드풋 전략이라고 하며 이 전략은 현재 현물을 보유한 것이 아니라, 매도 포지션을 취하고 있거나, 미래에 현물을 매입할 예정인 경우 사용하는 전략이다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '파생상품 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (197, 'MULTIPLE_CHOICE', '[{"data": "파생결합상품을 활용한 포트폴리오 운용전략에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["선물, 옵션과 같은 파생상품을 활용하여 특정 수익구조로 만든 금융상품으로 다양한 수익구조를 제공하는 장점은 있으나 구조가 복잡하여 개인투자자가 포트폴리오에 활용하기에는 어려움이 많다", "주식이나 환율, 금리와 같은 기초자산과 선물, 옵션 등의 파생상품을 결합하여 기초자산의 움직임과 별도의 수익구조를 제공함으로써 포트폴리오 효과를 높일 수 있다", "포트폴리오에 활용할 경우 시장의 움직임이 급격한 변동성을 보이는 경우 손실폭이 커질 수 있다는 단점이 있다", "투자되는 기초자산가격의 움직임을 예측하기 어려운 국면에 기초자산 가격의 움직임과 상관관계가 낮게 정해진 구조의 수익구조를 제공할 수 있다는 장점이 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "파생결합상품은 주식이나 환율, 금리와 같은 기초자산과 선물, 옵션 등의파생상품을 결합하여 기초자산의 움직임과 별도의 수익구조를 제공하는 상품으로 포트폴리오 효과를 높일 수 있다는 장점이 있다. 아울러 개인 투자자가 투자하기 포트폴리오에 편입하기 어려운 파생상품을 활용하여 구조화된 수익구조를 제공하는 금융투자상품으로 보다 용이한 투자가 가능하다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '파생결합상품 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (198, 'MULTIPLE_CHOICE', '[{"data": "주가연계 파생결합상품의 종류와 그에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ELD(주가연계예금) ? 주가지수의 변화와 연계되어 지급이자율이 달라지는 예금상품으로 원금이 보장되고 예금자보호를 받는다", "ELF(주가연계펀드) ? 주가지수의 변화와 연계된 구조의 ELS(주가연계증권)에 투자하는 펀드로 투자성과는 편입한 ELS의 성과에 연동된다", "ELT(주가연계신탁) ? 주가지수의 변화와 연계된 구조의 ELS에 투자하는 특정금전신탁으로 증권회사나 은행 등 신탁업자가 취급하는 상품이다", "ELS(주가연계증권) ? 주가지수의 변화와 연계된 구조의 증권형 상품으로 원금이 보장되지는 않지만 예금자보호를 받는다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "ELS(주가연계증권), ELF(주가연계펀드), ELF(주가연계신탁)와 같은 상품은 투자결과에 따라 투자자의 수익이 달라지는 상품으로 원금이 보장되지 않고, 예금자보호에서 제외되는 상품이다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '파생결합상품 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (199, 'MULTIPLE_CHOICE', '[{"data": "투자자 A는 현재 보유하고 있는 주식 포트폴리오의 가격이 상승할 것으로 예상하고 있지만, 향후 1개월간 5% 이상의 수익은 거두기 어려울 것으로 판단하고 있다. 이러한 시나리오에 입각해서 투자자 A가 파생상품을 활용하여 해당 포트폴리오 운용을 최적화 할 수 있는 전략은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["보유 중인 주식포트폴리오의 가치에 해당하는 만큼 주식선물을 매도하는 전략", "보유 중인 주식을 매개로 한 콜옵션을 매도하여 프리미엄을 수취하는 전략", "보유 중인 주식포트폴리오의 가치에 해당하는 만큼 주식선물을 매수하는 전략", "보유 중인 주식을 매개로 한 풋옵션을 매수하여 프리미엄을 수취하는 전략"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "투자자 A는 현재 보유중인 주식포트폴리오의 가격이 제한된 범위 내에서 상승할 것으로 예상하고 있다. 따라서 이 경우 현재 보유 중인 주식 포트폴리오는 그대로 유지한 상황에서 옵션거래를 통해 추가적인 수익기회를 얻는 전략이 유효하며 이때 사용하는 전략이 보유 주식 포트폴리오를 매개로 한 콜옵션을 거래상대방에게 매도하고 그 대가로 옵션 프리미엄을 수취하는 전략이다. 이러한 전략을 커버드콜 전략이라고 한다. 커버드 콜 전략은 주식포트폴리오의 가격이 향후 상승할 것으로 예상되지만 제한된 범위내에서 상승할 것으로 판단되는 상황에서 유효한 전략이며, 실제 옵션 만기시점에 예상한 것보다 가격 상승폭이 큰 경우에는 옵션의 조건에 따라 수익이 제한될 수 있는 단점이 있다.  "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '파생결합상품 활용한 헤지 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (200, 'MULTIPLE_CHOICE', '[{"data": "투자자가 펀드를 활용하여 포트폴리오를 구성하는 목적 및 장단점으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["펀드는 전문가에 의한 운용이 이루어지기 때문에 다양한 자료와 시장데이터에 근거한 체계적인 포트폴리오 운용이 가능하다", "펀드는 투자자 본인이 직접 포트폴리오를 운용하는 방식 대비 다수의 투자자로부터 자금을 받아 집합투자형태로 운용하기 때문에 비용이 절감되는 효과가 크다", "펀드는 다수의 투자자로부터 자금을 받아 집합투자형태로 운용되기 때문에 적은 금액으로도 포트폴리오 분산투자효과가 높다", "펀드를 활용하여 포트폴리오를 운용하는 경우 원금보장이 되지 않을 뿐만 아니라 예금자보호법의 대상도 되지 않으므로 주의해야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "펀드는 전문가에 의한 체계적인 운용과 집합투자 운용방식에 따른 분산투자효과의 극대화, 풍부한 유동성 등 많은 장점을 가지고 있지만, 직접투자 대비 위탁운용 방식으로 인한 불가피한 투자비용 증가라는 단점도 동시에 가지고 있다. 따라서 투자자는 펀드를 활용하여 포트폴리오를 운용하고자 하는 경우 비용 대비 효율성이 높은 상품을 선별하여 투자하는 것이 필요하다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '펀드 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (201, 'MULTIPLE_CHOICE', '[{"data": "개인투자자가 펀드를 활용하여 포트폴리오를 구성하는 경우에 필요한 프로세스에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["펀드 포트폴리오를 구성할 때 투자자는 다양한 조건들을 검토해야 하지만, 특히 최근 1년간 실현수익률을 평가하여 상위권에 있는 펀드를 중심으로 포트폴리오를 구성해야 한다", "펀드 포트폴리오를 구성할 때에는 투자대상 자산이나 지역, 국가, 섹터 등에 대한 검토 뿐만 아니라 어떠한 스타일과 전략을 갖고 있는지에 대한 세분류도 매우 중요하다", "펀드 포트폴리오를 구성하기 위해 투자자는 해당 운용사와 매니저의 투자철학이나 운용스타일을 확인하는 것이 필요하다", "펀드 포트폴리오를 구성하기 위해 투자자는 펀드평가 사이트를 방문하여 펀드의 위험대비 성과지표 등을 확인하는 것이 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "펀드를 활용하여 포트폴리오를 구성하는 경우 투자자는 과거 수익률 추이도 중요하지만 단순히 과거수익률에 의존하는 것 보다는 미래전망에 기초한 펀드투자가 더욱 중요하다. 이를 위해서 투자자는 사전적으로 펀드를 운용하는 자산운용사의 투자철학이나 전담 펀드매니저의 운용전략 등을 꼼꼼히 점검하는 것이 필요하고, 이를 위해서 해당 자산운용사의 홈페이지나 펀드평가사이트 등을 방문하여 리서치하는 것이 필요하다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '펀드 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (202, 'MULTIPLE_CHOICE', '[{"data": "개인투자자가 펀드를 활용하여 포트폴리오를 구성하는 목적 및 활용에 관한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["펀드는 전문가를 통한 체계적인 포트폴리오 운용 및 관리가 가능하다는 측면에서 활용도가 매우 높다", "펀드를 활용하여 포트폴리오를 구성하고자 하는 투자자는 직접투자 대비 높은 투자비용을 감안하여 비용 대비 효과를 극대화하는 노력이 필요하다", "투자자가 펀드를 통해 포트폴리오를 구성하게 되면 펀드 내에서 자체적인 관리가 이루어 지므로 운용에 대한 필요성이 없어지는 장점이 있다", "펀드는 다수의 투자자로부터 자금을 모집하여 투자하는 집합투자방식으로 투자자가 작은 투자원금으로도 포트폴리오 투자를 하기에 용이하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "펀드는 자체적으로 전문가에 의한 운용이 이루어지고 지속적으로 리밸런싱이 이루어진다는 점에서 위탁운용 방식을 통한 체계적인 포트폴리오 관리가 가능하다는 장점이 있다. 그렇지만 동일한 자산 및 시장에 투자하는 다수의 펀드상품이 존재하기 때문에 투자자는 펀드 포트폴리오를 구성함에 있어 비용 대비 효율성을 높일 수 있는 펀드를 선택하는 것과 함께 지속적으로 펀드의 운용상황 및 결과를 체크하고 펀드 포트폴리오를 관리하는 것이 필요하다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '펀드 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (203, 'MULTIPLE_CHOICE', '[{"data": "펀드 포트폴리오에 활용할 수 있는 펀드의 유형 및 특징에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["펀드 포트폴리오 중 주식, 채권, 파생상품 등 다양한 자산에 투자하여 절대수익을 추구하는 펀드로 롱숏펀드가 있으며 이는 주식 매수와 매도포지션을 동시에 가져감으로써 변동성을 줄이는 전략을 추구한다", "펀드 포트폴리오 중 주식, 채권, 파생상품 등 다양한 자산에 투자하여 절대수익을 추구하는 펀드로 이벤트드리븐 펀드가 있으며 이는 기업구조조정, 인수합병과 같은 이벤트 발생대상 기업에 투자함으로써 수익기회를 높일 수 있는 전략을 추구한다", "펀드 포트폴리오 중 대표적인 위험자산인 주식에 투자하는 주식형펀드는 규약상 최고 주식투자한도가 2/3를 초과하는 펀드를 말한다", "펀드 포트폴리오 중 대표적인 안전자산인 채권에 투자하는 채권형펀드는 규약상 주식은 10% 미만으로만 투자하고 나머지 자산은 채권에만 투자하는 펀드를 말한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "규약상 채권형펀드는 주식투자는 불가능하면서 채권, 기업어음(CP) 등 채권형 자산에만 투자하는 시가평가펀드를 말한다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '펀드 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (204, 'MULTIPLE_CHOICE', '[{"data": "투자자가 펀드 포트폴리오를 구성하기 위해 사전적으로 점검해야 할 내용 및 행동방식으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["펀드는 동일한 자산이나 시장에 투자하는 동종 전략의 다양한 상품이 있기 때문에 투자자는 좋은 펀드를 선정함으로써 포트폴리오의 효율성을 높이기 위해 노력해야 한다", "좋은 펀드를 선정하기 위해 투자자는 펀드를 운용하는 펀드운용회사와 펀드매니저의 운용철학과 전략을 점검해야 하고 이를 위해 펀드운용회사의 홈페이지나 펀드평가사이트를 확인하는 것이 필요하다", "투자자는 펀드폴리오를 구성하기 위해 투자설명서에 기재되어 있는 펀드의 투자등급을 확인하고 본인의 투자성향에 맞는 투자전략인지 점검하는 것이 필요하다", "투자자는 좋은 펀드 포트폴리오를 구성하기 위해 펀드평가 사이트를 확인하여 단기수익률 흐름을 중점적으로 체크해야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "투자자는 좋은 펀드 포트폴리오를 구성하기 위해 펀드평가 사이트 및 자산운용사 홈페이지를 통해 수익률 및 변동성 등 다양한 위험 대비 성과지표를 확인해야 한다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '펀드 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (205, 'MULTIPLE_CHOICE', '[{"data": "펀드 위험지표 및 위험대비 성과지표에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자자는 좋은 펀드 포트폴리오를 선정하기 위해 다양한 평가지표를 확인하는 것이 필요하고, 대표적으로 위험지표와 위험대비 성과지표를 들 수 있다", "펀드 포트폴리오의 위험을 평가하는 대표적 지표로는 표준편차와 베타가 있으며 두 지표는 그 값이 낮을수록 펀드의 성과 변동성이 크다는 의미이므로 포트폴리오의 표준편차와 베타를 높이는 전략이 필요하다", "펀드 포트폴리오의 위험대비 성과를 평가하는 대표적인 지표로 샤프, 트레이너, 젠센알파 등의 지표가 있으며 이들 지표가 클수록 위험 대비 성과가 높다는 의미로 해석할 수 있다", "펀드 포트폴리오 내에 편입한 펀드의 벤치마크 시장과의 민감도를 나타내는 대표적인 위험지표로 베타지표가 있으며 베타지표가 낮을 수록 펀드의 변동성이 낮다고 해석할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "펀드 포트폴리오의 위험을 평가하는 대표적인 지표로는 표준편차, 베타 등이 있으며 이들 지표는 그 값이 높을수록 펀드의 성과 변동성이 크다는 의미이므로 투자자는 펀드 포트폴리오를 선정할 때 이들 값이 낮은 펀드로 포트폴리오를 구성하는 것이 필요하다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '위험조정수익률 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (206, 'MULTIPLE_CHOICE', '[{"data": "투자자가 펀드 포트폴리오를 구성하는 과정에서 활용할 수 있는 다양한 조건 및 이를 적용하는 방법에 대한 설명으로 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자자는 다양한 투자자산에 투자하는 펀드의 위험대비 기대수익을 확인하고 상관관계가 낮은 펀드에 분산투자하여 펀드 포트폴리오를 구성하는 것이 필요하다", "투자자는 펀드 포트폴리오의 효율을 높일 수 있도록 상관관계가 높은 펀드에 대한 집중투자전략으로 포트폴리오를 구성하는 것이 필요하다", "투자자는 펀드 포트폴리오의 효율성을 높이기 위해 위험대비 기대수익률을 체크하여 그 비율이 낮은 펀드에 대한 집중투자전략으로 포트폴리오를 구성하는 것이 필요하다", "투자자는 다양한 수익-위험 구조를 가진 펀드유형 중 고수익-고위험 구조를 가진 펀드에 집중투자하는 포트폴리오전략을 추구하는 것이 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "투자자는 펀드 포트폴리오를 구성할 때 다양한 위험대비 기대수익을 가진 펀드에 분산투자하여 포트폴리오의 분산투자 효과를 높일 필요가 있다. 이를 위해서는 고위험-고수익유형의 펀드나 저위험-저수익유형의 펀드 등 특정 유형에 쏠리지 않고 다양한 수익-위험 구조의 펀드에 분산하여 투자하는 것이 필요하다. 투자자는 수익과 위험은 필연적으로 같은 방향으로 움직인다는 점을 인지해야 한다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '펀드 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (207, 'MULTIPLE_CHOICE', '[{"data": "펀드 포트폴리오를 관리하는 과정과 리밸런싱 프로세스에 관한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자자는 개별 펀드에서 수행되는 자산 재배분과는 별도로 지속적인 보유 펀드 포트폴리오의 리밸런싱을 통해 전체 펀드 포트폴리오의 수익률 관리를 해야 한다", "투자자는 펀드 포트폴리오의 효율을 높일 수 있도록 지속적인 펀드 리밸런싱을 수행해야 하며 최대한 그 주기를 단축할 필요가 있다", "투자자는 리밸런싱에 수반되는 비용을 감안하여 가장 적합한 리밸런싱 주기를 설정하고 주기에 따라 리밸런싱을 수행하는 것이 효율적이다", "투자자는 최초 포트폴리오 구성 시 정한 각 유형별 비중에서 일정 비율 이상으로 펀드 비중이 증감하는 경우에 최초 비율로 다시 조절해 주는 주기적 교체매매 전략을 활용할 필요가 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "펀드 리밸런싱을 위해서는 필연적으로 비용이 수반되며 이는 펀드 가입 시 지불하는 선취수수료 등과 같이 다양하다. 따라서 펀드의 리밸런싱 주기가 너무 잦으면 비용 대비 효율성이 떨어질 수 있다.  따라서 투자자는 펀드 리밸런싱을 위한 각자의 원칙을 정하는 것이 필요하다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '펀드 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (208, 'MULTIPLE_CHOICE', '[{"data": "다음은 투자자의 생애주기에 맞춰 자산배분전략을 수행하는 타겟데이트펀드(TDF)에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["타겟데이트펀드는 생애주기별로 위험자산-안전자산의 의 투자비중을 자동으로 조절해주는 펀드이다", "타겟데이트펀드는 Glide Path에 따라 은퇴가 많이 남은 시기에는 위험자산의 비중을 높여 공격적인 투자를 하고 은퇴시기가 가까워질 수록 위험자산의 비중을 축소한다", "타겟데이트펀드는 투자자 연령대에 맞춰 적극적으로 안전자산과 위험자산의 비중을 자동으로 조절해 주는 자산배분펀드로 단기적인 시장변화에 적극적으로 대응하는 전략을 구사한다", "타겟데이트펀드는 집합투자상품으로 운용되기 때문에 투자자 개개인의 다양한 변수들을 고려할 수 없다는 단점이 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "타겟데이트펀드는 투자자 연령대 및 은퇴시점에 맞춘 생애주기별 자산배분전략을 구사하는 상품으로 장기적으로 노후에 대비하는 투자플랜을 운용되는 상품이다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', 'TDF 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (209, 'MULTIPLE_CHOICE', '[{"data": "대체투자상품 포트폴리오를 구성하는 목적 및 의의에 대한 설명으로 가장 거리가 먼 것은? ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["최근 대체투자상품의 급성장과 대중화로 인해 개인투자자들도 대체투자상품을 활용한 포트폴리오 구성이 점차 많아지고 있다", "대체투자상품은 일반 전통자산과의 낮은 상관관계로 인해 포트폴리오 투자의 효율성을 높일 수 있다", "대체투자상품 포트폴리오는 주식, 채권 등 전통자산 포트폴리오 대비 높은 기대수익을 제공한다", "대체투자상품 포트폴리오는 시장에 연동되는 전통자산 대비 투자시점에 제시하는 기대수익률의 달성 확률이 상대적으로 높다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "대체투자상품 포트폴리오는 전통자산 포트폴리오와의 낮은 상관관계 등포트폴리오 활용도 측면에서 매우 유용하다. 아울러 주식이나 채권과 같이 시장의 움직임에 깊이 연동되는 전통자산과 달리 투자시점에 제시하는 기대수익률의 달성 확률이 높은 경향이 있다. 그러나 대체투자상품 포트폴리오가 전통자산 포트폴리오 대비 일방적으로 수익률이 높거나 위험대비 기대수익이 좋은 것은 아니다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '대체투자상품 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (210, 'MULTIPLE_CHOICE', '[{"data": "대체투자상품 포트폴리오 구성과정 및 유의사항에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["대체투자자산은 일반적으로 투자규모가 크고, 직접 조달하기 쉬우며 장내시장에서 거래되는 형태가 많아서 개인 투자자가 대체투자상품 포트폴리오를 구성하는데 효율적이다", "대체투자상품은 일반적으로 전통자산 대비 투자기간이 장기이고 자산의 유동성이 낮기 때문에 유동성 관리가 중요하다", "대체투자상품은 일반적으로 공모형태보다는 사모형태로 모집되는 경우가 많기 때문에 대체투자상품 포트폴리오 구성과정은 전통자산 포트폴리오 대비 어려움이 있을 수 있다", "대체투자상품은 내재된 리스크나 환매제약 조건 등 사전에 점검해야 할 사항들이 많기 때문에 전문가를 통해 꼼꼼히 확인하는 작업이 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "대체투자자산은 일반적으로 투자규모가 크고, 직접 조달하기 어려우며 장외시장에서 거래되는 형태가 많아서 개인 투자자가 대체투자상품 포트폴리오를 구성하기 위해서는 직접투자보다는 펀드나 신탁 등 간접투자상품을 활용하는 것이 효율적이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '대체투자상품 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (211, 'MULTIPLE_CHOICE', '[{"data": "대체투자상품 포트폴리오를 구성하는데 활용할 수 있는 헤지펀드에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["헤지펀드는 시장상황과 상관관계가 낮은 다양한 전략을 통해 보다 효율적인 포트폴리오 투자효과를 제공할 수 있다", "멀티전략형 헤지펀드는 주식, 채권 등의 전통자산과 부동산, 인프라 등 다수의 대체전략을 자산배분의 형태로 운용하는 전략으로 포트폴리오 투자효과를 높일 수 있다", "헤지펀드는 개인투자자가 직접적으로 투자하기 어려운 다양한 자산에 손쉽게 투자할 수 있는 수단을 제공하고 있으며 불특정다수의 투자자로부터 공모 형태로 펀드를 조성하여 높은 유동성을 제공하고 있다", "개인투자자가 헤지펀드에 가입할 때는 높은 최소가입규모와 함께 일정 기간 환매제한과 같은 까다로운 입출입 규정 등을 잘 살펴봐야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "헤지펀드는 전통자산 뿐만 아니라 다양한 대체전략을 레버리지를 일으켜 투자하는 사모펀드의 형태이므로 투자기간이 장기일 수 있고, 환매 제한기간과 높은 최소가입금액 등 개인 투자자 입장에서 제한이 있을 수 있다는 점은 감안해야 한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '대체투자상품 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (212, 'MULTIPLE_CHOICE', '[{"data": "개인투자자가 투자자산 포트폴리오를 구성할 때, 대체투자상품과 전통자산투자상품의 비중을 조절하는 방법으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["일반적으로 개인 투자자는 과거 투자 경험에 의존하여 포트폴리오를 구성하는 측면이 강하기 때문에 시장과 연관성이 높은 전통자산과 시장과 연관성이 낮은 대체투자상품을 함께 섞어서 포트폴리오를 구성할 필요가 있다", "대체투자상품을 포트폴리오에 활용할 때는 전통자산에서 커버하지 못하는 리스크-리턴 프로파일 상의 영역을 채워 투자하는 것이 좋다", "전통자산과 대체투자자산의 가격간 상관관계를 인지하고 시장 국면에 따라 유기적으로 비중을 조절하는 것이 필요하다", "전통자산과 대체투자자산은 상관관계가 낮아 포트폴리오 효과가 크기 때문에 초기에 비중을 균등하게 배분하여 투자할 필요가 있고, 이러한 비중은 절대적으로 유지하는 것이 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "일반적으로 개인 투자자는 과거의 투자경험에 대한 의존도가 높아 포트폴리오가 특정 자산에 편중되는 경향이 많이 나타나기 때문에 시장의 변동성에 그대로 노출되는 경우가 많다. 따라서 개인 투자자가 투자 포트폴리오에 대체투자상품을 활용하는 목적은 전통자산에서 커버하지 못하는 리스크-리턴 프로파일 상의 영역을 채워, 보다 안정적인 자산관리를 하는데 있다. 따라서 투자자는 전통자산과 대체투자자산간의 상관관계를 파악하고, 시장국면에 따라 비중을 유기적으로 조절하는 것이 필요하다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '대체투자상품 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (213, 'MULTIPLE_CHOICE', '[{"data": "경기 및 금리 상황에 따른 전통자산과 대체자산 가격의 움직임에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["경기가 좋아지면서 인플레이션이 발생하는 구간에서는 금리가 상승할 가능성이 크기 때문에 채권의 비중은 축소하는 대신 원자재와 같은 대체투자상품의 비중을 늘리는 것이 필요하다", "경기가 악화되면서 물가가 하락하는 구간에서는 금리가 하락할 가능성이 크기 때문에 채권의 비중은 늘리는 대신 원자재의 비중은 축소하는 것이 필요하다", "경기가 좋아지면서 물가가 하락하는 구간에서는 기업실적 상승과 함께 금리가 안정화 되면서 주식의 비중을 늘리는 것이 필요하다", "경기가 악화되면서 물가가 상승하는 구간에서는 금리가 상승할 가능성이 크기 때문에 채권의 비중은 줄이는 대신 부동산의 비중을 늘리는 것이 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "일반적으로 경기가 악화되면서 물가가 상승하는 구간에서는 기업의 실적악화와 함께 물가 상승을 억제하기 위해 정책금리가 상승하고, 이에 따른 시장금리 상승이 진행될 가능성이 크기 때문에 주식이나 채권의 비중을 축소하고, 대체투자상품 중에서는 금리와 역의 상관관계가 높은 부동산의 비중을 축소하는 전략이 필요하다. 반면 물가가 상승하는 구간이므로 원자재에 대한 투자비중을 늘림으로써 인플레이션에 대응하는 전략이 필요하다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '대체투자상품 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (214, 'MULTIPLE_CHOICE', '[{"data": "다음과 같은 증권사 시장전망에 동의한 투자자가 취해볼 수 있는 전통자산과 대체투자상품 간 리밸런싱 내용으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["현재 시장 판단 및 향후 전망 :", "현재 시장은 풍부한 유동성으로 인해 경기 호황국면을 이어가고 있고, 이에 맞춰 기업실적은 호조세를 이어가고 있음. 반면 물가는 상승세를 이어가며 인플레이션에 대한 우려가 제기되는 국면임"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식비중 확대 ? 채권비중 축소 ? 부동산비중 축소 ? 원자재 비중 확대", "주식비중 축소 ? 채권비중 확대 ? 부동산비중 축소 ? 원자재 비중 확대", "주식비중 확대 ? 채권비중 확대 ? 부동산비중 확대 ? 원자재 비중 확대", "주식비중 축소 ? 채권비중 축소 ? 부동산비중 축소 ? 원자재 비중 축소"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "현재 시장국면은 경기호황국면이 이어지면서 기업실적의 호조세가 지속되고 이로 인한 주식시장의 활황이 지속될 수 있는 환경이다. 따라서 주식의 비중은 확대하는 것이 필요하다. 반면에 인플레이션에 대한 우려로 인해 향후 중앙은행의 정책금리 인상과 이로 인한 시장금리 상승이 예상되는 국면이기 때문에 채권의 비중은 축소하는 것이 필요하다. 대체투자상품 중 금리와 역의 상관관계에 있는 부동산 역시 비중을 축소하는 것이 바람직하며, 반면 인플레이션 헷지기능을 갖고 있는 원자재의 비중은 확대함으로써 인플레이션에 따른 부작용을 완화시킬 수 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '대체투자상품 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (215, 'MULTIPLE_CHOICE', '[{"data": "개인투자자가 포트폴리오에 많이 활용하고 있는 대표적인 대체투자상품와 그에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금은 대표적인 안전자산으로 인식되는 대체투자상품으로 글로벌 경기침체나 경제위기 국면에서 가격이 상승하는 특징이 있다", "부동산은 저금리가 이어지는 국면에서 가격이 상승하는 특징이 있으며 물가동향과 가격이 정비례하여 움직이는 특징이 있다", "원자재는 대표적인 인플레이션 헷지용 대체투자상품으로 주식이나 채권과 같은 전통자산과의 낮은 상관관계로 분산투자효과가 높다", "경기악화 국면에서 정부의 경기진착책 등 정책 이슈가 기대되는 국면에서는 인프라와 같은 대체투자상품에 대한 비중확대가 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "부동산은 저금리가 이어지는 국면에서 가격이 상승하는 특징이 있으며 물가의 움직임과 부동산 가격은 반대로 움직이는 경향이 있다. 따라서 인플레이션 국면에서는 부동산 투자비중은 축소하는 전략이 필요하다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '대체투자상품 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (216, 'MULTIPLE_CHOICE', '[{"data": "개인투자자가 자산배분전략과 포트폴리오에 활용할 수 있는 상장지수펀드(ETF)에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["상장지수펀드는 낮은 비용, 높은 유동성, 투명한 운용 등의 특징을 갖추고 있어 개인 투자자가 포트폴리오 운용을 하는데 있어 효율을 높일 수 있는 대표적인 상품이다", "상장지수펀드는 주식, 채권 등과 같은 전통자산 뿐만 아니라 부동산, 원자재, 인프라 등 대체투자상품 등을 기초로 하여 만들 수 있어 활용도가 높은 상품이다", "상장지수펀드는 특정지수를 벤치마크로 선정하고, 이 지수를 추종하여 수익을 내는 패시브 형태의 상품만 존재한다", "최근에는 다양한 상장지수펀드를 편입하여 자산배분효과를 높이는 EMP (ETF Managed Portfolio)와 같은 상품도 등장하며 더욱 활용도를 높여주고 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "상장지수펀드는 특정지수를 벤치마크로 선정하고 이 지수를 추종하여 수익을 내는 패시브 펀드로 처음 등장하였으나, 최근에는 액티브 전략으로 운용되는 상품이 등장하며 빠르게 성장하고 있는 추세이다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '개인투자자 자산배분전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (217, 'MULTIPLE_CHOICE', '[{"data": "개인투자자가 자산배분전략과 포트폴리오를 구성하는데 활용할 수 있는 간접투자수단에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인투자자가 자산배분전략과 포트폴리오를 구성하는데 있어 비용 측면이나 효율성 측면에서는 직접 자산을 편입하는 것이 가장 유리하다", "펀드나 신탁과 같은 간접투자상품은 동일한 자산에 투자하더라도 운용주체의 성향에 따라 운용전략에 차이가 많을 수 있기 때문에 신중한 선택이 필요하다", "로보어드바이저 상품은 개인 투자자의 성향을 분석하여 이에 맞는 자산배분전략과 포트폴리오를 구성하고 관리하는 상품으로 최근 활용도가 증가하고 있다", "로보어드바이저와 같은 투자자 맞춤형 상품은 자산배분전략과 포트폴리오 운용에 있어 활용도가 높으나 고비용이나 성과의 불확실성 등의 단점도 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "개인 투자자가 간접투자상품을 활용하여 포트폴리오를 구성하는 것은 시장에서 직접 자산을 편입하여 포트폴리오를 구축하는 것 보다 훨씬 편리하고 효율적이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '개인투자자 자산배분전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (218, 'MULTIPLE_CHOICE', '[{"data": "자산배분전략과 포트폴리오 구성에 앞서 수행되어야 하는 투자목표설정에 대한 설명으로 가장 거리가 먼 것은? ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인투자자가 자산배분전략과 포트폴리오를 구성하는데 있어 수익실현 기준 및 로스컷 기준 등 명확한 목표를 수립하는 것이 매우 중요하다", "자산배분의 안정성과 투자 수익성을 함께 추구하는 것을 목표로 하는 전략을 베타-알파 투자전략이라 하고, 이 전략은 장기적이고 안정적 운용을 목표로 하는 베타 자산에 70~80%를 투자하고, 나머지 자산을 추가수익 가능성이 높은 알파 자산에 투자하는 전략이다", "자산배분전략을 충실하게 반영하여 안정적인 성과유지를 목표로 하는 전략을 베타 투자전략이라고 하고, 이 전략은 최초 설정한 자산군별 비중을 큰 변화 없이 유지하는 전략이다", "시장상황에 따른 적극적인 투자수익 추구를 목표로 하는 전략을 알파 투자전략이라고 하고, 이 전략은 전체 자산을 추가수익 가능성이 높은 고위험-고수익 자산에 투자하는 전략이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "자산배분전략과 포트폴리오 운용을 수행하는 목적은 특정 자산에 몰빵투자를 하거나 단기적인 시장의 움직임만을 추종하여 투자함으로써 발생할 수 있는 위험을 줄이고 안정적인 자산관리를 도모하기 위함이다. 따라서 다양한 자산배분전략과 포트폴리오 구성의 목표 중 특정 자산이나 전략에 전체 자산을 투자하는 것은 성립되지 않는다. 알파투자전략의 경우에는 베타투자자산의 비중을 50% 수준 이내에서 유지하고, 나머지 비중은 시장의 단기변화를 효과적으로 포착할 수 있는 알파투자 전략에 투자하는 전략을 말한다"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '개인투자자 자산배분전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (219, 'MULTIPLE_CHOICE', '[{"data": "개인투자자가 투자목표를 정하고, 포트폴리오를 관리할 때의 유의사항으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인투자자가 투자목표를 수립할 때는 가급적 목표수익률을 높게 설정하고 시장상황에 따라 목표수익률을 조절해 가는 것이 필요하다", "포트폴리오를 관리함에 있어 기준이 되는 수익률은 전 운용기간에 걸친 평균수익률을 사용하여야 한다", "포트폴리오를 관리함에 있어 유동성 리스크에 대한 점검이나 긴급상황에 대비한 유동성 관리는 필수적이다", "포트폴리오의 수익률은 특정시점에만 관리하는 것이 아니라 시장환경의 변화에 따라 지속적으로 관리하는 것이 중요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "투자에 대한 목표 수익률은 적정한 수준으로 설정하는 것이 좋다. 목표수익률이 과도할수록 투자자는 특정 자산에 집중해서 투자하는 경향이 생기게 되고 이로 인해 안정적인 자산관리가 어려워 질 수밖에 없다. 자산배분전략과 포트폴리오 운용은 일시적인 높은 수익이 아니라 꾸준하고 안정적인 수익을 지속적으로 가져감으로써 안정적인 자산관리를 할 수 있는데 목적이 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '개인투자자 자산배분전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (220, 'MULTIPLE_CHOICE', '[{"data": "개인별 목표에 기반하여 자산배분전략과 포트폴리오 운용을 수행하는 방법에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인투자자가 기관투자자 및 투자전문기관의 자산배분전략을 참고할 수는 있지만, 투자자의 개별 상황을 감안하지 않은 전략이므로 현실적으로 맞지 않을 수 있다", "투자자 목표기반 자산배분전략과 포트폴리오를 구성하는데 있어 가장 중요한 것은 시장성과 대비 꾸준히 초과성과를 달성하는 것에 있다", "투자자 목표기반 자산배분전략과 포트폴리오를 구성하는 것은 나의 목표와 상황에 맞는 가장 효율적인 전략을 찾는 것이다", "투자자 목표기반 자산배분전략과 포트폴리오 구성을 통해 개인투자자가 가장 노출되기 쉬운 군중심리 리스크를 제어할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "투자자 목표기반 자산배분전략과 포트폴리오를 구성하는데 있어 중요한 것은 시장의 성과 대비 몇 %의 수익을 얻겠다거나, 절대수익으로 몇 %의 수익을 내겠다는 것이 아니라 내가 수립한 목표를 달성하기 위한 자산배분과 포트폴리오 전략을 구성하는 것에 있다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '개인투자자 자산배분전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (221, 'MULTIPLE_CHOICE', '[{"data": "투자자 목표기반 자산배분전략 및 포트폴리오 구성 단계에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["첫번째 단계에서는 구체적이고 현실적인 투자 목표를 수립해야 하며 구체적으로 명확한 투자기간과 미래에 필요한 자금 규모 등과 같은 현실적인 목표를 수립한다", "두번째 단계에서는 세금제도에 기반한 세후 수익률과 함께 투자시 소요되는 투자비용을 고려하여 포트폴리오에 넣을 수 있는 투자 후보군을 구축한다", "세번째 단계에서는 투자자산의 위험 대비 기대수익을 감안하여 목표에 맞는 투자군을 선택해야 하며 이때 투자자는 자산군의 최근 성과를 기준으로 투자군을 선택한다", "네번째 단계에서는 투자자의 제약 조건을 고려해서 시나리오 분석을 진행하고 세번째 단계에서 선택된 투자군 내에 있는 자산 중에서 실제 투자자산을 선택하여 투자 포트폴리오를 구성한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "투자자 목표기반 자산배분전략과 포트폴리오를 구성 절차 중 세번째 단계에서는 투자자산의 위험 대비 기대수익을 감안하여 목표에 맞는 투자군을 선택하게 되며 투자자는 자산군의 최근 성과만을 기준으로 판단하는 것이 아니라 장기간의 평균수익률을 감안하는 것이 중요하다. 따라서 투자자는 장기간의 역사적 성과에 기반해서 다양한 자산들의 위험대비 기대수익을 참고하여 투자하는 것이 필요하다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '개인투자자 자산배분전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (222, 'MULTIPLE_CHOICE', '[{"data": "자산배분전략과 포트폴리오 구성에 앞서 선행되어야 하는 투자성향 분석에 대한 설명으로 가장 거리가 먼 것은? ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["공격투자형은 시장평균 수익률을 훨씬 상회하는 높은 투자수익을 추구하는 성향으로 자산가치의 변동에 따른 손실위험을 적극 수용하는 특징이 있다", "위험중립형은 투자원금의 보전보다는 위험을 감내하더라도 높은 수준의 투자수익 실현을 추구하는 특징이 있다", "안정추구형은 투자원금의 손실 위험은 최소화하고, 이자소득이나 배당소득 수준의 안정적인 투자를 목표로 하는 특징이 있다", "안정형은 투자원금에 손실이 발생하는 것을 원하지 않으며 예적금 수준의 수익률을 목표로 하는 특징이 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "투자성향 중 위험 중립형은 투자에 상응하는 투자위험이 있음을 충분히 인식하고 있고, 예금성 상품보다 높은 수익을 기대할 수 있다면 일정수준의 손실위험을 감수하는 특징이 있다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자성향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (223, 'MULTIPLE_CHOICE', '[{"data": "투자성향과 이에 따른 자산배분비율의 조합 중 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["안정추구형 투자자 = 안전자산 100% 투자", "공격투자형 투자자 = 위험자산 100% 투자", "적극투자형 투자자 = 위험자산 70~90% : 안전자산 10~30%", "위험중립형 투자자 = 안전자산 90% : 위험자산 10%"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "투자자 유형별로 다음과 같은 위험자산과 안전자산의 배분이 적합하다. \n안정추구형이나 안정형 투자자 = 안전자산 90% : 위험자산 10%\n공격투자형이나 적극투자형 투자자 = 안전자산 10~30% : 위험자산 70~90%\n위험중립형 투자자 = 안전자산 40~60% : 위험자산 60~40%"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자성향 이해', null, null);
