insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (263, 'MULTIPLE_CHOICE', '[{"data": "당신은 금송이에게 ''하이리스크-하이리턴''이라는 개념을 설명하려 합니다. 올바른 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["얻고자 하는 수익이 높을수록 손실 위험이 커진다는 뜻이다.", "주식 위험정도는 5단계 중 4단계로 \"고위험\" 군이다.", "지난 41년간 주식(코스피)의 연수익률 중간값은 10%였다.", "일반적인 금융상품 위험크기는 예금<주식<채권 순이다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "일반적인 금융상품의 위험 크기는 예금 < 채권 < 주식 입니다."}]', '상', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/08/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', null, null, null);
