insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (269, 'MULTIPLE_CHOICE', '[{"data": "금송이는 자신만의 방법으로 기업가치를 분석하여 주식 투자를 하고 있습니다. 이에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기업가치는 기업의 안전성, 수익성, 성장성에 대한 평가를 종합하여 분석한다", "가치투자란 우량기업의 주식이 싸게 거래될 때 매수해서 제 가치로 매도하는 것이다", "바겐세일 가격으로 거래되는 우량기업의 주가가 적정가치로 회귀하는데 수년이 걸릴 수 있다", "재무제표로 기업의 사업 및 자산 가치를 살필 수 있고, 기업의 미래가치도 산정할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "기업의 재무적 요소를 활용하여 기업가치를 측정해 볼 수 있지만, 기업의 미래가치 및 미래성장 가능성 등에 대한 분석은 투자자의 능력과 통찰이 필요합니다."}]', '상', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/08/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', null, null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (270, 'MULTIPLE_CHOICE', '[{"data": "A기업 주가는 5천원, B기업은 주가는 1만원입니다. 금송이는 규모가 큰 기업에 투자하기 위해 가격이 비싼 B기업 주식을 사려합니다. 다음 중 가장 올바른 조언은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["B기업은 주식이 2배나 비싸니 A기업 보다 규모가 큰게 맞다", "기업규모는 주식가격으로 알 수 없고 시가총액을 확인해야 한다", "기업규모는 주가에 반비례하니 A기업이 B기업보다 더 크다", "기업규모는 기업에서 발표하는 재무제표의 주석으로 알 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "기업규모는 자본금, 시가총액 등으로 구분합니다. 시가총액의 경우, 분류 3개월전부터 일평균 시가총액을 기준으로 시가총액 상위 100위까지를 ''대형주''로, 상위 101위부터 300위까지를 ''중형주''로, 나머지 종목을 ''소형주''로 분류합니다."}]', '하', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/08/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', null, null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (271, 'MULTIPLE_CHOICE', '[{"data": "기업의 자산·이익·자본·배당에 해당하는 재무정보와 주가를 이용한 기업평가방법에 대한 금송이의 설명 중 가장 올바르지 않은 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["PBR이 1보다 작다면, 기업의 청산 가치가 현재의 주가보다 크다", "PER이 3배라면, 1주당 이익보다 3배 높은 가격으로 주식이 거래되고 있다는 것을 의미한다", "ROE는 기업 경영효율성을 측정 하는 것으로 낮을수록 좋다는 의미이다", "배당수익률은 1주당 배당금을 주가로 나눈 값으로, 배당금이 주가의 몇% 인가를 나타낸다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "ROE(자기자본이익률)는 높을수록 경영효율성 및 사업실적이 좋다는 것을 의미합니다."}]', '중', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/08/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', null, null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (272, 'MULTIPLE_CHOICE', '[{"data": "금송이는 PER이 5배인 A기업에 투자하려 한다. 이 업종 PER은 10배, 타업종 B기업 PER은 20배이다. 금송이에게 해 줄 조언 중 가장 올바르지 않은 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["PER은 현재 주가와 과거 실적으로 산출되는 지표이므로, 추정 PER보다 투자에 활용도가 높다", "PER이 높은 주식 보다는 낮은 주식에 투자하는 것을 저PER주 투자라 한다", "저PER의 원인이 일시적인 가격하락이 아니고 실적하향인 경우를 가치함정이라고 한다", "PER은 업종마다 평균이 달라 업종이 다른 A,B 기업을 단순 비교하는 것은 적절치 않다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "PER은 현재의 주가와 과거 실적으로 산출된 EPS(주당순이익)로 산출되어 기업가치를 평가하는데 한계가 있습니다. 이를 보완하기 위한 추정EPS로 구한 추정PER이 투자에 활용도가 더 높을 수 있습니다."}]', '상', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/08/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', null, null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (273, 'MULTIPLE_CHOICE', '[{"data": "금송이는 A주식에 투자하여 배당금을 받고자 합니다. 다음의 경우 금송이가 배당금을 받으려면 언제까지 주식을 사야 할까요?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["<3분기 배당일정>", "배당락일 : 9월 29일(수)", "배당기준일 : 9월 30일(목)", "배당지급일 : 11월18일(목)"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["9월 28일(화)", "9월 29일(수)", "9월 30일(목)", "11월 18일(목)"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "배당금을 받으려면 배당기준일에 반드시 해당 주식을 보유하고 있어야 합니다. 또한 주식 거래의 결제(T+2일 결제) 특성상 영업일 기준으로 최소 2일 전에는 주식을 매수해야 합니다."}]', '하', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/08/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', null, null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (274, 'MULTIPLE_CHOICE', '[{"data": "금송이는 배당금을 받기 위해 A기업에 투자하려 합니다. 배당에 대해 금송이에게 해 줄 수 있는 설명으로 가장 올바르지 않은 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["당기순이익 가운데 주주에게 지급하는 것을 배당이라고 한다", "주당배당금 5백원, 액면가 5천원이면 실질 배당수익률은 10%이다", "높은 배당률은 기업의 재투자가 부족하다는 뜻일 수 있다", "예금 금리가 1%일 때, 3~5% 이상의 배당수익률이면 고배당이라고 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "실질 배당수익률은 주당배당금과 주가로 계산됩니다. 즉 주당배당금/주가 입니다."}]', '중', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/08/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', null, null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (275, 'MULTIPLE_CHOICE', '[{"data": "금송이는 투자할 주식을 고르기 위해 조언을 구합니다. 종목고르기 방법 중 가장 올바르지 않은 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기업의 수익 증대, 낮은 부채, 자산 규모 등을 확인하는 것은 종목고르기의 기초 요소이다", "모멘텀 투자할 때는 종목의 고평가 여부를 판단하는 주요 지표로 P/E 비율을 이용한다", "가치 투자할 때는 기업의 펀더 멘털을 분석하여 저평가 여부를 판단한다", "기업의 양적요인과 경영진, 고객 충성도 등도 파악하여 종합적인 미래성장성을 판단해야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "P/E(Price/Earning) 비율은 가치투자에서 종목의 고평가 여부를 판단하는 주요 지표로 사용됩니다."}]', '상', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/08/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', null, null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (276, 'MULTIPLE_CHOICE', '[{"data": "당신은 금송이에게 단기 주가변동 이유를 설명합니다. 가장 올바르지 않은 설명은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["시장 전체의 가격 변화가 클 때, 많은 주식은 같은 방향으로 움직일 수 있다", "통상, 채권 이자율이 높아지면 주식투자의 매력도 높아진다", "원자재 기반의 제품을 제공하는 기업의 주가는 원자재 가격에 따라 크게 영향받을 수 있다", "경제지표, 기준금리 등의 발표는 주식투자 여부를 결정하는데 영향을 끼칠 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "일반적으로 안전자산 성격인 채권의 이자율이 높아지면 위험자산인 주식의 매력도는 상대적으로 낮아집니다."}]', '하', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/08/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', null, null, null);
