insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (278, 'MULTIPLE_CHOICE', '[{"data": "금송이는 투자하고 있던 A기업에서 자사주(자기회사 주식)를 매입한다는 공시를 보게 되었습니다. 금송이의 다음 판단 중 가장 올바르지 않은 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["A기업이 자기회사 주식을 매입하는 것으로 단기적으로 주가에 부정적인 영향을 준다", "자기회사 주식을 소각하는 경우는 주식수가 줄어 A기업의 1주당 이익이 커진다고 판단한다", "A기업이 사들인 자기회사 주식은 원칙적으로 6개월이내에 매도할 수 없다", "A기업의 매출과 순이익 등 사업실적 증가 여부를 추가로 확인해야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "자사주 매입은 주주친화적인 행동으로 분류되며, 단기적으로 주가에 긍정적인 영향을 미칩니다."}]', '상', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/08/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', null, null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (279, 'MULTIPLE_CHOICE', '[{"data": "금송이는 A기업의 투자정보를 모으던 중 최근 분기에 발표한 기업실적보고서를 보게 되었습니다. 이와 대한 설명으로 가장 올바르지 않은 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["일반적으로 기업의 실적발표에 투자자는 높은 관심을 보인다", "발표된 기업실적은 시장 예측치와도 비교해야 한다", "예측치보다 저조한 기업실적은 주가에 부정적인 영향을 준다", "기업실적 예측치는 주식시장을 운영하는 거래소가 발표한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "기업의 실적예측치는 증권사(애널리스트들)가 경제와 산업 상황을 고려해 산정하여 발표하며, 다른 증권사의 전망치를 평균한 것을 컨센서스라고 합니다."}]', '하', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/08/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', null, null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (280, 'MULTIPLE_CHOICE', '[{"data": "금송이는 최근 경영진을 교체하고 신상품을 출시하는 A기업에 투자를 고려하고 있습니다. 다음 판단 중 가장 올바르지 않은 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["매출, 이익 등 숫자로 표현되는 양적 요인외에 질적 요인도 주가에 영향을 미칠 수 있다", "경영진의 교체는 주가에 영향을 미치는 요인이 될 수 있다", "팬덤과 같은 강한 고객충성도는 주가에 긍정적인 영향을 미칠 수 있다", "기업의 회계부정행위는 주가에 작은 영향을 미칠 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "기업의 회계부정행위는 투자자에게 치명적인 위험이다. 주가는 하락하고 주식시장에서 거래정지가 되고, 종국에는 무상감자 또는 상장폐지도 될 수 있습니다."}]', '중', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/08/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', null, null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (281, 'MULTIPLE_CHOICE', '[{"data": "금송이는 주식투자를 위해 산업의 특징과 트렌드를 조사하려고 합니다. 다음 중 가장 올바르지 않은 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["산업에 대한 구체적인 정보는 증권사 애널리스트 보고서에 담겨 있다", "자신이 종사하는 업종 등 익숙한 산업을 먼저 조사한다", "인구구조의 변화는 유아용품, 교육, 헬스케어 산업 등에 영향을 미친다", "전자 산업은 삼성전자 주가가 오르면 다른 전자회사 주가도 오른다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "과거에는 특정 종목이 오르면 그 산업에 있는 주식이 다 오르는 경우가 많았습니다. 삼성전자가 오르면 다른 전자회사 주가가 아무 이유 없이 같이 올랐습니다. 기업 내용을 고려하지 않던 때의 얘기인데, 지금은 그런 경우가 거의 없습니다."}]', '상', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/08/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', null, null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (282, 'MULTIPLE_CHOICE', '[{"data": "안전지향 투자자인 금송이가 다음 상황에서 판단하는 내용 중 가장 합리적인 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["금송이는 1,000만원을 예금과 배당주에 50:50 포트폴리오를 구성하고 목표 수익률은 3%로 정하고 있습니다. 주가는 일정한데 예금 금리가 1%에서 2%로 올른다고 합니다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["이자가 늘어나는 것으로만 해석하고 다른 행동은 하지 않는다", "예금을 늘리기 위해서 주식의 일부를 처분한다", "주식에 더 투자하기 위하여 예금의 일부를 해지한다", "주식 투자를 위하여 예금의 전부를 해지한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "안정지향 투자자의 포트폴리오 목표 수익률을 3%로 정하고 있는 가운데 예금금리 인상으로 예금수익이 증가한다면 위험자산인 주식을 줄이고 안전자산인 예금으로 이동하는게 합리적입니다."}]', '하', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/08/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', null, null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (283, 'MULTIPLE_CHOICE', '[{"data": "금송이의 다음 질문에 대한 올바른 답변으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["요즘 경기는 너무 나쁜데 주식시장은 계속 오르는 상황이 이해가 되지 않습니다. 왜 그런가요?"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["경기는 회복기, 호황기, 후퇴기, 침체기 순으로 순환하고 주식시장은 경기상황에 앞서 움직인다", "통계청은 경기종합지수를 발표하고, 경기선행지수가 상승하면 경기가 좋아질 것으로 예상한다", "KOSPI지수는 경기선행지수 중 하나로, 약 6개월 앞서 움직인다", "경기 방어주는 경기 변동에 따라 기업 실적과 주가가 크게 달라지는 주식으로 자동차,철강 등이 해당한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "경기 방어주는 경기와 상관없이 움직이는 주식입니다. 전기, 가스, 철도 같은 공공재와 의약품, 식료품 같은 생필품을 만드는 회사가 여기에 해당합니다. 반면 경기 민감주는 경기 변동에 따라 기업 실적이 크게 달라지는 주식입니다. 자동차, 철강, 항공운수, 석유화학, 건설, IT가 이에 해당합니다."}]', '중', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/08/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', null, null, null);
