insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (288, 'MULTIPLE_CHOICE', '[{"data": "다음 그래프에서 52주 고가와 저가를 바르게 표시한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_IMAGE", "preface": "null", "dataType": "image", "data": ["img_1.png"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["(고가) 5월 / (저가) 6월", "(고가) 1월 / (저가) 7월", "(고가) 4월 / (저가) 10월", "(고가) 12월 / (저가) 1월"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "52주 고가는 1월이고 52주 저가는 7월 입니다."}]', '하', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/08/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', null, null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (289, 'MULTIPLE_CHOICE', '[{"data": "금송이는 A기업 공모주에 투자하려고 하는데 \"따상이 기대된다\"라는 기사를 보았습니다. 올바른 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["공모주란 기업이 주식시장에 상장하기 위하여, 일반인에게 배정하는 주식을 말한다", "따상은 상장 첫날, 공모가 대비 두 배로 시작하여 가격제한폭(30%)까지 오르는 것을 말한다", "최대주주는 보호예수기간 종료 후 대량 매도할 수도 있다", "상장 후 주가는 공모가 이하로 하락할 수 없다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "공모가는 기업공개 단계에서 정해지는 단일가격이지만, 상장 후에는 여러 상황에 따라 주가는 변동하게 됩니다. 이 때 공모가격보다 주가는 오를 수도 있고 떨어질 수도 있습니다."}]', '중', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/08/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', null, null, null);
