insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (291, 'MULTIPLE_CHOICE', '[{"data": "금송이는 주식 차트 보는 법을 배우려고 합니다. 다음 설명 중 가장 올바르지 않는 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["단기 이동평균선이 중장기 이동평균선을 상향돌파 시 매도신호로 활용한다", "주가가 저점에서 많이 오른 종목은 차익실현 등으로 주가의 변동 위험성이 커질 수 있다", "파란색 막대를 음봉, 빨간색 막대를 양봉이라고 한다", "차트로 알 수 있는 과거 흐름은 미래 결과를 보장하지 않는다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "단기 이동평균선이 중장기 이동평균선을 상향돌파할때는 매수신호로 활용합니다."}]', '하', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/08/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', null, null, null);
