insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (1, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "3년 간 주식투자로 다음과 같은 현금흐름이 발생했을 때, 보유기간 동안 수익률로 올바른 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 2, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "투자에서 발생하는 수익률은 다음과 같이 계산한다. 먼저 주식과 채권과 같은 투자 자산을 매도한 가격에서 이를 매입할 때 들어간 금액을 뺀 시세 차익과 채권 이자와 주식 배당처럼 투자 자산에서 발생한 현금흐름을 더한다. 그리로 수수료 등 비용을 차감한다. 이렇게 계산한 금액을 매입 금액으로 나눈 다음 100을 곱해 퍼센트(%)로 환산하면 수익률을 계산할 수 있다. \n\n수익률=(매도 금액-매입 금액±보유 기간 중 현금흐름-비용)/(매입 금액)  ×100\n          =  (120만원-100만원+10만원-5만원)/100만원  ×100 = 25% ", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자수익률 계산');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (2, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "1,000만원을 연 복리 6%로 투자해서 2,000만원으로 만드는데 걸리는 시간으로 가장 올바른 것은? (근사치로 구하시오)", "type": "QUESTION_TEXT"}]', '[{"data": 4, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "72의 법칙은 복리를 전제로 자산이 2배로 늘어나는 데 걸리는 시간을 손쉽게 계산하는 방식이다. 72를 해당 수익률로 나누면 대략적으로 원금의 2배가 되는 기간을 산출할 수 있다.  \n\n72÷금리= 원금이 2배가 되는 시기(연)\n\n따라서 1000만원의 돈을 연 6%의 복리 상품에 투자하면 원금이 2배가 되는 데 걸리는 시간은 12년(72÷6=12)이다. ", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '72의 법칙 활용');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (3, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "장기 투자성과에 가장 큰 영향을 미치는 것으로 올바른 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 1, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "투자 전략을 실행하는 과정에서 투자자는 자산 배분, 종목 선정, 투자 시기 포착이라는 3가지 문제에 직면한다. 이중 투자성과에 가장 큰 영향을 미치는 것은 자산배분이다. 1974~1983년 91개의 대규모 연금 플랜을 연구한 결과, 자산 배분이 포트폴리오 운용 성과에 91.5%의 영향을 미치는 것으로 나타났고, 증권 선택(2.7%)과 시장 예측(1.8%)이 미치는 영향은 미미했다.", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자산배분 중요성 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (4, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "증권시장이 효율적이라고 전제하는 소극적 투자 관리 전술에 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 4, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "전략적 차원에서 결정된 자산 배분의 범위 내에서, 최적의 투자 대안을 선정하는 전술적 차원에서의 대응은 적극적 투자 관리와 소극적 투자 관리로 나눌 수 있다. 이중 소극적 투자 관리(Passive Investment Style)는 증권시장이 효율적이기기 때문에 과대평가 또는 과소평가 된 증권을 찾기 어렵다고 생각한다. 구체적인 종목을 분석하거나 독자적으로 판단하지 않고, 시장 전체의 일반적인 예측을 그대로 받아들여 정보 비용을 줄이고 매수와 매도를 최소화해 거래 비용을 줄인다. 단순 매수 후 보유하는 전략, 인덱스펀드와 ETF를 활용한 투자, 정액 분할 투자가 여기에 해당된다. 일부 종목에 집중투자하는 것은 적극적 투자관리 전략에 해당한다.", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '소극적 투자관리 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (5, 'MULTIPLE_CHOICE_W_PASSAGE', '[{"data": "주당장부가치비율에 대한 설명으로 올바른 것을 모두 고르시오.", "type": "QUESTION_TEXT"}, {"data": "[가. 주가를 주당 순자산으로 나눈 값이다, \n나. 이 지표가 1보다 낮다면 청산가치가 현재 주가보다 많다는 의미이다,\n다. 성장성이 높은 기업은 일반적으로 이 지표가 높게 산출된다,\n라. 보유하고 있는 공장이나 부동산 등 자산이 많아질수록 이 지표는 낮게 산출된다,\n마. 이 지표의 절대적인 적정 수준이 존재하지 않는다]", "type": "PREFACED_EXAMPLE_BOX_TEXT"}]', '[{"data": 4, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "PBR은 주가를 주당순자산으로 나눈 값으로, 기업이 청산된다고 가정할 때 주주가 건질 수 있는 청산가치의 규모를 주가와 비교하는 지표이다. PBR이 1보다 낮다는 것은 청산가치가 주가보다 크다는 것을 의미하며, 대부분 자산이 많은 기업들의 PBR이 상대적으로 낮은 편이다. 보유 자산은 적지만 성장성에 대한 기대가 높아 주가가 비싸게 형성되는 기업의 PBR은 상대적으로 높은 편이다. PER과 마찬가지로 PBR 역시 절대적인 적정 수준은 존재하지 않으면 상대적 비교에 유용하다.", "type": "SOLUTION_TEXT"}]', '상', '미니테스트', null, null, null, '이장영', TO_DATE('2021/06/15 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식가치지표의 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (6, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "재무제표에 포함되는 항목으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 4, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "재무제표는 기업의 모든 경영활동을 회계처리 방식으로 정리한 경영성적표이다. 재무제표는 크게 재무상태표와 손익계산서, 현금흐름표와 자본변동표, 그리고 주석으로 이루어진다.\n지문 1의 재무상태표는 자산/부채/자본으로 구성된 기업의 재무상태를 보여준다.\n지문 2의 현금흐름표는 일정 기간의 현금성 자산의 조달과 지출내역을 보여준다\n지문 3의 주석은 재무상태표 등의 보완설명 등 회계정책과 근거, 질적 정보 등 보충설명을 제공하는 참고사항이다.\n지문 4의 산업연관표는 한국은행이 작성하는 국민계정 분석도표로, 생산과정에 포함된 재화와 서비스의 생산과 처분을 경제활동별로 파악할 수 있는 표이다.", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '재무제표 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (7, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "\"삼성전자 우\"에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 2, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "주권 중 보통주와 우선주의 구분은 무엇보다 의결권의 유무이다. 주주총회에서 안건에 대해 투표를 통해 표결에 참여할 수 있는 주주는 보통주를 보유한 주주이며, 우선주는 의결권은 없는 대신 이익배당에서 우선 순위에 있다. 일반적으로 국내에서 우선주는 보통주보다 배당률이 높은 편이다.", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/06/21 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '우선주 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (8, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "캔들챠트에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 4, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "국내 주식시장에서 활용하는 챠트는 대부분 캔들챠트이다. 일중에 관찰되는 시가와 고가, 저가와 종가를 한 개의 몸통에서 모두 표시할 수 있다. 국내에서는 시가보다 종가가 높다면 몸통을 빨간색으로 칠하고, 시가보다 종가가 낮다면 몸통을 파란색으로 표시한다. 장중 고가와 저가는 몸통 위아래에 선으로 표시한다. \n따라서 캔들챠트의 위에서 아래까지의 길이가 장중 가격 등락의 폭을 보여준다. 위에서 아래까지의 길이기 길어질수록, 장중 가격등락이 큰 편임을 알 수 있다. 캔들챠트는 일중 주가의 흐름과 변동성을 동시에 보여준다.", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/06/22 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '캔들챠트 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (9, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "중앙은행이 금리인하를 통해 시중금리의 하락을 유도할 때 경제에 끼칠 것으로 예상되는 영향으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 1, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "중앙은행의 금리인하는 시중 유동성을 저축 등 이자상품에서 실물경제 분야로 이동시켜 경제를 활성화시키려는 노력의 일환이다.", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '기준금리 영향 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (10, 'MULTIPLE_CHOICE_W_PASSAGE', '[{"data": "<보기>의 (    ) 안에 들어갈 말로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"data": "[일반적으로 경기가 좋아지면 주가는 상승하나 채권 가격이 하락하고, 반대로 경기가 나빠지면 주가는 하락하나 채권 가격이 상승한다. 이는 경기라는 변수에 대해서 주식과 채권 가격이 (         )이기 때문이다]", "type": "EXAMPLE_BOX_TEXT"}]', '[{"data": 3, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "상관계수는 -1부터 +1까지 산출되며, +1은 완전히 같은 방향으로 움직이는 것을, -1은 완전히 반대 방향으로 움직이는 것을 의미한다. 상관계수가 마이너스 라는 것은 대체로 반대방향으로 움직인다는 것을 의미한다.", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '상관계수 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (11, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "증시주변 자금에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 1, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "일부 전문가는 고객예탁금에서 신용잔고를 차감한 금액을 종합 증시주변자금으로 계측하기도 한다. ", "type": "SOLUTION_TEXT"}]', '상', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '증시주변자금 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (12, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "환율과 경제의 상호관계에 관한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 3, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "수 많은 국가들이 자국의 통화가치를 약하게 하여 외국 통화의 유입을 유도하는 물밑 정책을 펼치고 있다. 이른바 전 세계적으로 확산되어 있는 소리없는 전쟁, 환율전쟁이 그것이다.", "type": "SOLUTION_TEXT"}]', '상', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '환율의 영향 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (13, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "행동편향을 통한 실수가 금융투자상품과 서비스를 선택할 때 빈번히 발생하는 이유에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 3, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "지문 1의 경우, 금융상품에 대한 학습경험의 기회가 적기 때문에 행동편향에 따라 행동한다.\n지문 2의 경우, 많은 금융투자 의사결정이 비용 대비 효용을 따지기보다는 스트레스, 걱정, 손실이나 후회에 대한 두려움에 떠밀려 의사결정을 한다.\n지문 3의 경우, 금융소비자가 행동편향에 따라 투자의사를 결정하면 혹독한 대가를 치룬 후 후회하는 모습을 반복할 수 있다. 이런 투자의사결정의 실수를 극복하기 위해서 내가 가진 행동편향을 이해할 필요가 있다.\n지문 4의 경우, 대부분의 금융투자자들은 금융투자상품을 복잡하게 여겨서 제대로 알고 의사결정을 하려는 시간과 노력을 기울이지 않고 행동편향에 따라 선택한다.\n", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 발생이유 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (14, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "군중심리는 주식시장이 급격히 오르거나 떨어질 때 자주 작동하여 시장을 과열시키거나 쉽게 얼어붙게 한다. 이러한 군중심리에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 2, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "군중심리의 기저에는 사회에 순응하고 군중에서 소외되고 싶지 않은 인간의 본능이 깔려 있다. 이에 따라 남들이 투자를 통해 돈을 벌었다거나 하면 거기서 소외되지 않게 투자에 뛰어든다. 경험이 적은 금융투자자이거나, 자신감이 없는 금융투자자일수록 군중심리에 더 취약하다.", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '군중심리편향 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (15, 'MULTIPLE_CHOICE_W_PASSAGE', '[{"data": "<보기>의 (    ) 안에 들어갈 말로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"data": "[2001년 바버와 오딘 교수는 주식투자에 있어서 남녀간 차이가 뚜렷이 존재한다는 연구 결과를 발표했다. 이 연구를 통해 남자가 여자보다 (     )편향이 높고, 주식투자에 있어서 남자는 (     )편향으로 인한 과다한 거래로 여자보다 수익률이 낮다고 밝혔다.]", "type": "EXAMPLE_BOX_TEXT"}]', '[{"data": 1, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "UC-Davis 바버(Brad Barber) 교수와 UC-Berkeley의 오딘(Terrance Odean) 교수는 1991년부터 1997년까지 6년간 무려 35,000 가구의 주식계좌를 살펴본 후 주식투자에 있어서 남녀간 성(性)적 차이가 뚜렷이 존재한다는 연구 결과를 발표했다.\n남자가 여자보다 자기과신 편향이 높아 주가의 상승과 하락을 예상할 수 있다는 확신으로 빈번한 매매를 했을 확률이 높다. 이로 인한 결과는 거래비용 증가와 수익률 악화로 이어질 수 있다.", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '매몰비용편향 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (16, 'MULTIPLE_CHOICE_W_PASSAGE', '[{"data": "투자자들의 대화를 듣고, 이를 토대로 C씨에게 예상되는 행동편향으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"data": "[A : 주식투자는 장기로 해야 한다는데 10년, 20년 후 장기적으로 괜찮을 분야가 뭐가 있을까?,\nB : 야 장기는 무슨~, 당장 돈 쓸 일이 태산인데 그래 가지고 어느 세월에 돈벌겠냐? 빨리 오를 종목을 찾아야지! 크게 오르는 종목들이 즐비하고 당장 돈 쓸 일이 태산인데 말이야.,\nA : 그런가? 장기투자를 해야 안정적이라고 해서 말이야.,\nB : 야 환매를 해서 이익을 얻지 못하려면 뭐 하러 투자를 하냐!]", "type": "EXAMPLE_BOX_TEXT"}]', '[{"data": 2, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "현재중시 편향에 빠진 투자자들은 미래에 비해 현재를 중시하여, 즉각적인 욕망을 충족하려는 충동에 따라 행동하려고 한다. 이에 따라 금융투자에서도 당장의 이익만을 추구하는 단기 투자에 그치고 노후 같은 장기 목표에 대한 투자를 등한시 한다.", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '현재중시편향 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (17, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "금융투자회사에 해당하는 것으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 4, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "우리나라 금융회사는 은행, 비은행 금융회사, 보험회사, 금융투자회사, 기타 금융회사로 분류된다. 이중에 금융투자회사는 증권회사, 선물회사, 자산운용회사, 투자자문회사, 부동산신탁회사, 종합금융회사가 있다. 상호저축은행은 비은행 금융회사다.\n지문1 증권회사는 대표적인 금융투자회사다.\n지문2 선물회사는 금융투자업의 투자중개업(주로 선물)을 주로 하는 금융투자회사다.\n지문3 자산운용회사는 금융투자업 중 집합투자업을 주로 하는 금융투자회사다.\n지문4 상호저축은행은 비은행 금융회사로 비은행 금융회사에는 상호저축은행, 여신금융전문회사, 대부업자, 상호금융이 있다.", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '금융투자회사 종류 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (18, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "공모주와 스팩(SPAC)투자에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 1, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "지문1 공모주 청약을 위해서는 금융투자회사를 통해서 청약을 하고, 배정받는 주식을 보유하거나 매매를 통해 이익을 얻을 수 있다. 따라서 증권회사의 계좌는 반드시 있어야 청약이 가능하다.\n지문2 주관사나 공동주관사를 통해서만 해당 공모주의 청약을 할 수 있기 때문에 해당 공모주를 주관(또는 공동주관)하는 증권사의 계좌가 필요하다.   \n* IPO(Initial Public Offering) 또는 상장(Listing) : 기업이 발행한 주권이 한국거래소 (KRX)가 정하는 요건에 따른 증권시장에서 거래가 될 수 있는 자격을 부여 받는 것\n지문3 스팩의 큰 특징중의 하나로 합병이 불발될 때는 원금과 이익금을 돌려받는 구조다.\n지문4 스팩은 합병을 목적으로 하는 명목상의 회사다.", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '공모주 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (19, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "투자자 A씨는 월요일에 OO전자 주식을 매수 하였다. 주식이 결제되어 투자자 A씨의 계좌로 입고되는 것은 언제일까?(월요일 ~ 금요일 사이에 공휴일은 없는 것으로 가정)", "type": "QUESTION_TEXT"}]', '[{"data": 3, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "지문1 매수당일 평가금액이 표시되지만, 결제되어 입고 되지는 않는다.\n지문3 매매가 체결된 주식의 결제시점은 체결일(월요일, D일)로부터 3영업일(D+2일)로 규정되어 있어 수요일이 정답이다.", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '실전주식거래 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (20, 'MULTIPLE_CHOICE_W_PASSAGE', '[{"data": "<보기>의 (    ) 안에 들어갈 말로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"data": "[투자실적을 평가하는 지표중에 수익률과 위험요소를 동시에 고려하여 성과를 측정하는 지표를 (         ) 라 한다]", "type": "EXAMPLE_BOX_TEXT"}]', '[{"data": 3, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "지문1 단순수익률은 위험요소를 고려하지 않는 수익률 기준이다.\n지문2 벤치마크는 수익률을 비교평가하는 기준으로 위험요소인 변동성을 별도록 고려하지는 않는다.\n지문3 수익률과 위험요소를 동시에 고려하여 성과를 측정하는 지표를 위험조정 성과지표(RAPM : Risk ?Adjuisted Performance)라 한다. 선택하는 수익률과 위험지표와 위험을 조정하는 방식에 따라 단위 위험당 초과수익률과 위험조정 수익률로 나눌 수 있다. \n지문4 절대수익 추구 성과지표라는 용어는 없으나, 투자자가 목표로 하는 절대수익률을 기준으로 할 수는 있다. ", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '벤치마크 이해');
