insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (92, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "주식에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 3, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "발행된 주식은 지정된 거래소에서 거래되기도 하지만, 거래소 밖에서 거래가 이뤄질 때도 있다. 이를 장외거래라고 한다", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식일반 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (93, 'MULTIPLE_CHOICE_W_PASSAGE', '[{"data": "다음 (  ) 안에 들어갈 말로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"data": "[한국거래소에서 주식을 거래할 때, 하루에 움직일 수 있는 주가의 변동폭은 상하 (    ) %이다]", "type": "EXAMPLE_BOX_TEXT"}]', '[{"data": 4, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "한국거래소의 주식거래 일별 가격 제한은 기준가 대비 상하 30%다", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '가격변동폭 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (94, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "주식가치의 적정성을 평가할때 활용하는 지표로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 4, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "지문 1은 주가이익비율(또는 주가수익비율)으로, 주가를 주당 순이익으로 나눈 값이다.\n지문 2는 주가장부가치비율(또는 주가순자산비율)으로, 주가를 주당 순자산으로 나눈 값이다.\n지문 3은 주가매출비율으로 주가를 주당 매출액으로 나눈 값이다.\n지문 4는 파생결합증권의 하나인 주가워런트증권(Equity Linked Warrant)으로, 주식가치 평가지표와는 무관한 금융투자상품의 하나이다.", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/06/15 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주가배율의 정확한 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (95, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "기술적 분석 중 이동평균선에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 3, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "이동평균선은 기술적 분석에서 중요한 보조지표이다. 일정 기간의 주가 평균치를 선으로 연결하여 현재의 주가와 비교하거나 이동평균선 자체의 흐름을 분석할 수 있다. \n이동평균선을 주가가 상향돌파하는 상황을 골드크로스로 부르며, 중요한 주식매수 신호로 인식된다. 반대로 이동평균선을 주가가 하향돌파하는 상황을 데드크로스(Dead Cross)라고 부르며, 중요한 주식매도 신호로 인식된다.", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/06/27 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '이동평균선 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (96, 'MULTIPLE_CHOICE_W_PASSAGE', '[{"data": "다음과 같은 주식에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"data": "[A주식 : 주가 10,000원 액면가 5,000원,\nB주식 : 주가 2,000원 액면가 1,000원 (6개월 전 액면분할 실시 5000원→1000원)]", "type": "PREFACED_EXAMPLE_BOX_TEXT"}]', '[{"data": 1, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "액면분할은 기존 액면가격을 나누어 비율만큼 발행주식수를 늘리는 것이다. 기존 액면가 5000원에서 1000원으로 액면분할을 한다면, 발행주식수는 기존 1주에서 5주로 늘어난다.\n지문 ①에서 만일 A주식이 5,000원에서 1,000원으로 액면분할한다면, 직관적으로 주가 역시 10,000원에서 2,000원으로 1/5 낮아질 수 있다. 주가를 비교 평가할 때, 액면가 대지 주가는 참고지표일 뿐, 주가의 가치적도로 활용하지 않는다. 지문 ②와 지문 ③은 액면분할로 인해 통상적으로 발생할 수 있는 현상이다.\n지문 ④에서 두 기업의 PER가 5.0배로 동일하다면, 내재가치 측면에서 비슷하다고 평가할 수 있다.", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/06/24 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '액면가와 시가 구별');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (97, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "경제지표에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 3, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "금리나 주가에 직접적인 영향을 미치는 것은 GDP 지표 발표치 자체가 아니라 한국은행과 각 금융 기관이 전망하는 향후 GDP 추정치와 이에 대한 시장의 컨센서스 변화인 경우가 많다.", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '경제지표 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (98, 'MULTIPLE_CHOICE_W_PASSAGE', '[{"data": "시중금리의 변화로 <보기>의 (    ) 안에 들어갈 말을 순서대로 올바르게 나열한 것은?", "type": "QUESTION_TEXT"}, {"data": "[경기하락예상 → (       ),\n통화긴축정책 → (       ),\n확장적 재정정책 → (       ),\n주식시장 상승 → (       )]", "type": "PREFACED_EXAMPLE_BOX_TEXT"}]', '[{"data": 1, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "기준금리 상향 등을 통한 통화 긴축정책은 시중 유동성을 흡수하며 금리상승을유발하며 확장적 재정정책 (경기부양 재정정책)은 국채발행과 경기부양 가능성 확대로 금리상승을 유발한다. 주식시장 상승은 경기지표의 호전과 채권시장 자금의 주식시장 이동을 통해 금리상승을 유발한다.", "type": "SOLUTION_TEXT"}]', '상', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '금리변동요인 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (99, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "자본시장과 거시경제 변수 등에 대한 설명으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 2, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "①시중 금리 수준이 높아지면 대출이 줄어들어 시중 유동성이 줄어들게 된다.\n③GDP는 분기별로 발표하기 때문에 금리나 주가에 직접적인 영향을 미치는 것은 지표 발표 자체가 아니라 한국은행과 각 금융 기관이 전망하는 향후 GDP 추정치와 이에 대한 시장의 컨센서스 변화인 경우가 많다.\n④수많은 국가들이 자국의 통화가치를 약하게 하여 외국 통화의 유입을 유도하는 물밑 정책을 펼치고 있다.", "type": "SOLUTION_TEXT"}]', '상', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '거시경제변수 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (100, 'MULTIPLE_CHOICE_W_PASSAGE', '[{"data": "다음 신문 기사의 내용을 통해 김씨의 행동편향으로 예상되는 것으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"data": "[<기사> 투자 경험이 없는 김씨는 어느 날 친구로부터 주식으로 돈을 벌었다는 얘기에 귀가 솔깃했다. 좋은 종목 있으면 추천해달라고 친구에게 요청했고, 친구는 증권 브로커 B씨를 소개해주었다. 이씨는 전문가처럼 주식에 관해 유창하게 설명해 주었고 바이오 종목 1개를 추천했다. 김씨는 1천만원을 투자했고, 이익이 났다. 김씨는 투자에 대해 자신감이 붙었고 금액도 1억원으로 늘리고 투자종목도 늘렸다. 그러나 몇 개 종목에서 손해가 나기 시작했고 원금까지 손실을 입기 시작했다. 덜컥 겁이나 이제라도 그만해야 싶었지만 손실이 커져 그만할 수 없었다. 급기야 원금을 반이나 날린 후에 진작 그만두었어야 한다고 후회했다]", "type": "EXAMPLE_BOX_TEXT"}]', '[{"data": 2, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "지문 1의 경우, 투자 경험이 없는 김씨가 친구가 수익이 났다는 얘기만을 듣고 귀가 솔깃해 추천을 권유한 것은 군중심리 편향에 가깝다.\n지문 2의 경우, 사후확증 편향은 이미 발생한 일의 결과를 알고 나면 그 일이 벌어질 수 밖에 없었고 이미 그렇게 될 줄 알고 있었던 것처럼 믿게 되는 오류로 상기 기사내용과는 거리가 멀다.\n지문 3의 경우, 원금까지 손실을 입기 시작한 이후 그만하지 못한 것은 손실을 회피하려는 편향 때문이다.\n지문 4의 경우, 투자에 대한 경험이 없었으나 초기 투자 성공으로 투자에 대한 자신감이 붙어 투자 금액을 쉽게 늘린 것은 자기과신 편향의 영향으로 볼 수 있다.", "type": "SOLUTION_TEXT"}]', '상', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '사후확증 편향 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (101, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "한 설문조사 결과 운전자의 88%가 자신이 평균보다 운전을 잘한다고 생각하며 그 중 80% 이상이 자신의 운전실력이 상위 30% 안에 든다고 생각한다고 했다. 이렇듯 사람들은 본인에 대한 과도한 믿음, 즉 자기과신을 하는 경향이 강한다. 이러한 자기과신 편향이 투자의사결정에 영향을 주었을 때의 행동으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 3, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "지문 1의 경우, 자기자신 뿐만 아니라 타인의 말을 그대로 믿고 행동하는 것도 그런 대상을 내가 잘 골랐다는 자기과신 편향의 영향이다. \n지문 2의 경우, 폰지 사기의 전형적인 수법으로 몇번의 수익금 수령이 초기 불안을 없애고 자기과신을 통해 더 많은 돈을 투자한다.\n지문 3의 경우, 현상유지 편향이 영향을 주었을 때의 행동으로 조정할 의사가 있거나 조정해야 할 상품을 그대로 유지하게 한다.\n지문 4의 경우, 자기과신 편향은 이렇다 할 대표성이 없는 몇차례의 경험을 통해 더욱 단단해져 이번에도 지난번처럼 될 거라 믿고 행동하게 만든다.", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '현상유지편향 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (102, 'MULTIPLE_CHOICE_W_PASSAGE', '[{"data": "다음 투자 격언은 어떤 행동편향에 대한 주의를 주고 있는가?", "type": "QUESTION_TEXT"}, {"data": "[우리가 위험에 빠지는 이유는 무언가를 몰라서가 아니다. 확실히 안다고 착각해서다. - 마크 트웨인]", "type": "EXAMPLE_BOX_TEXT"}]', '[{"data": 4, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "지문1의 경우, 사람들은 돈에 대한 기준이나 가치를 모두 같은 것으로 생각하지 않고 각각의 용도에 따라 다르게 생각하며 취급하는 것을 말한다.\n지문2의 경우, 사람들이 합리적이거나 객관적인 판단이 아닌 사실이나 숫자를 기준으로 삼고, 이를 바탕으로 결정을 내리는 편향이다.\n지문3의 경우, 사람들이 이득보다 손실이 더 크게 느껴, 이익 증가보다 손실 증가에 민감하게 느끼는 것을 말한다.\n지문4의 경우, 사람들은 자신의 능력 또는 판단이 스스로에게 유리한 결과를 가져다 줄 것이 믿고 행동한다. 이러한 자기과신 편향은 자신이 가지고 있는 능력이나 정보, 지식 수준을 실제보다 더 높거나 정확하다고 믿게 만들어 섣불리 위험한 투자를 할 수 있다.", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자기과신편향 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (103, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "심리학적 분석에 따르면 인간의 사고 체계는 두가지 시스템에 의해 작동한다. 빠르게 직관적으로 생각하는 시스템(휴리스틱)과 느리고 노력이 필요한 시스템(알고리즘)이 그것이다. 사람들은 금융투자 의사결정시에 후자을 사용한다고 착각하지만 전자를 사용해 결정하는 경우가 많다. 금융투자 시 전자의 시스템은 이익과 손실상황에서 어떤 성향을 보이는가?", "type": "QUESTION_TEXT"}]', '[{"data": 2, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "향후 주식시장에 대한 긍정적인 전망이 우세할 때 이미 수익이 난 계좌를 보유한 투자자는 긍정적인 전망에도 불구하고 이익을 실현하고 싶어한다. 미래의 불확실성이 싫기 때문에 당장의 이익을 챙겨야 할 것 같아서이다. 반대로 주식시장에 대한 부정적인 전망이 우세 할 때 이미 손실이 난 계좌를 보유한 투자자는 시장상황이 부정적이어서 지금의 손실규모가 더 커질 수 있음에도 사람들은 손실을 확정 짓지 않고 위험을 보유하려고 한다. 이익상황에서는 위험회피성향을, 손실상황에서는 위험보유성향을 보이고 있는 것이다.", "type": "SOLUTION_TEXT"}]', '상', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '손실회피성향 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (104, 'MULTIPLE_CHOICE_W_PASSAGE', '[{"data": "<보기>의 (    ) 안에 들어갈 내용으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"data": "[(          )은 특정 투자자로부터 금융투자상품에 대한 투자판단 전부 또는 일부를 일임받아 투자자별로 구분하여 금융투자상품을 운용하는 업(業)을 말한다]", "type": "EXAMPLE_BOX_TEXT"}]', '[{"data": 1, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "투자일임업은 특정 투자자로부터 금융투자상품에 대한 투자판단 전부 또는 일부를 일임 받아 투자자별로 구분하여 금융투자상품을 운용하는 업(業)이다.\n지문2 투자자문업은 특정인을 위하여 금융투자상품의 가치 또는 투자판단에 대한 자문을 제공하는 업(業)을 말한다.\n지문3 투자매매업은 금융투자회사가‘회사의 고유재산으로 투자’하는 것을 말한다. \n지문4 집합투자업은 우리가 일반적으로 알고 있는 펀드운용을 하는 것이다. 정확하게는 ‘2인 이상의 투자자로부터 모은 금전 등을 투자자로부터 일상적인 운용지시를 받지 아니하면서 재산적 가치가 있는 투자대상자산을 취득, 처분 그 밖의 방법으로 운용하고 그 결과를 투자자에게 배분하여 귀속하는 것’을 말한다. ", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자일임업 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (105, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "투자자 A씨의 거래형태에 대한 설명으로 사실과 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 3, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "지문1 증권회사 위탁계좌와 MTS를 활용한 직접매매형태다.\n지문2 ETF에 투자하는 특정금전신탁에 가입하는 형태로 은행의 신탁을 통해서 ETF를 거래할 수 있는 방법이다.\n지문3 특정금전신탁을 통해서 매매하던지 직접 매매하던지 매매하는 ETF는 동일하다. 그러나 투자비용에서는 위탁계좌를 통한 매매와 특정금전신탁을 통한 매매는 큰 차이가 있다. 특정금전신탁이 수취하는 신탁보수가 위탁계좌에는 없고, 매매수수료도 차이가 난다.\n지문4 증권회사를 통한 ETF매매는 한국거래소에 직접 주문을 내지만, 특정금전신탁을 통한 매매는 고객의 운용지시와 특정금전신탁의 주문이 증권사를 통해서 이루어 지기 때문에 아무리 빨라도 실시간 매매라고 할 수는 없다.", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '실전주식거래 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (106, 'MULTIPLE_CHOICE_W_PASSAGE', '[{"data": "<보기>의 (    ) 안에 들어갈 내용으로 적합한 것을 모두 묶은 것은?", "type": "QUESTION_TEXT"}, {"data": "[''금융상품 한눈에''(http://finlife.fss.or.kr/)에서는 (       ) 등의 금융상품 금리와 수익률, 보험료 등을 한번에 비교 조회할 수 있다.]", "type": "EXAMPLE_BOX_TEXT"}, {"data": "[㉠예금  ㉡대출  ㉢연금저축  ㉣보험  ㉤펀드]", "type": "PREFACED_EXAMPLE_BOX_TEXT"}]', '[{"data": 4, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "지문1, 지문2, 지문3, 지문4 ‘금융상품 한눈에’ 에서는 예금과 대출, 연금저축, 보험, 펀드 모두를 한 번에 비교할 수 있다.", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '비교공시 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (107, 'MULTIPLE_CHOICE_W_PASSAGE', '[{"data": "<보기>의 (    ) 안에 들어갈 말로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"data": "[주식 매매 시 가격을 지정해서 주문하는 것을 ( ㉠ )주문이라고 하고 가격은 정하지 않고 주문을 내면 접수된 시점에 가장 유리한 가격으로 매매가 체결되는 주문을 ( ㉡ )주문이라 한다]", "type": "EXAMPLE_BOX_TEXT"}]', '[{"data": 1, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "지문1 ‘ 지정가주문’과 ‘시장가주문’에 대한 설명\n지문2 증권을 매매할 때 위탁증거금으로 현금 대신 유가증권을 사용할 수 있다. 이때 대용 유가증권의 가격을 ‘대용가’라고 한다.\n지문3 & 지문4 ‘조건부 지정가’는 지정가로 거래를 시도하다가 거래가 체결되지 않으면 장 마감 시점에 시장가도 전환하며 매매 체결을 하는 주문거래 방식이다.", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주문종류 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (108, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "투자 수익률을 계산할 때 고려해야 할 사항으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 3, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "수수료와 세금이 눈에 드러나는 비용이라면 그렇지 않은 것도 있다. 대표적인 예로 기회비용이 있다. 기회비용이란 어떤 행위를 하기 위해 포기해야 하는 다른 기회의 가치를 말하는데, 투자에도 그대로 적용된다. 예를 들어, 주식 투자를 하면서 많은 시간과 노력을 기울여 종목을 발굴할 것이다. 이런 시간과 노력이 얼마만큼 가치를 가질 것인지 명시적으로 계산하기는 어렵지만, 기회비용이 발생한 것만큼은 분명하다. 시간과 노력을 다른 곳에 기울일 수도 있기 때문이다. 투자 활동에 수반되는 기회비용을 줄이기 위한 방편으로 직접투자 대신 펀드와 위탁 판매와 같은 간접투자를 이용하기도 하는데, 이때는 투자 결정을 대신해 주는 대가로 운용 보수를 지불한다.", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '수익률 결정요소로서 비용');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (109, 'MULTIPLE_CHOICE_W_PASSAGE', '[{"data": "다음과 같은 투자대안이 있을 때, 합리적 선택으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"data": "[투자대안 A: 기대수익률 5%, 투자위험 10%,\n투자대안 B: 기대수익률 10%, 투자위험 10%,\n투자대안 C: 기대수익률 10%, 투자위험 7%,\n투자대안 D: 기대수익률 5%, 투자위험 7%]", "type": "PREFACED_EXAMPLE_BOX_TEXT"}]', '[{"data": 1, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "기대수익이 동일하다면 투자위험이 적은 투자대안을 선택하고, 투자위험이 동일하다면 수익률이 큰 대안을 선택하는 것이 유리하다. A와 B는 투자위험이 동일하기 때문에 기대수익률이 큰 B를 매수하고 A를 매도하는 것이 유리하다.   ", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '합리적 투자결정');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (110, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "홍길동씨는 주식시장에 이용할 수 있는 모든 정보가 반영되어 있다고 생각하지 않는 투자자이다. 홍길동씨의 생각에 부합하는 투자 전술로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 4, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "적극적 투자 관리(Active Investment Style)는 다소 위험을 감수하더라도 시장의 평균을 넘어서는 초과 수익을 추구한다. 증권시장이 비효율적이라고 전제하고, 위험이 동일한 증권들 중에서 수익이 높은 증권을 선택하거나, 기대 수익이 동일한 증권 중에서 위험이 낮은 증권을 선택하는 방법이다. 적극적 투자 관리 방법으로는 소주종목이나 특정 산업에 집중적으로 투자하거나, 투자 타이밍을 포착하거나, 시장의 이례적인 현상을 이용해 종목을 선정하는 것이 대표적이다", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '적극적 투자관리 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (111, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "직접 투자의 장단점에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 4, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "직접 투자의 가장 큰 장점은 자신에게 적합한 포트폴리오를 스스로 꾸릴 수 있다는 점이다. 자신의 취향에 맞는 종목을 골라, 사고 싶을 때 사고 팔고 싶을 때 판다. 내 돈을 스스로 관리한다는 점에서 자부심도 느낄 수 있다. \n하지만 일반인들 중에 충분한 투자 경험과 역량을 갖춘 이는 많지 않은 데다, 단기간에 전문성을 갖추기도 어렵다. 투자 역량과 경험을 충분히 갖춘 투자자라고 해도 의사결정을 내리려면 필요한 정보를 취득하고 분석하는 데 많은 시간이 든다. 전문성을 갖추지 않은 채 투자 대안을 충분히 검토하지 않고 감정에 치우쳐 매매하면 수익은 내지 못하고 수수료만 지불할 가능성이 높다", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '직접투자 장단점 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (112, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "투자기간 동안 주가가 지속적으로 상승하거나 하락할 때 적립식과 거치식 투자 성과에 대한 설명으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 1, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "투자기간 동안 주가가 지속적으로 하락하는 상황에서는 적립식 투자를 해도 손실을 피할 수 없다. 다만 목돈을 한꺼번에 투자는 것보다 손실 규모를 줄일 수 있다. 반대로 주가가 지속적으로 상승하는 경우에는 초기에 목돈을 투자는 거치식이 적립식보다 나은 성과를 보인다. ", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '적립식투자 장점 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (113, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "투자자금 중 일부를 부채로 조달하는 레버리지투자의 효과에 대한 설명으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 2, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "금융에서는 실제 가격 변동보다 몇 배나 많은 투자 수익이 발생하는 현상을 레버리지 효과라고 한다. 레버리지 효과가 발생하려면 투자 자금 중에 일부를 자기 자본이 아닌 부채로 조달해야 한다. 분산투자를 하면 위험이 줄지만 기대 수익도 줄어든다. 이와 반대로 레버리지를 이용해 기대수익과 함께 위험을 높이는 투자 전략도 있다", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '레버리지투자 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (114, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "□□기업의 부채비율이 500%라고 할 때, 부채비율을 줄일 수 있는 방법으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 2, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "부채비율을 줄이기 위해서는 부채를 낮추거나 자본을 늘려야 한다. \n지문 1에서 부채 항목인 차입금을 갚으면 부채가 줄어들어 부채비율을 줄인다.\n지문 2에서 타인자본인 사채를 발행하면, 부채항목이 늘어나기 때문에 부채비율을 늘리는 조치이다.\n지문 3의 유상증자를 실시하는 것은 자기자본인 자본을 증액하는 것이므로, 부채비율을 줄인다.\n지문 4에서 기존 발행된 회사채를 상환하여 부채항목을 줄일 수 있다.", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/06/15 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '부채와 자본 구별');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (115, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "주식회사가 신규 투자를 받기 위해 자금을 조달하는 방식으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 3, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "신규 투자를 위한 자금조달은 자기자본을 늘리거나 타인자본을 차입하는 방식으로 진행할 수 있다. 금융기관에서 대출을 받는 방식도 가능하다\n지문 1에서 회사채 발행은 타인자본을 늘려 운영자금을 마련할 수 있다.\n지문 2에서 유휴부지를 매각하여 현금을 확보하면 설비투자를 진행할 수 있다.\n지문 3에서 액면분할은 자기자본의 지분권을 쪼개는 것일뿐 자본의 증가를 유발하지 않는다\n지문 4에서 제3자배정을 통한 유상증자 역시 설비투자를 마련할 수 있는 방안이다.", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/06/15 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자금조달 수단');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (116, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "배당금에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 4, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "배당은 기업의 잉여자본을 주주에게 환원하는 경제행위이며, 주주는 기업에게 이익배당을 청구할 수 있는 권리가 있다. 성장단계의 기업은 배당보다 투자에 집중하는 경향이 높다. \n지문 2와 4의 배당수익률 산식은 (주당배당금/주가)*100이므로, 배당수익률이 높다면 배당금이 많거나 주가가 낮은 경우이다. 배당수익률은 배당 규모를 판단하는 지표이며, 주가의 예상 등락을 평가할 수 있는 가격지표로 활용할 수 없다", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/06/18 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '배당 관련용어 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (117, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "상장기업이 주주와 이해관계자, 그리고 일반인을 대상으로 주가에 영향을 미칠만한 사실을 공개적으로 밝히는 행위는?", "type": "QUESTION_TEXT"}]', '[{"data": 1, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "기업이 이해관계자와 일반인에게 주가에 영향을 미칠 만한 각종 사실을 공개적으로 밝히는 행위다. 공시는 사실성과 즉시성을 갖춰야만 투자자에게 올바른 정보를 신속하게 전달할 수 있다", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '공시 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (118, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "경제지표에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 3, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "금리나 주가에 직접적인 영향을 미치는 것은 GDP 지표 발표치 자체가 아니라 한국은행과 각 금융 기관이 전망하는 향후 GDP 추정치와 이에 대한 시장의 컨센서스 변화인 경우가 많다.", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '경제지표 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (119, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "물가와 채권 금리의 관계에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 3, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "물가 지수의 변화가 채권시장에 끼치는 영향은 직접적인 데 반해 주식시장에 끼치는 영향은 다소 간접적이다. 물가 상승으로 한국은행의 기준금리 인상 등 긴축 통화가 예상될 경우 단기적으로 주가에는 좋지 않은 영향을 끼칠 수 있다. 하지만 장기적으로 물가 수준의 상승은 곧 기업이 보유하고 있는 자산 가격의 상승, 기업이 창출하는 명목 이익의 상승으로 연결되므로 주가에 긍정적으로 작용한다는 분석이 많다. ", "type": "SOLUTION_TEXT"}]', '상', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '물가의 영향 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (120, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "자본시장과 거시경제 변수 등에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 3, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "③중장기 채권의 금리들이 현저히 떨어져 있을 경우 채권의 금리가 추가하락할 수 있는 여지가 적어져 경기가 좋지 않을 때 채권 가격이 상승하며 주식의 가격하락 위험을 상쇄시켜주는 주식+채권 포트폴리오 효과를 크게 기대하기 어렵다. ", "type": "SOLUTION_TEXT"}]', '상', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '거시경제변수 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (121, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "합리적인 투자자라면 시장 상황이나 미래 가치 등을 판단하여 보유하고 있는 주식의 매도 여부를 결정한다. 그러나 일반적으로 그 종목의 적정가치를 따져보려 하지않고 자신이 구입한 가격만을 최우선 결정요인으로 판단하는 경우가 많다. 이러한 편향을 일컫는 말은 무엇인가?", "type": "QUESTION_TEXT"}]', '[{"data": 2, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "사람들은 객관적인 판단이 아닌 사실이나 숫자를 기준으로 삼고, 이를 바탕으로 결정을 내리는데 이를 기준점 편향이라한다. 기준점 편향을 앵커링효과라고 도 한다.  앵커링효과에서 앵커(Anchor)는 배를 정박시킬 때 배가 떠내려가지 않도록 하는 닻을 의미하며 앵커링효과는 배를 정박시키는 닻을 내리듯 사람의 생각이나 판단이 그 기준점을 벗어나지 않는 것을 말한다.", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '기준점편향 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (122, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "금융상품 투자시 ‘과거의 운용실적이 미래의 수익을 보장하지 않습니다.’라는 문구는 다음 중 어떤 행동편향을 방지하기 위한 것인가?", "type": "QUESTION_TEXT"}]', '[{"data": 4, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "과거의 운용실적이 미래의 수익을 보장하지 않는다는 것은 투자의사 결정을 할 때, 과거의 수익만을 보고 앞으로도 그런 수익이 지속될 것이라고 판단하는 것은 위험하다는 것을 알리는 것이다. 과거의 수익은 미래의 수익을 담보하는 대표성이 있는 것이 아닌데 앞으로도 과거처럼 수익이 날거라고 믿고 행동하는 과거추세지속가정 편향을 막기위한 것이다.", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '과거추세지속가정편향 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (123, 'MULTIPLE_CHOICE_W_PASSAGE', '[{"data": "다음은 행동편향에 빠지지 않기 위한 방법 중 하나로, 오버나이트 테스트에 대한 설명이다. 오버나이트 테스트를 통해 극복하고자 하는 행동편향으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"data": "[당신이 보유한 종목들이 투자 손해를 보고 있다. 만약 누군가가 오늘 당신이 보유한 모든 종목을 팔아 현금화해준다면 다음날 그 현금으로 당신이 가지고 있던 그 종목을 다시 매수할 것인가? 만약 다시 매수하지 않을 거라면 그런 종목을 지금은 왜 그대로 가지고 있는가? 오버나이트 테스트는 내가 보유하고 있는 종목들의 가치가 오르거나 내린 주식이 아닌 현금일때도 그 주식을 매수할 것인가를 생각해보고 계속 보유여부를 합리적으로 따져 보는 것이다.]", "type": "EXAMPLE_BOX_TEXT"}]', '[{"data": 4, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "매몰비용의 오류에서 벗어나는 방법은 투자가 잘못되어 가고 있다는 것을 발견했다면 재빠르게 정신을 차리고 되돌아서는 것뿐이다. 매몰비용을 포기하고 합리적인 결정을 내려야 하는데 투자에서는 오버나이트 테스트를 해보는 것도 좋은 방법 중 하나이다. ", "type": "SOLUTION_TEXT"}]', '중', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '매몰비용편향 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (124, 'MULTIPLE_CHOICE_W_PASSAGE', '[{"data": "투자 초보자들이 빠지기 쉬운 오류 중 하나는 투자의 패턴을 발견했다고 착각하는 것이다. 다음과 같은 생각을 갖는 A씨가 가진 행동편향으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"data": "[내가 몇 달간 쭉 지켜 지켜봤는데 이 주식은 3일 연속 하락하면 그 다음에는 꼭 오르더라고 그래서 지금 사야해]", "type": "EXAMPLE_BOX_TEXT"}]', '[{"data": 3, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "도박사의 오류란 서로 독립적인 확률적 사건이 서로 확률에 영향을 미친다는 착각에 기인한 오류이다. 주가가 오르거나 내릴 확률은 반반일 뿐인데 며칠 연속 떨어졌으니 오를 때가 되었다거나 반대로 며칠 연속 올랐으니 오늘은 떨어질거라 판단하는 것이다.", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '도박사의오류 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (125, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "증권회사의 비대면 계좌를 개설하기 위해 필요한 것과 가장 거리가 먼것은?", "type": "QUESTION_TEXT"}]', '[{"data": 4, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "지문1 본인명의의 스마트 폰이 필요하다.\n지문2 신분증은 실명확인을 위한 신분증 촬영을 위해 필요하다.\n지문3 실명확인을 위해 필요하다.\n지문4 HTS는 개설된 계좌를 사용하여 거래에 필요한 프로그램이다. 비대면계좌를 개설하기 위해 반드시 필요한 것은 아니다.", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '계좌개설절차 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (126, 'MULTIPLE_CHOICE_WO_PASSAGE', '[{"data": "주식매매를 위한 대기자금도 단기 실세금리 수준의 이자율을 받을 수 있도록 비대면 주식계좌 개설시 함께 계좌를 개설하는 단기 금융투자상품으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}]', '[{"data": 2, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "지문1 ELS(Equity Linked Securities)는 주가지수 또는 특정주식가격의 변동과 연계되어 수익률이 결정되는 증권\n지문2 CMA(cash management account)어음관리계좌 또는 종합자산관리계좌라 불린다. 고객이 예치한 자금을 CP나 양도성예금증서(CD)·국공채 등의 채권에 투자하여 그 수익을 고객에게 돌려주는 금융상품\n지문3 DLS(Derivative Linked Securities) 주가 외 기초자산(금리, 통화, 상품, 신용위험 등) 가격의 변동과 연계되어 수익률이 결정되는 증권\n지문4 개인형 퇴직연금 IRP (Individual Retirement Pension)개인별로 퇴직금 및 여유자금을 운용할 수 있는 퇴직금 전용계좌", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', 'CMA 이해');
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "PART", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION" ) values (127, 'MULTIPLE_CHOICE_W_PASSAGE', '[{"data": "시장가 주문에 대한 설명으로 <보기>의 (    ) 안에 들어갈 말로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"data": "[시장가 주문이란 투자자가 종목과 ( ㉠ )은 지정하되 (  ㉡ )은 지정하지 않고 주문을 내면 주문이 접수된 시점에서 가장 유리한 가격으로 매매가 성립되는 주문을 말한다]", "type": "EXAMPLE_BOX_TEXT"}]', '[{"data": 2, "type": "MULTIPLE_CHOICE_CORRECT_ANSWER"}, {"data": "지문1~4 시장가 주문이란 투자자가 (종목), (수량)은 지정하되 (가격)은 지정하지 않고 주문을 내면 주문이 접수된 시점에서 가장 유리한 가격으로 매매가 성립되는 주문을 말한다. ", "type": "SOLUTION_TEXT"}]', '하', '미니테스트', null, null, null, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주문종류 이해');
